};

const ChatInput = ({ onSendMessage, loading }) => {
    const [message, setMessage] = useState('');
    const [isRecording, setIsRecording] = useState(false);
    const textareaRef = useRef(null);
    
    const handleSubmit = (e) => {
        e.preventDefault();
        if (message.trim() && !loading) {
            onSendMessage(message.trim());
            setMessage('');
            if (textareaRef.current) {
                textareaRef.current.style.height = 'auto';
            }
        }
    };
    
    const handleKeyPress = (e) => {
        if (e.key === 'Enter' && !e.shiftKey) {
            e.preventDefault();
            handleSubmit(e);
        }
    };
    
    const adjustHeight = (e) => {
        const textarea = e.target;
        textarea.style.height = 'auto';
        textarea.style.height = Math.min(textarea.scrollHeight, 120) + 'px';
    };
    
    return (
        <div className="chat-input-container">
            <form onSubmit={handleSubmit} className="chat-input-form">
                <div className="input-wrapper">
                    <textarea
                        ref={textareaRef}
                        value={message}
                        onChange={(e) => {
                            setMessage(e.target.value);
                            adjustHeight(e);
                        }}
                        onKeyPress={handleKeyPress}
                        placeholder="Share what's on your mind..."
                        className="chat-textarea"
                        disabled={loading}
                        rows={1}
                        maxLength={2000}
                    />
                    
                    <div className="input-actions">
                        <button
                            type="button"
                            className={`voice-button ${isRecording ? 'recording' : ''}`}
                            onClick={() => setIsRecording(!isRecording)}
                            title="Voice input (coming soon)"
                            disabled
                        >
                            ðŸŽ¤
                        </button>
                        
                        <button
                            type="submit"
                            className="send-button"
                            disabled={!message.trim() || loading}
                        >
                            {loading ? (
                                <span className="spinner small"></span>
                            ) : (
                                <span className="send-icon">âž¤</span>
                            )}
                        </button>
                    </div>
                </div>
                
                <div className="input-hints">
                    <span>Press Enter to send, Shift+Enter for new line</span>
                    <span className="char-counter">{message.length}/2000</span>
                </div>
            </form>
        </div>
    );
};

const ChatInterface = ({ user, onRewardEarned }) => {
    const [messages, setMessages] = useState([]);
    const [loading, setLoading] = useState(false);
    const [initialLoading, setInitialLoading] = useState(true);
    const { makeRequest } = useAPI();
    const messagesEndRef = useAutoScroll(messages);
    
    // Load chat history on component mount
    useEffect(() => {
        loadChatHistory();
    }, []);
    
    const loadChatHistory = async () => {
        try {
            const response = await makeRequest('/chat/history?limit=50');
            setMessages(response.messages || []);
        } catch (error) {
            console.error('Failed to load chat history:', error);
        } finally {
            setInitialLoading(false);
        }
    };
    
    const handleSendMessage = async (messageText) => {
        if (!messageText.trim()) return;
        
        // Add user message immediately
        const userMessage = {
            id: Date.now(),
            message_type: 'user',
            content: messageText,
            timestamp: new Date().toISOString()
        };
        
        setMessages(prev => [...prev, userMessage]);
        setLoading(true);
        
        try {
            const response = await makeRequest('/chat/send', {
                method: 'POST',
                body: JSON.stringify({ message: messageText })
            });
            
            // Add AI response
            setMessages(prev => [...prev, response.ai_response]);
            
            // Handle rewards
            if (response.reward && response.reward.coins_awarded > 0) {
                onRewardEarned({
                    type: 'chat',
                    coins: response.reward.coins_awarded,
                    xp: response.reward.xp_awarded,
                    level_up: response.reward.level_up
                });
            }
            
            // Handle crisis detection
            if (response.is_crisis) {
                // Show crisis support modal or notification
                console.log('Crisis detected - showing support resources');
            }
            
        } catch (error) {
            console.error('Chat error:', error);
            // Add error message
            const errorMessage = {
                id: Date.now() + 1,
                message_type: 'assistant',
                content: "I'm having trouble responding right now. Please try again in a moment.",
                timestamp: new Date().toISOString(),
                is_error: true
            };
            setMessages(prev => [...prev, errorMessage]);
        } finally {
            setLoading(false);
        }
    };
    
    const handleSuggestionClick = async (suggestion) => {
        try {
            await makeRequest('/rewards/claim', {
                method: 'POST',
                body: JSON.stringify({ activity_type: suggestion.type })
            });
            
            onRewardEarned({
                type: suggestion.type,
                coins: suggestion.coins,
                xp: 20,
                message: `Great job completing: ${suggestion.text}`
            });
            
        } catch (error) {
            console.error('Failed to claim reward:', error);
        }
    };
    
    if (initialLoading) {
        return (
            <div className="chat-loading">
                <div className="loading-spinner">
                    <span className="spinner large"></span>
                    <p>Loading your conversation...</p>
                </div>
            </div>
        );
    }
    
    return (
        <div className="chat-interface">
            <div className="chat-header">
                <div className="chat-title">
                    <h2>ðŸ’¬ Chat with MoodMate</h2>
                    <p>Your AI mental health companion</p>
                </div>
                
                <div className="user-stats">
                    <div className="stat-item">
                        <span className="stat-icon">ðŸª™</span>
                        <span className="stat-value">{user.coins}</span>
                    </div>
                    <div className="stat-item">
                        <span className="stat-icon">ðŸ”¥</span>
                        <span className="stat-value">{user.streak}</span>
                    </div>
                    <div className="stat-item">
                        <span className="stat-icon">â­</span>
                        <span className="stat-value">Lv.{user.level}</span>
                    </div>
                </div>
            </div>
            
            <div className="chat-messages" ref={messagesEndRef}>
                {messages.length === 0 ? (
                    <div className="welcome-message">
                        <div className="welcome-avatar">ðŸ¤–</div>
                        <div className="welcome-content">
                            <h3>Hello {user.display_name}! ðŸ‘‹</h3>
                            <p>I'm MoodMate, your AI mental health companion. I'm here to listen, support, and help you on your wellness journey.</p>
                            <p>How are you feeling today? Feel free to share what's on your mind!</p>
                        </div>
                    </div>
                ) : (
                    messages.map((message, index) => (
                        <ChatMessage
                            key={message.id || index}
                            message={message}
                            isUser={message.message_type === 'user'}
                            suggestions={message.suggestions}
                            onSuggestionClick={handleSuggestionClick}
                        />
                    ))
                )}
                
                {loading && (
                    <div className="ai-typing">
                        <div className="typing-indicator">
                            <div className="typing-dots">
                                <span></span>
                                <span></span>
                                <span></span>
                            </div>
                            <span>MoodMate is typing...</span>
                        </div>
                    </div>
                )}
            </div>
            
            <ChatInput onSendMessage={handleSendMessage} loading={loading} />
        </div>
    );
};

