const TherapistBooking = ({ therapist, user, onClose, onBooked }) => {
    const [selectedDate, setSelectedDate] = useState('');
    const [selectedTime, setSelectedTime] = useState('');
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const { makeRequest } = useAPI();
    
    const handleBooking = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError('');
        
        if (user.coins < therapist.price_per_session) {
            setError('Insufficient coins for this session');
            setLoading(false);
            return;
        }
        
        try {
            const scheduledTime = `${selectedDate}T${selectedTime}:00`;
            
            const response = await makeRequest(`/therapists/${therapist.id}/book`, {
                method: 'POST',
                body: JSON.stringify({ scheduled_time: scheduledTime })
            });
            
            onBooked(response.session);
            onClose();
            
        } catch (error) {
            setError(error.message);
        } finally {
            setLoading(false);
        }
    };
    
    // Generate available dates (next 14 days)
    const availableDates = useMemo(() => {
        const dates = [];
        const today = new Date();
        for (let i = 1; i <= 14; i++) {
            const date = new Date(today);
            date.setDate(today.getDate() + i);
            dates.push(date.toISOString().split('T')[0]);
        }
        return dates;
    }, []);
    
    // Generate time slots
    const timeSlots = [
        '09:00', '10:00', '11:00', '12:00', '13:00', '14:00', 
        '15:00', '16:00', '17:00', '18:00', '19:00', '20:00'
    ];
    
    return (
        <div className="modal-overlay" onClick={onClose}>
            <div className="modal-content booking-modal" onClick={e => e.stopPropagation()}>
                <div className="modal-header">
                    <h2>Book Session with {therapist.name}</h2>
                    <button className="modal-close" onClick={onClose}>Ã—</button>
                </div>
                
                <div className="booking-content">
                    <div className="therapist-summary">
                        <img src={therapist.profile_image} alt={therapist.name} />
                        <div>
                            <h3>{therapist.name}</h3>
                            <p>{therapist.specialization}</p>
                            <div className="cost">
                                <span className="coin-icon">ðŸª™</span>
                                <span>{therapist.price_per_session} coins</span>
                            </div>
                        </div>
                    </div>
                    
                    <form onSubmit={handleBooking}>
                        <div className="form-group">
                            <label>Select Date</label>
                            <select 
                                value={selectedDate} 
                                onChange={(e) => setSelectedDate(e.target.value)}
                                required
                            >
                                <option value="">Choose a date</option>
                                {availableDates.map(date => (
                                    <option key={date} value={date}>
                                        {new Date(date).toLocaleDateString()}
                                    </option>
                                ))}
                            </select>
                        </div>
                        
                        <div className="form-group">
                            <label>Select Time</label>
                            <select 
                                value={selectedTime} 
                                onChange={(e) => setSelectedTime(e.target.value)}
                                required
                            >
                                <option value="">Choose a time</option>
                                {timeSlots.map(time => (
                                    <option key={time} value={time}>{time}</option>
                                ))}
                            </select>
                        </div>
                        
                        {error && <div className="error-message">{error}</div>}
                        
                        <div className="booking-footer">
                            <div className="balance-info">
                                Your balance: <span className="coin-icon">ðŸª™</span> {user.coins} coins
                            </div>
                            
                            <div className="modal-actions">
                                <button type="button" className="btn btn-secondary" onClick={onClose}>
                                    Cancel
                                </button>
                                <button 
                                    type="submit" 
                                    className="btn btn-primary" 
                                    disabled={loading || !selectedDate || !selectedTime}
                                >
                                    {loading ? 'Booking...' : 'Book Session'}
                                </button>
                            </div>
                        </div>
                    </form>
                </div>
            </div>
        </div>
    );
};

