const StatsDisplay = ({ user }) => {
    const getXPForNextLevel = (level) => {
        return (level ** 2) * 100;
    };
    
    const currentLevelXP = getXPForNextLevel(user.level - 1);
    const nextLevelXP = getXPForNextLevel(user.level);
    const progressXP = user.experience - currentLevelXP;
    const neededXP = nextLevelXP - currentLevelXP;
    const progressPercent = (progressXP / neededXP) * 100;
    
    return (
        <div className="stats-display">
            <div className="stat-card level-card">
                <div className="stat-icon">â­</div>
                <div className="stat-content">
                    <div className="stat-label">Level</div>
                    <div className="stat-value">{user.level}</div>
                    <div className="progress-bar">
                        <div 
                            className="progress-fill" 
                            style={{ width: `${progressPercent}%` }}
                        ></div>
                    </div>
                    <div className="progress-text">
                        {progressXP} / {neededXP} XP
                    </div>
                </div>
            </div>
            
            <div className="stat-card coins-card">
                <div className="stat-icon">ðŸª™</div>
                <div className="stat-content">
                    <div className="stat-label">Coins</div>
                    <div className="stat-value">{user.coins}</div>
