    const handleRegister = (userData) => {
        setUser(userData);
        addNotification({
            type: 'success',
            title: 'Welcome to MoodMate!',
            message: 'Your mental wellness journey starts now.',
            icon: 'ðŸŽ‰'
        });
    };
    
    const handleLogout = async () => {
        try {
            await makeRequest('/auth/logout', { method: 'POST' });
        } finally {
            setUser(null);
            setCurrentTab('chat');
            addNotification({
                type: 'info',
                title: 'Logged out',
                message: 'See you soon!',
                icon: 'ðŸ‘‹'
            });
        }
    };
    
    const addNotification = (notification) => {
        const id = Date.now();
        setNotifications(prev => [...prev, { ...notification, id }]);
        
        // Auto-remove after 5 seconds
        setTimeout(() => {
            removeNotification(id);
        }, 5000);
    };
    
    const removeNotification = (id) => {
        setNotifications(prev => prev.filter(n => n.id !== id));
    };
    
    const handleRewardEarned = (reward) => {
        setCurrentReward(reward);
        
        // Update user stats if provided
        if (reward.coins || reward.xp) {
            setUser(prev => ({
                ...prev,
                coins: prev.coins + (reward.coins || 0),
                experience: prev.experience + (reward.xp || 0),
                level: reward.level_up ? reward.level_up.new_level : prev.level
            }));
        }
    };
    
    const contextValue = {
        user,
        setUser,
        currentTab,
        setCurrentTab,
        theme,
        setTheme,
        addNotification
    };
    
    if (loading) {
        return (
            <div className="app-loading">
                <div className="loading-content">
                    <div className="app-logo">ðŸ§ </div>
                    <h1>MoodMate</h1>
                    <div className="spinner large"></div>
                    <p>Loading your mental wellness companion...</p>
                </div>
            </div>
        );
    }
    
    return (
        <AppContext.Provider value={contextValue}>
            <div className={`app theme-${theme}`}>
                {!user ? (
                    <div className="auth-container">
                        <div className="auth-background"></div>
                        <div className="auth-content">
                            <div className="auth-logo">
                                <div className="logo-icon">ðŸ§ </div>
                                <h1>MoodMate</h1>
                                <p>Your AI Mental Health Companion</p>
                            </div>
                            
                            {authMode === 'login' ? (
                                <LoginForm
                                    onLogin={handleLogin}
                                    onSwitchToRegister={() => setAuthMode('register')}
                                />
                            ) : (
                                <RegisterForm
                                    onRegister={handleRegister}
                                    onSwitchToLogin={() => setAuthMode('login')}
                                />
                            )}
                        </div>
                    </div>
                ) : (
                    <div className="app-main">
                        <div className="app-header">
                            <div className="header-left">
                                <div className="app-logo">
                                    <span className="logo-icon">ðŸ§ </span>
                                    <span className="logo-text">MoodMate</span>
                                </div>
                            </div>
                            
                            <div className="header-center">
                                <nav className="main-nav">
                                    <button
                                        className={`nav-tab ${currentTab === 'chat' ? 'active' : ''}`}
                                        onClick={() => setCurrentTab('chat')}
                                    >
                                        ðŸ’¬ Chat
                                    </button>
                                    <button
                                        className={`nav-tab ${currentTab === 'therapists' ? 'active' : ''}`}
                                        onClick={() => setCurrentTab('therapists')}
                                    >
                                        ðŸ¥ Therapists
                                    </button>
                                    <button
                                        className={`nav-tab ${currentTab === 'progress' ? 'active' : ''}`}
                                        onClick={() => setCurrentTab('progress')}
                                    >
                                        ðŸ“Š Progress
                                    </button>
                                </nav>
                            </div>
                            
                            <div className="header-right">
                                <StatsDisplay user={user} />
                                
                                <div className="user-menu">
                                    <div className="user-info">
                                        <span className="user-name">{user.display_name}</span>
                                        <div className="user-avatar">
                                            {user.avatar_url ? (
                                                <img src={user.avatar_url} alt={user.display_name} />
                                            ) : (
                                                <span>ðŸ‘¤</span>
                                            )}
                                        </div>
                                    </div>
                                    
                                    <div className="user-actions">
                                        <button
                                            className="theme-toggle"
                                            onClick={() => setTheme(theme === 'dark' ? 'light' : 'dark')}
                                            title="Toggle theme"
                                        >
                                            {theme === 'dark' ? 'â˜€ï¸' : 'ðŸŒ™'}
                                        </button>
                                        
                                        <button
                                            className="logout-button"
                                            onClick={handleLogout}
                                            title="Logout"
                                        >
                                            ðŸšª
                                        </button>
                                    </div>
                                </div>
                            </div>
                        </div>
                        
                        <div className="app-content">
                            {currentTab === 'chat' && (
                                <ChatInterface 
                                    user={user} 
                                    onRewardEarned={handleRewardEarned}
                                />
                            )}
                            
                            {currentTab === 'therapists' && (
                                <TherapistDirectory 
                                    user={user} 
                                    onRewardEarned={handleRewardEarned}
                                />
                            )}
                            
                            {currentTab === 'progress' && (
                                <div className="progress-view">
                                    <h2>ðŸ“Š Your Progress</h2>
                                    <StatsDisplay user={user} />
                                    
                                    <div className="mood-history">
                                        <h3>Recent Mood History</h3>
                                        {user.mood_history && user.mood_history.length > 0 ? (
                                            <div className="mood-timeline">
                                                {user.mood_history.slice(-10).map((entry, index) => (
                                                    <div key={index} className="mood-entry">
                                                        <div className="mood-date">
                                                            {new Date(entry.timestamp).toLocaleDateString()}
                                                        </div>
                                                        <div className="mood-info">
                                                            <span className="mood-name">{entry.mood}</span>
                                                            <span className="mood-confidence">
                                                                {Math.round(entry.confidence * 100)}% confidence
                                                            </span>
                                                        </div>
                                                    </div>
                                                ))}
                                            </div>
                                        ) : (
                                            <p>Start chatting to track your mood over time!</p>
                                        )}
                                    </div>
                                    
                                    <div className="achievements-section">
                                        <h3>ðŸ† Achievements</h3>
                                        {user.achievements && user.achievements.length > 0 ? (
                                            <div className="achievements-grid">
                                                {user.achievements.map((achievement, index) => (
                                                    <div key={index} className="achievement-badge">
                                                        <span className="achievement-icon">ðŸ†</span>
                                                        <span className="achievement-name">
                                                            {achievement.replace('_', ' ').toUpperCase()}
                                                        </span>
                                                    </div>
                                                ))}
                                            </div>
                                        ) : (
                                            <p>Complete activities to earn achievements!</p>
                                        )}
                                    </div>
                                </div>
                            )}
                        </div>
                    </div>
                )}
                
                <NotificationSystem 
                    notifications={notifications} 
                    onRemove={removeNotification} 
                />
                
                {currentReward && (
                    <RewardModal 
                        reward={currentReward} 
                        onClose={() => setCurrentReward(null)} 
                    />
                )}
            </div>
        </AppContext.Provider>
    );
};
export default App;
