import React, { useState, useEffect, useRef } from "react";
import "./app.css";

function App() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [isTyping, setIsTyping] = useState(false);
  const [user, setUser] = useState(null);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [showLogin, setShowLogin] = useState(true);
  const [showRegister, setShowRegister] = useState(false);
  const [showForgotPassword, setShowForgotPassword] = useState(false);
  const [activeTab, setActiveTab] = useState("chat");
  const [journalEntries, setJournalEntries] = useState([]);
  const [therapists, setTherapists] = useState([]);
  const [resources, setResources] = useState([]);
  const [moodWall, setMoodWall] = useState([]);
  const [moodTribes, setMoodTribes] = useState([]);
  const [shopItems, setShopItems] = useState([]);
  const [userTribes, setUserTribes] = useState([]);
  const [currentTheme, setCurrentTheme] = useState("default");
  const [typingIndicator, setTypingIndicator] = useState("");
  const [showEmergencyModal, setShowEmergencyModal] = useState(false);
  const [achievements, setAchievements] = useState([]);
  const [activeTribe, setActiveTribe] = useState(null);
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [showNewChatModal, setShowNewChatModal] = useState(false);
  const [loginError, setLoginError] = useState("");
  const [registerError, setRegisterError] = useState("");
  const [aiProvider, setAiProvider] = useState("gemini");
  const [audioEnabled, setAudioEnabled] = useState(true);
  const [musicEnabled, setMusicEnabled] = useState(true);
  const [tribeChatMessages, setTribeChatMessages] = useState({});
  const [tribeInput, setTribeInput] = useState("");
  const [lastStreakUpdate, setLastStreakUpdate] = useState("");

  const chatEndRef = useRef(null);

  // Sample data
  const sampleTherapists = [
    {id: 1, name: "Dr. Priya Sharma", specialization: "Clinical Psychology", price: 799, rating: 4.7, experience: "8 years", avatar: "ðŸ‘©â€âš•ï¸"},
    {id: 2, name: "Rajiv Mehta", specialization: "Counseling Psychology", price: 599, rating: 4.5, experience: "5 years", avatar: "ðŸ‘¨â€âš•ï¸"},
    {id: 3, name: "Dr. Anjali Singh", specialization: "Psychiatry", price: 999, rating: 4.8, experience: "12 years", avatar: "ðŸ‘©â€âš•ï¸"},
    {id: 4, name: "Neha Gupta", specialization: "Art Therapy", price: 499, rating: 4.6, experience: "4 years", avatar: "ðŸŽ¨"}
  ];

  const sampleResources = [
    {type: "crisis", name: "National Suicide Prevention Lifeline", number: "1-800-273-8255", available: "24/7"},
    {type: "crisis", name: "Crisis Text Line", number: "Text HOME to 741741", available: "24/7"},
    {type: "support", name: "Mental Health America", website: "mhanational.org", description: "Resources and screening tools"}
  ];

  const sampleMoodTribes = [
    {id: 1, name: "Positivity Tribe", description: "For those seeking positivity and joy", member_count: 245, isMember: false},
    {id: 2, name: "Calm Collective", description: "Find your inner peace together", member_count: 189, isMember: false},
    {id: 3, name: "Anxiety Allies", description: "Support for those dealing with anxiety", member_count: 312, isMember: false},
    {id: 4, name: "Mindfulness Masters", description: "Practice mindfulness together", member_count: 156, isMember: false},
    {id: 5, name: "Breakup Recovery", description: "Healing after relationship endings", member_count: 128, isMember: false},
    {id: 6, name: "Career Stress", description: "Managing work-related stress", member_count: 201, isMember: false},
    {id: 7, name: "Student Support", description: "Academic pressure and study stress", member_count: 178, isMember: false}
  ];

  const sampleShopItems = [
    {id: 1, name: "Sunset Theme", description: "Warm orange and purple theme", price: 0, category: "theme"},
    {id: 2, name: "Ocean Theme", description: "Calming blue theme", price: 0, category: "theme"},
    {id: 3, name: "Forest Theme", description: "Green nature-inspired theme", price: 0, category: "theme"},
    {id: 4, name: "Neon Theme", description: "Vibrant neon colors", price: 5, category: "theme"},
    {id: 5, name: "Glass Theme", description: "Modern glassmorphism design", price: 0, category: "theme"},
    {id: 6, name: "Exclusive Avatar", description: "Special avatar frame", price: 0, category: "cosmetic"},
    {id: 7, name: "Meditation Pack", description: "Exclusive meditation guides", price: 5, category: "content"},
    {id: 8, name: "Second Chance Token", description: "Save your streak if you miss a day", price: 0, category: "utility"},
    {id: 9, name: "Custom Voice", description: "Personalized AI voice", price: 0, category: "feature"}
  ];

  const sampleAchievements = [
    {id: 1, name: "First Steps", description: "Complete your first journal entry", earned: true, icon: "ðŸ“"},
    {id: 2, name: "7-Day Warrior", description: "Maintain a 7-day streak", earned: false, icon: "ðŸ”¥"},
    {id: 3, name: "Community Builder", description: "Join your first tribe", earned: false, icon: "ðŸ‘¥"},
    {id: 4, name: "30-Day Calm Seeker", description: "Maintain a 30-day streak", earned: false, icon: "ðŸŒ¿"},
    {id: 5, name: "365-Day Legend", description: "Maintain a 365-day streak", earned: false, icon: "ðŸ‘‘"},
    {id: 6, name: "Mental Fitness Pro", description: "Reach 90+ mental fitness score", earned: false, icon: "ðŸ’ª"}
  ];

  // AI Response templates for different moods
  const aiResponses = {
    happy: [
      "That's wonderful to hear! ðŸ˜Š Your positivity is contagious. What's making you feel so happy today?",
      "I'm so glad you're feeling happy! ðŸŒŸ Celebrating the good moments is important. Want to share what's bringing you joy?",
      "Happiness looks good on you! âœ¨ Let's savor this moment together. What's making you smile today?"
    ],
    sad: [
      "I'm sorry you're feeling down. ðŸ˜” It's okay to have sad days - they make the happy ones brighter. Want to talk about what's bothering you?",
      "I hear you're feeling sad, and I'm here for you. ðŸ’™ Sometimes just sharing how we feel can lighten the load. What's on your mind?",
      "Thank you for sharing that you're feeling sad. It takes courage to acknowledge these feelings. ðŸŒ§ï¸ Would you like to talk more about what's causing this?"
    ],
    anxious: [
      "I understand that anxious feeling. ðŸ«‚ Let's take a deep breath together. Would you like to try a quick grounding exercise?",
      "Anxiety can be really overwhelming. ðŸ’­ I'm here to help you through it. Want to explore what's triggering these feelings?",
      "It's okay to feel anxious sometimes. ðŸŒ«ï¸ Let's work through this together. Would naming the specific worries help?"
    ],
    angry: [
      "I hear the frustration in your words. ðŸ’¢ Anger is a valid emotion - it's what we do with it that matters. Want to talk about what's upsetting you?",
      "It sounds like you're feeling pretty angry right now. ðŸ”¥ Let's channel that energy constructively. What's triggering this response?",
      "Anger can be a powerful messenger. ðŸ“£ I'm here to listen without judgment. Want to explore what's beneath the surface?"
    ],
    neutral: [
      "Thanks for checking in with me. ðŸ¤— How has your day been so far?",
      "I'm here to listen whenever you're ready to share. ðŸ’« What's on your mind today?",
      "Hello there! ðŸŒˆ How are you really feeling behind that neutral facade?"
    ]
  };

  // Load saved session
  useEffect(() => {
    const token = localStorage.getItem('accessToken');
    const refreshToken = localStorage.getItem('refreshToken');
    const userData = localStorage.getItem('user');
    const savedTheme = localStorage.getItem('theme') || 'default';
    const savedTribes = JSON.parse(localStorage.getItem('userTribes') || '[]');
    const savedAchievements = JSON.parse(localStorage.getItem('achievements') || '[]');
    const savedAiProvider = localStorage.getItem('aiProvider') || 'gemini';
    const savedAudio = localStorage.getItem('audioEnabled') !== 'false';
    const savedMusic = localStorage.getItem('musicEnabled') !== 'false';
    const savedLastStreakUpdate = localStorage.getItem('lastStreakUpdate') || '';
    
    if (token && userData) {
      const userObj = JSON.parse(userData);
      setUser(userObj);
      setIsLoggedIn(true);
      setShowLogin(false);
      setCurrentTheme(savedTheme);
      setUserTribes(savedTribes);
      setAchievements(savedAchievements);
      setAiProvider(savedAiProvider);
      setAudioEnabled(savedAudio);
      setMusicEnabled(savedMusic);
      setLastStreakUpdate(savedLastStreakUpdate);
      loadInitialData();
      
      // Update streak if it's a new day
      updateStreakAndCoins(userObj);
    }
  }, []);

  // Auto scroll
  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  // Check backend connectivity on app load
  useEffect(() => {
    checkBackendConnectivity();
  }, []);

  const checkBackendConnectivity = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/health');
      if (!response.ok) {
        console.warn("Backend server might be down");
      }
    } catch (error) {
      console.warn("Cannot connect to backend server", error);
    }
  };

  const refreshToken = async () => {
    try {
      const refreshToken = localStorage.getItem('refreshToken');
      if (!refreshToken) {
        throw new Error("No refresh token available");
      }

      const response = await fetch('http://localhost:5000/api/refresh', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ refreshToken })
      });
      
      if (response.ok) {
        const data = await response.json();
        localStorage.setItem('accessToken', data.token);
        return data.token;
      } else {
        console.error("Token refresh failed with status:", response.status);
        throw new Error("Token refresh failed");
      }
    } catch (error) {
      console.error("Token refresh error:", error);
      // Don't logout immediately, let the individual requests handle fallback
      throw error;
    }
  };

  const handleApiError = async (error, originalRequest = null) => {
    console.error("API Error:", error);
    
    // Handle token expiration
    if (error.message.includes("401") || error.message.includes("authentication")) {
      try {
        // Try to refresh the token
        const newToken = await refreshToken();
        
        // Retry the original request if provided
        if (originalRequest && newToken) {
          originalRequest.headers.Authorization = `Bearer ${newToken}`;
          const retryResponse = await fetch(originalRequest.url, originalRequest);
          return retryResponse;
        }
        
        return true;
      } catch (refreshError) {
        console.error("Token refresh failed:", refreshError);
        // Only logout if it's a critical operation, otherwise use fallback
        if (originalRequest && originalRequest.url.includes('/api/chat')) {
          handleLogout();
          alert("Session expired. Please login again.");
        }
        return false;
