 const sendMessage = async () => {
  if (!input.trim() || !isLoggedIn) return;

  const userMsg = { text: input, sender: "user" };
  setMessages(prev => [...prev, userMsg]);
  setInput("");
  setIsTyping(true);
  
  // Check for emergency keywords
  const emergencyKeywords = ['suicide', 'kill myself', 'end it all', 'want to die', 'self harm', 'hurt myself'];
  const hasEmergency = emergencyKeywords.some(keyword => 
    input.toLowerCase().includes(keyword)
  );
  
  if (hasEmergency) {
    setShowEmergencyModal(true);
    setIsTyping(false);
    return;
  }
  
  // Simulate typing animation
  simulateTyping();

  try {
    let token = localStorage.getItem('accessToken');
    
    // If no token, try to refresh first
    if (!token) {
      try {
        token = await refreshToken();
      } catch (refreshError) {
        console.error("No token available and refresh failed");
        throw new Error("Authentication required");
      }
    }

    const requestOptions = {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${token}`
      },
      body: JSON.stringify({ message: input })
    };

    let response = await fetch('http://localhost:5000/api/chat', requestOptions);

    // Handle 401 specifically - try to refresh token
    if (response.status === 401) {
      console.log("Token expired, attempting refresh...");
      try {
        token = await refreshToken();
        requestOptions.headers.Authorization = `Bearer ${token}`;
        response = await fetch('http://localhost:5000/api/chat', requestOptions);
        
        // If still failing after refresh, use fallback
        if (!response.ok) {
          throw new Error("Still unauthorized after refresh");
        }
      } catch (refreshError) {
        console.error("Token refresh failed:", refreshError);
        throw new Error("Authentication failed");
      }
    }

    if (!response.ok) {
      throw new Error(`API error: ${response.status}`);
    }

    const data = await response.json();
    handleChatResponse(data);

  } catch (error) {
    console.log("Chat API failed, using Gemini directly:", error);
    
    // Use Gemini directly as fallback
    try {
      await callGeminiDirectly(input);
    } catch (geminiError) {
      console.error("Gemini also failed:", geminiError);
      // Final fallback to diverse AI responses
      const mood = detectMoodFromInput(input);
      const moodResponses = aiResponses[mood] || aiResponses.neutral;
      const randomResponse = moodResponses[Math.floor(Math.random() * moodResponses.length)];
      
      const botMsg = { 
        text: randomResponse, 
        sender: "bot",
        mood: mood,
        moodEmoji: getMoodEmoji(mood),
        phrase: getMoodPhrase(mood)
      };
      setMessages(prev => [...prev, botMsg]);
    
  }

  setIsTyping(false);
  setTypingIndicator("");
};

