import React, { useState, useEffect, useRef } from 'react';
import './Community.css';

// Sample data
const initialMoodWall = [
  {
    id: 1, 
    userName: "MindfulExplorer", 
    text: "Just completed my first mindfulness meditation. Feeling incredible peace and clarity! ðŸŒ¿", 
    likes: 284,
    comments: 42,
    shares: 18,
    mood: "peaceful",
    timestamp: "2 minutes ago",
    userLevel: 27,
    userBadges: ["Meditation Expert", "Wellness Champion"],
    verified: true,
    liked: false
  },
  {
    id: 2, 
    userName: "CalmGuide", 
    text: "Our mindfulness group just had an amazing session. The positive energy was palpable! âœ¨", 
    likes: 567,
    comments: 89,
    shares: 34,
    mood: "calm",
    timestamp: "15 minutes ago",
    userLevel: 42,
    userBadges: ["Mindfulness Teacher", "Group Leader"],
    verified: true,
    liked: false
  }
];

const initialGroups = [
  {
    id: 1, 
    name: "ðŸ“š Study Stress & Exams", 
    description: "Share strategies for managing academic pressure and exam anxiety.", 
    members: 2456, 
    isMember: false,
    engagementLevel: 82,
    activity: "high",
    tags: ["academic", "exams", "stress"],
    messages: [
      { id: 1, user: "StudyGuide", text: "Remember to take regular breaks while studying. Pomodoro technique works wonders!", time: "9:30 AM" },
      { id: 2, user: "ExamSurvivor", text: "Anyone have tips for dealing with test anxiety?", time: "10:45 AM" },
      { id: 3, user: "FocusedMind", text: "Creating a study schedule really helped me stay organized.", time: "11:20 AM" }
    ]
  },
  {
    id: 2, 
    name: "ðŸ’¬ Friends & Social Life", 
    description: "Navigate social challenges and build healthier relationships.", 
    members: 1873, 
    isMember: false,
    engagementLevel: 78,
    activity: "medium",
    tags: ["friendship", "social", "relationships"],
    messages: [
      { id: 1, user: "SocialButterfly", text: "How do you maintain friendships when you're feeling down?", time: "10:15 AM" },
      { id: 2, user: "QuietSoul", text: "Social anxiety can be tough. Anyone else struggle with this?", time: "11:30 AM" },
      { id: 3, user: "GoodFriend", text: "Setting boundaries has been a game changer for my social life.", time: "12:45 PM" }
    ]
  },
  {
    id: 3, 
    name: "ðŸ  Family & Home Issues", 
    description: "Find support for family dynamics and home-related challenges.", 
    members: 1562, 
    isMember: false,
    engagementLevel: 75,
    activity: "medium",
    tags: ["family", "home", "support"],
    messages: [
      { id: 1, user: "FamilyFirst", text: "Dealing with family expectations can be overwhelming sometimes.", time: "8:30 AM" },
      { id: 2, user: "HomeHarmony", text: "How do you create a peaceful home environment?", time: "10:20 AM" },
      { id: 3, user: "Understanding", text: "Communication is key in family relationships.", time: "1:15 PM" }
    ]
  },
  {
    id: 4, 
    name: "ðŸ’¼ Work & Career Pressure", 
    description: "Manage workplace stress and career-related anxieties.", 
    members: 2894, 
    isMember: false,
    engagementLevel: 85,
    activity: "high",
    tags: ["work", "career", "pressure"],
    messages: [
      { id: 1, user: "CareerCoach", text: "Work-life balance is essential for mental health.", time: "9:15 AM" },
      { id: 2, user: "Ambitious", text: "Dealing with imposter syndrome at work? You're not alone.", time: "11:05 AM" },
      { id: 3, user: "WorkInProgress", text: "Setting boundaries with work email after hours has helped my mental health.", time: "4:30 PM" }
    ]
  },
  {
    id: 5, 
    name: "ðŸ’” Breakups & Relationships", 
    description: "Heal from heartbreak and navigate relationship challenges.", 
    members: 3217, 
    isMember: false,
    engagementLevel: 88,
    activity: "very high",
    tags: ["breakups", "relationships", "healing"],
    messages: [
      { id: 1, user: "HealingHeart", text: "Breakups are tough but remember you're not alone in this.", time: "10:30 AM" },
      { id: 2, user: "SelfLove", text: "Practicing self-care after a breakup is so important.", time: "12:15 PM" },
      { id: 3, user: "MovingForward", text: "It gets better with time. Be patient with yourself.", time: "2:45 PM" }
    ]
  },
  {
    id: 6, 
    name: "ðŸŒ‘ Self-Doubt & Confidence", 
    description: "Build self-esteem and overcome negative self-talk.", 
    members: 2756, 
    isMember: false,
    engagementLevel: 80,
    activity: "high",
    tags: ["confidence", "self-esteem", "growth"],
    messages: [
      { id: 1, user: "ConfidentYou", text: "Negative self-talk can be so damaging. How do you combat it?", time: "9:45 AM" },
      { id: 2, user: "SelfBelief", text: "Celebrating small wins has helped boost my confidence.", time: "11:20 AM" },
      { id: 3, user: "GrowingStrong", text: "Remember that everyone has insecurities - you're not alone.", time: "3:10 PM" }
    ]
  },
  {
    id: 7, 
    name: "ðŸš« Bullying & Discrimination", 
    description: "Find support and resources for dealing with bullying and discrimination.", 
    members: 1893, 
    isMember: false,
    engagementLevel: 83,
    activity: "high",
    tags: ["bullying", "discrimination", "support"],
    messages: [
      { id: 1, user: "StrongTogether", text: "No one should face bullying alone. We're here for each other.", time: "10:10 AM" },
      { id: 2, user: "EqualityForAll", text: "Sharing experiences can be healing and empowering.", time: "12:30 PM" },
      { id: 3, user: "Resilient", text: "Remember your worth is not defined by others' actions.", time: "2:15 PM" }
    ]
  },
  {
    id: 8, 
    name: "ðŸ’¸ Money & Financial Stress", 
    description: "Manage financial anxiety and develop healthier money habits.", 
    members: 2318, 
    isMember: false,
    engagementLevel: 79,
    activity: "medium",
    tags: ["money", "financial", "stress"],
    messages: [
      { id: 1, user: "FinancialPeace", text: "Budgeting has really helped reduce my money anxiety.", time: "9:30 AM" },
      { id: 2, user: "Thrifty", text: "Anyone have tips for saving money without feeling deprived?", time: "11:45 AM" },
      { id: 3, user: "MoneyMindful", text: "Financial stress can be overwhelming. Let's support each other.", time: "3:30 PM" }
    ]
  },
  {
    id: 9, 
    name: "Positive Mindset Collective", 
    description: "Focus on developing positive thinking patterns and emotional resilience.", 
    members: 1245, 
    isMember: false,
    engagementLevel: 87,
    activity: "high",
    tags: ["positivity", "resilience", "growth"],
    messages: [
      { id: 1, user: "GroupAdmin", text: "Welcome to our community! Feel free to introduce yourself.", time: "10:30 AM" },
      { id: 2, user: "SerenitySeeker", text: "Hello everyone! I'm excited to join this group.", time: "10:45 AM" },
      { id: 3, user: "MindfulUser", text: "Does anyone have tips for maintaining positivity during stressful times?", time: "11:20 AM" }
    ]
  },
  {
    id: 10, 
    name: "Anxiety Support Network", 
    description: "Support and techniques for managing anxiety and stress in daily life.", 
    members: 2312, 
    isMember: true,
    engagementLevel: 92,
    activity: "very high",
    tags: ["support", "anxiety-help", "calm"],
    messages: [
      { id: 1, user: "SupportGuide", text: "Remember to breathe deeply when feeling anxious. It helps!", time: "9:15 AM" },
      { id: 2, user: "CalmSoul", text: "I find that journaling really helps me process my thoughts.", time: "10:30 AM" },
      { id: 3, user: "PeacefulMind", text: "Has anyone tried the 5-4-3-2-1 grounding technique? It works wonders for me.", time: "11:05 AM" }
    ]
  }
];

function Community({ user }) {
  const [moodWall, setMoodWall] = useState(initialMoodWall);
  const [groups, setGroups] = useState(initialGroups);
  const [activeTab, setActiveTab] = useState('groups');
  const [selectedGroup, setSelectedGroup] = useState(null);
  const [newPostText, setNewPostText] = useState('');
  const [isCreatingPost, setIsCreatingPost] = useState(false);
  const [newMessage, setNewMessage] = useState('');
  const messagesEndRef = useRef(null);

  // Scroll to bottom of chat
  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [selectedGroup?.messages]);

  const handleAddPost = () => {
    if (newPostText.trim() !== "") {
      const newPost = {
        id: Date.now(),
        userName: user?.username || "Anonymous",
        text: newPostText,
        likes: 0,
        comments: 0,
        shares: 0,
        mood: "neutral",
        timestamp: "Just now",
        userLevel: user?.level || 1,
        userBadges: user?.badges || ["New Member"],
        verified: user?.verified || false,
        liked: false
      };
      setMoodWall(prev => [newPost, ...prev]);
      setNewPostText('');
      setIsCreatingPost(false);
    }
  };

  const handleJoinGroup = (groupId) => {
    setGroups(prevGroups => 
      prevGroups.map(group => 
        group.id === groupId ? { 
          ...group, 
          isMember: !group.isMember, 
          members: group.isMember ? group.members - 1 : group.members + 1 
        } : group
      )
    );
  };

  const handleLikePost = (postId) => {
    setMoodWall(prev => 
      prev.map(post => 
        post.id === postId ? { 
          ...post, 
          likes: post.liked ? post.likes - 1 : post.likes + 1,
          liked: !post.liked
        } : post
      )
    );
  };

  const handleCommentPost = (postId) => {
    setMoodWall(prev => 
      prev.map(post => 
        post.id === postId ? { ...post, comments: post.comments + 1 } : post
      )
    );
  };

  const handleSharePost = (postId) => {
    setMoodWall(prev => 
      prev.map(post => 
        post.id === postId ? { ...post, shares: post.shares + 1 } : post
      )
    );
    alert("Post shared successfully!");
  };

  const handleSendMessage = () => {
    if (newMessage.trim() !== "" && selectedGroup) {
      const updatedGroups = groups.map(group => {
        if (group.id === selectedGroup.id) {
          return {
            ...group,
            messages: [
              ...group.messages,
              {
                id: group.messages.length + 1,
                user: user?.username || "You",
                text: newMessage,
                time: new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })
              }
            ]
          };
        }
        return group;
      });
      
      setGroups(updatedGroups);
      setSelectedGroup(updatedGroups.find(g => g.id === selectedGroup.id));
      setNewMessage('');
    }
  };

  const renderEngagementIndicator = (level) => {
    return (
      <div className="engagement-indicator">
        <div className="engagement-bar">
          <div className="engagement-fill" style={{ width: `${level}%` }}></div>
        </div>
        <span className="engagement-value">{level}%</span>
      </div>
    );
  };

  const renderActivityLevel = (level) => {
    const levels = {
      "very high": { color: "#10B981", label: "Very Active" },
      "high": { color: "#84CC16", label: "Active" },
      "medium": { color: "#F59E0B", label: "Moderate" },
      "low": { color: "#EF4444", label: "Low" }
    };
    
    return (
      <span className="activity-level" style={{ color: levels[level]?.color }}>
        â— {levels[level]?.label}
      </span>
    );
  };

  return (
    <div className="community-hub">
      <div className="community-header">
        <h2>
          <span className="main-title">Community</span>
        </h2>
        <div className="community-controls">
          <div className="view-toggles">
            <button className={`view-toggle ${activeTab === 'feed' ? 'active' : ''}`} 
                    onClick={() => setActiveTab('feed')}>
              <span className="toggle-icon">ðŸ’¬</span> Feed
            </button>
            <button className={`view-toggle ${activeTab === 'groups' ? 'active' : ''}`} 
                    onClick={() => setActiveTab('groups')}>
              <span className="toggle-icon">ðŸ‘¥</span> Groups
            </button>
          </div>
        </div>
      </div>

      <div className="community-layout">
        <div className="main-content">
          {activeTab === 'feed' && (
            <div className="mood-wall-section">
              <div className="section-header">
                <h3>Community Feed</h3>
              </div>
              
              <div className="create-post-card">
                <div className="user-avatar">
                  {user?.username?.charAt(0) || "A"}
                </div>
                <div className="post-input">
                  <textarea 
                    placeholder="Share your thoughts with the community..." 
                    value={newPostText}
                    onChange={(e) => setNewPostText(e.target.value)}
                    onFocus={() => setIsCreatingPost(true)}
                  ></textarea>
                  {isCreatingPost && (
                    <div className="post-options">
                      <div className="mood-selector">
                        <span>How are you feeling?</span>
                        <div className="mood-options">
                          {["peaceful", "happy", "calm", "neutral", "anxious", "sad"].map(mood => (
                            <button key={mood} className="mood-option">{mood}</button>
                          ))}
                        </div>
                      </div>
                      <div className="post-actions">
                        <button className="cancel-btn" onClick={() => {
                          setIsCreatingPost(false);
                          setNewPostText('');
                        }}>Cancel</button>
                        <button className="share-btn" onClick={handleAddPost}>Share</button>
                      </div>
                    </div>
                  )}
                </div>
              </div>
              
              <div className="post-list">
                {moodWall.map(post => (
                  <div key={post.id} className="post-card">
                    <div className="post-header">
                      <div className="user-info">
                        <div className="post-avatar">{post.userName.charAt(0)}</div>
                        <div className="user-details">
                          <div className="username-row">
                            <span className="post-username">{post.userName}</span>
                            {post.verified && <span className="verified-badge">âœ“</span>}
                            <span className="user-level">Level {post.userLevel}</span>
                          </div>
                          <span className="post-timestamp">{post.timestamp}</span>
                        </div>
                      </div>
                      <div className="post-mood">
                        <span className={`mood-indicator ${post.mood}`}></span>
                        {post.mood}
                      </div>
                    </div>
                    <p className="post-content">{post.text}</p>
                    <div className="post-badges">
                      {post.userBadges.map((badge, index) => (
                        <span key={index} className="badge">{badge}</span>
                      ))}
                    </div>
                    <div className="post-actions">
                      <button 
                        className={`action-btn like ${post.liked ? 'active' : ''}`} 
                        onClick={() => handleLikePost(post.id)}
                      >
                        <span className="icon">ðŸ‘</span> Like ({post.likes})
                      </button>
                      <button 
                        className="action-btn comment" 
                        onClick={() => handleCommentPost(post.id)}
                      >
                        <span className="icon">ðŸ’¬</span> Comment ({post.comments})
                      </button>
                      <button 
                        className="action-btn share" 
                        onClick={() => handleSharePost(post.id)}
                      >
                        <span className="icon">â†—ï¸</span> Share ({post.shares})
                      </button>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          )}
          
          {activeTab === 'groups' && !selectedGroup && (
            <div className="groups-grid">
              <h3>Support Groups</h3>
              <div className="groups-container">
                {groups.map(group => (
                  <div key={group.id} className={`group-card ${group.isMember ? 'joined' : ''}`}>
                    <div className="group-header">
                      <h4>{group.name}</h4>
                      {renderEngagementIndicator(group.engagementLevel)}
                    </div>
                    <p className="group-description">{group.description}</p>
                    <div className="group-meta">
                      <div className="group-stats">
                        <span className="members-count">ðŸ‘¥ {group.members.toLocaleString()} members</span>
                        {renderActivityLevel(group.activity)}
                      </div>
                      <div className="group-tags">
                        {group.tags.map(tag => (
                          <span key={tag} className="tag">#{tag}</span>
                        ))}
                      </div>
                    </div>
                    <div className="group-footer">
                      <button 
                        className={`join-btn ${group.isMember ? 'joined' : ''}`}
                        onClick={() => handleJoinGroup(group.id)}
                      >
                        {group.isMember ? 'Joined' : 'Join Group'}
                      </button>
                      <button 
                        className="explore-btn"
                        onClick={() => setSelectedGroup(group)}
                      >
                        {group.isMember ? 'Enter Chat' : 'View Group'}
                      </button>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          )}

          {activeTab === 'groups' && selectedGroup && (
            <div className="group-chat-container">
              <div className="chat-header">
                <button className="back-button" onClick={() => setSelectedGroup(null)}>
                  â† Back
                </button>
                <h3>{selectedGroup.name}</h3>
                <div className="member-count">
                  {selectedGroup.members} members
                </div>
              </div>
              
              <div className="chat-messages">
                {selectedGroup.messages.map(message => (
                  <div 
                    key={message.id} 
                    className={`message ${message.user === (user?.username || "You") ? 'own-message' : ''}`}
                  >
                    <div className="message-header">
                      <span className="message-user">{message.user}</span>
                      <span className="message-time">{message.time}</span>
                    </div>
                    <div className="message-content">{message.text}</div>
                  </div>
                ))}
                <div ref={messagesEndRef} />
              </div>
              
              <div className="chat-input-container">
                <input
                  type="text"
                  placeholder="Type your message here..."
                  value={newMessage}
                  onChange={(e) => setNewMessage(e.target.value)}
                  onKeyPress={(e) => e.key === 'Enter' && handleSendMessage()}
                  className="chat-input"
                />
                <button 
                  className="send-button"
                  onClick={handleSendMessage}
                  disabled={!newMessage.trim()}
                >
                  Send
                </button>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default Community;
