import React, { useState, useEffect, useRef } from 'react';
import './Community.css';

// Enhanced sample data with futuristic features
const initialMoodWall = [
    {
        id: 1, 
        userName: "NeuroExplorer7", 
        text: "Just completed my first neural sync meditation. Feeling incredible resonance with the collective consciousness! ðŸŒŒ", 
        likes: 284,
        comments: 42,
        shares: 18,
        mood: "euphoric",
        timestamp: "2 minutes ago",
        userLevel: 27,
        userBadges: ["Quantum Meditator", "Empathy Champion"],
        verified: true
    },
    {
        id: 2, 
        userName: "ZenHarmonizer", 
        text: "Our mindfulness tribe just achieved Level 5 coherence in our group meditation session. The energy was palpable! âœ¨", 
        likes: 567,
        comments: 89,
        shares: 34,
        mood: "calm",
        timestamp: "15 minutes ago",
        userLevel: 42,
        userBadges: ["Mindfulness Master", "Tribe Leader"],
        verified: true
    },
    {
        id: 3, 
        userName: "Anonymous", 
        text: "Struggling with quantum anxiety fluctuations today. Grateful for the neural calming protocols in the app.", 
        likes: 162,
        comments: 35,
        shares: 2,
        mood: "anxious",
        timestamp: "1 hour ago",
        userLevel: 15,
        userBadges: ["Courage Explorer"],
        verified: false
    }
];

const initialTribes = [
    {
        id: 1, 
        name: "Quantum Positivity Collective", 
        description: "Harness quantum principles to amplify positive emotional states across our neural network.", 
        members: 1245, 
        isMember: false,
        coherenceLevel: 87,
        activity: "high",
        tags: ["quantum", "positivity", "energy"]
    },
    {
        id: 2, 
        name: "Neural Calm Alliance", 
        description: "Advanced techniques for anxiety modulation and neural pathway recalibration.", 
        members: 2312, 
        isMember: true,
        coherenceLevel: 92,
        activity: "very high",
        tags: ["neural", "calm", "anxiety-support"]
    },
    {
        id: 3, 
        name: "Mindfulness Resonance Guild", 
        description: "Achieve harmonic resonance through synchronized mindfulness practices.", 
        members: 1156, 
        isMember: false,
        coherenceLevel: 78,
        activity: "medium",
        tags: ["resonance", "mindfulness", "sync"]
    }
];

const initialChallenges = [
    {
        id: 1, 
        title: "7-Day Neural Coherence Challenge", 
        description: "Enhance your brainwave coherence through daily guided practices.", 
        reward: "Neural Harmony Badge + 300 energy credits",
        participants: 3421,
        duration: "7 days",
        difficulty: "intermediate"
    },
    {
        id: 2, 
        title: "Quantum Gratitude Resonance", 
        description: "Amplify gratitude frequencies across our collective consciousness field.", 
        reward: "Quantum Gratitude Badge + 200 energy credits",
        participants: 2897,
        duration: "5 days",
        difficulty: "beginner"
    }
];

const initialLiveEvents = [
    {
        id: 1,
        title: "Global Synchronized Meditation",
        host: "Master Yuna",
        participants: 12478,
        startTime: "30 minutes",
        duration: "45 min",
        energyLevel: "high"
    },
    {
        id: 2,
        title: "Neural Pathway Optimization Workshop",
        host: "Dr. Aris",
        participants: 5873,
        startTime: "2 hours",
        duration: "1 hour",
        energyLevel: "medium"
    }
];

function Community({ user }) {
    const [moodWall, setMoodWall] = useState(initialMoodWall);
    const [moodTribes, setMoodTribes] = useState(initialTribes);
    const [activeTab, setActiveTab] = useState('feed');
    const [holographicMode, setHolographicMode] = useState(false);
    const [selectedTribe, setSelectedTribe] = useState(null);
    const [newPostText, setNewPostText] = useState('');
    const [isCreatingPost, setIsCreatingPost] = useState(false);
    const canvasRef = useRef(null);

    useEffect(() => {
        if (holographicMode && canvasRef.current) {
            const canvas = canvasRef.current;
            const ctx = canvas.getContext('2d');
            canvas.width = window.innerWidth;
            canvas.height = window.innerHeight;
            
            // Neural network visualization for holographic mode
            const nodes = [];
            const connections = [];
            
            // Create nodes
            for (let i = 0; i < 50; i++) {
                nodes.push({
                    x: Math.random() * canvas.width,
                    y: Math.random() * canvas.height,
                    radius: Math.random() * 2 + 1,
                    speed: Math.random() * 2 + 0.5
                });
            }
            
            // Create connections
            for (let i = 0; i < nodes.length; i++) {
                for (let j = i + 1; j < nodes.length; j++) {
                    if (Math.random() < 0.02) {
                        connections.push({
                            from: i,
                            to: j,
                            active: Math.random() > 0.5
                        });
                    }
                }
            }
            
            const animate = () => {
                ctx.clearRect(0, 0, canvas.width, canvas.height);
                
                // Draw connections
                ctx.strokeStyle = 'rgba(100, 200, 255, 0.1)';
                ctx.lineWidth = 0.5;
                
                connections.forEach(conn => {
                    if (conn.active) {
                        ctx.beginPath();
                        ctx.moveTo(nodes[conn.from].x, nodes[conn.from].y);
                        ctx.lineTo(nodes[conn.to].x, nodes[conn.to].y);
                        ctx.stroke();
                    }
                });
                
                // Draw nodes
                nodes.forEach(node => {
                    ctx.beginPath();
                    ctx.arc(node.x, node.y, node.radius, 0, Math.PI * 2);
                    ctx.fillStyle = 'rgba(100, 200, 255, 0.5)';
                    ctx.fill();
                    
                    // Move nodes
                    node.x += (Math.random() - 0.5) * node.speed;
                    node.y += (Math.random() - 0.5) * node.speed;
                    
                    // Keep within bounds
                    if (node.x < 0) node.x = canvas.width;
                    if (node.x > canvas.width) node.x = 0;
                    if (node.y < 0) node.y = canvas.height;
                    if (node.y > canvas.height) node.y = 0;
                });
                
                if (holographicMode) requestAnimationFrame(animate);
            };
            
            animate();
        }
    }, [holographicMode]);

    const handleAddPost = () => {
        if (newPostText.trim() !== "") {
            const newPost = {
                id: Date.now(),
                userName: user?.username || "Anonymous",
                text: newPostText,
                likes: 0,
                comments: 0,
                shares: 0,
                mood: "neutral",
                timestamp: "Just now",
                userLevel: user?.level || 1,
                userBadges: user?.badges || ["New Explorer"],
                verified: user?.verified || false
            };
            setMoodWall(prev => [newPost, ...prev]);
            setNewPostText('');
            setIsCreatingPost(false);
        }
    };

    const handleJoinTribe = (tribeId) => {
        setMoodTribes(prevTribes => 
            prevTribes.map(tribe => 
                tribe.id === tribeId ? { 
                    ...tribe, 
                    isMember: !tribe.isMember, 
                    members: tribe.isMember ? tribe.members - 1 : tribe.members + 1 
                } : tribe
            )
        );
    };

    const handleLikePost = (postId) => {
        setMoodWall(prev => 
            prev.map(post => 
                post.id === postId ? { ...post, likes: post.likes + 1 } : post
            )
        );
    };

    const renderCoherenceIndicator = (level) => {
        return (
            <div className="coherence-indicator">
                <div className="coherence-bar">
                    <div className="coherence-fill" style={{ width: `${level}%` }}></div>
                </div>
                <span className="coherence-value">{level}%</span>
            </div>
        );
    };

    const renderActivityLevel = (level) => {
        const levels = {
            "very high": { color: "#4CAF50", label: "Very High" },
            "high": { color: "#8BC34A", label: "High" },
            "medium": { color: "#FFC107", label: "Medium" },
            "low": { color: "#FF9800", label: "Low" }
        };
        
        return (
            <span className="activity-level" style={{ color: levels[level]?.color }}>
                â— {levels[level]?.label}
            </span>
        );
    };

    return (
        <div className={`community-hub tab-content ${holographicMode ? 'holographic-mode' : ''}`}>
            {holographicMode && <canvas ref={canvasRef} className="holographic-canvas"></canvas>}
            
            <div className="community-header">
                <h2>
                    <span className="futuristic-title">NEURAL CONNECTION MATRIX</span>
                    <span className="subtitle">Global Consciousness Network</span>
                </h2>
                <div className="community-controls">
                    <div className="view-toggles">
                        <button className={`view-toggle ${activeTab === 'feed' ? 'active' : ''}`} 
                                onClick={() => setActiveTab('feed')}>
                            <span className="toggle-icon">ðŸ“¡</span> Neural Feed
                        </button>
                        <button className={`view-toggle ${activeTab === 'tribes' ? 'active' : ''}`} 
                                onClick={() => setActiveTab('tribes')}>
                            <span className="toggle-icon">ðŸ”—</span> Resonance Tribes
                        </button>
                        <button className={`view-toggle ${activeTab === 'events' ? 'active' : ''}`} 
                                onClick={() => setActiveTab('events')}>
                            <span className="toggle-icon">ðŸŒ</span> Live Events
                        </button>
                    </div>
                    <button className={`holographic-toggle ${holographicMode ? 'active' : ''}`}
                            onClick={() => setHolographicMode(!holographicMode)}>
                        {holographicMode ? 'DISABLE HOLO' : 'ACTIVATE HOLO'}
                    </button>
                </div>
            </div>

            <div className="community-layout">
                <div className="main-content">
                    {activeTab === 'feed' && (
                        <div className="mood-wall-section">
                            <div className="section-header">
                                <h3>ðŸŒŒ Collective Consciousness Stream</h3>
                                <div className="global-stats">
                                    <div className="stat">
                                        <span className="stat-value">24.7K</span>
                                        <span className="stat-label">Active Users</span>
                                    </div>
                                    <div className="stat">
                                        <span className="stat-value">87%</span>
                                        <span className="stat-label">Global Coherence</span>
                                    </div>
                                </div>
                            </div>
                            
                            <div className="create-post-card holographic">
                                <div className="user-avatar">
                                    {user?.username?.charAt(0) || "A"}
                                </div>
                                <div className="post-input">
                                    <textarea 
                                        placeholder="Transmit your thoughts to the collective..." 
                                        value={newPostText}
                                        onChange={(e) => setNewPostText(e.target.value)}
                                        onFocus={() => setIsCreatingPost(true)}
                                    ></textarea>
                                    {isCreatingPost && (
                                        <div className="post-options">
                                            <div className="mood-selector">
                                                <span>Select Mood:</span>
                                                <div className="mood-options">
                                                    {["euphoric", "happy", "calm", "neutral", "anxious", "sad"].map(mood => (
                                                        <button key={mood} className="mood-option">{mood}</button>
                                                    ))}
                                                </div>
                                            </div>
                                            <div className="post-actions">
                                                <button className="cancel-btn" onClick={() => {
                                                    setIsCreatingPost(false);
                                                    setNewPostText('');
                                                }}>Cancel</button>
                                                <button className="transmit-btn" onClick={handleAddPost}>Transmit</button>
                                            </div>
                                        </div>
                                    )}
                                </div>
                            </div>
                            
                            <div className="post-list">
                                {moodWall.map(post => (
                                    <div key={post.id} className="post-card holographic">
                                        <div className="post-header">
                                            <div className="user-info">
                                                <div className="post-avatar">{post.userName.charAt(0)}</div>
                                                <div className="user-details">
                                                    <div className="username-row">
                                                        <span className="post-username">{post.userName}</span>
                                                        {post.verified && <span className="verified-badge">âœ“</span>}
                                                        <span className="user-level">Level {post.userLevel}</span>
                                                    </div>
                                                    <span className="post-timestamp">{post.timestamp}</span>
                                                </div>
                                            </div>
                                            <div className="post-mood">
                                                <span className={`mood-indicator ${post.mood}`}></span>
                                                {post.mood}
                                            </div>
                                        </div>
                                        <p className="post-content">{post.text}</p>
                                        <div className="post-badges">
                                            {post.userBadges.map((badge, index) => (
                                                <span key={index} className="badge">{badge}</span>
                                            ))}
                                        </div>
                                        <div className="post-actions">
                                            <button className="action-btn like" onClick={() => handleLikePost(post.id)}>
                                                <span className="icon">âš¡</span> Energize ({post.likes})
                                            </button>
                                            <button className="action-btn comment">
                                                <span className="icon">ðŸ’¬</span> Resonate ({post.comments})
                                            </button>
                                            <button className="action-btn share">
                                                <span className="icon">ðŸ“¤</span> Amplify ({post.shares})
                                            </button>
                                        </div>
                                    </div>
                                ))}
                            </div>
                        </div>
                    )}
                    
                    {activeTab === 'tribes' && (
                        <div className="tribes-grid">
                            <h3>ðŸ”— Neural Resonance Tribes</h3>
                            <div className="tribes-container">
                                {moodTribes.map(tribe => (
                                    <div key={tribe.id} className={`tribe-card holographic ${tribe.isMember ? 'joined' : ''}`}>
                                        <div className="tribe-header">
                                            <h4>{tribe.name}</h4>
                                            {renderCoherenceIndicator(tribe.coherenceLevel)}
                                        </div>
                                        <p className="tribe-description">{tribe.description}</p>
                                        <div className="tribe-meta">
                                            <div className="tribe-stats">
                                                <span className="members-count">ðŸ‘¥ {tribe.members.toLocaleString()} members</span>
                                                {renderActivityLevel(tribe.activity)}
                                            </div>
                                            <div className="tribe-tags">
                                                {tribe.tags.map(tag => (
                                                    <span key={tag} className="tag">#{tag}</span>
                                                ))}
                                            </div>
                                        </div>
                                        <div className="tribe-footer">
                                            <button 
                                                className={`join-btn ${tribe.isMember ? 'joined' : ''}`}
                                                onClick={() => handleJoinTribe(tribe.id)}
                                            >
                                                {tribe.isMember ? 'Connected' : 'Connect Neural Link'}
                                            </button>
                                            <button className="explore-btn">Explore Tribe</button>
                                        </div>
                                    </div>
                                ))}
                            </div>
                        </div>
                    )}
                    
                    {activeTab === 'events' && (
                        <div className="live-events-section">
                            <h3>ðŸŒ Live Consciousness Events</h3>
                            <div className="events-grid">
                                {initialLiveEvents.map(event => (
                                    <div key={event.id} className="event-card holographic">
                                        <div className="event-header">
                                            <h4>{event.title}</h4>
                                            <div className="event-status live">LIVE</div>
                                        </div>
                                        <div className="event-details">
                                            <div className="event-host">
                                                <span className="label">Host:</span>
                                                <span className="value">{event.host}</span>
                                            </div>
                                            <div className="event-participants">
                                                <span className="label">Participants:</span>
                                                <span className="value">{event.participants.toLocaleString()}</span>
                                            </div>
                                            <div className="event-time">
                                                <span className="label">Starts in:</span>
                                                <span className="value">{event.startTime}</span>
                                            </div>
                                            <div className="event-energy">
                                                <span className="label">Energy Level:</span>
                                                <span className="value">{event.energyLevel}</span>
                                            </div>
                                        </div>
                                        <div className="event-actions">
                                            <button className="join-event-btn">Join Event</button>
                                            <button className="remind-btn">Set Reminder</button>
                                        </div>
                                    </div>
                                ))}
                            </div>
                            
                            <div className="challenges-section">
                                <h3>ðŸ† Consciousness Challenges</h3>
                                <div className="challenges-grid">
                                    {initialChallenges.map(challenge => (
                                        <div key={challenge.id} className="challenge-card holographic">
                                            <div className="challenge-header">
                                                <h4>{challenge.title}</h4>
                                                <div className="difficulty-badge">{challenge.difficulty}</div>
                                            </div>
                                            <p className="challenge-description">{challenge.description}</p>
                                            <div className="challenge-meta">
                                                <div className="participants">
                                                    <span className="icon">ðŸ‘¥</span> 
                                                    {challenge.participants.toLocaleString()} participating
                                                </div>
                                                <div className="duration">
                                                    <span className="icon">â±</span> 
                                                    {challenge.duration}
                                                </div>
                                            </div>
                                            <div className="challenge-reward">{challenge.reward}</div>
                                            <button className="join-challenge-btn">Activate Challenge</button>
                                        </div>
                                    ))}
                                </div>
                            </div>
                        </div>
                    )}
                </div>
                
                <div className="community-sidebar">
                    <div className="sidebar-card holographic">
                        <h3>ðŸ§  Your Neural Presence</h3>
                        <div className="user-stats">
                            <div className="stat">
                                <span className="stat-value">87%</span>
                                <span className="stat-label">Coherence</span>
                            </div>
                            <div className="stat">
                                <span className="stat-value">5</span>
                                <span className="stat-label">Tribes</span>
                            </div>
                            <div className="stat">
                                <span className="stat-value">142</span>
                                <span className="stat-label">Connections</span>
                            </div>
                        </div>
                        <div className="energy-status">
                            <div className="energy-bar">
                                <div className="energy-fill" style={{ width: '65%' }}></div>
                            </div>
                            <span className="energy-label">Mental Energy: 65/100</span>
                        </div>
                    </div>
                    
                    <div className="sidebar-card holographic">
                        <h3>ðŸš€ Trending in the Collective</h3>
                        <div className="trending-list">
                            <div className="trending-item">
                                <span className="trending-rank">1</span>
                                <div className="trending-info">
                                    <span className="trending-tag">#QuantumGratitude</span>
                                    <span className="trending-count">24.5K posts</span>
                                </div>
                            </div>
                            <div className="trending-item">
                                <span className="trending-rank">2</span>
                                <div className="trending-info">
                                    <span className="trending-tag">#NeuralSync</span>
                                    <span className="trending-count">18.7K posts</span>
                                </div>
                            </div>
                            <div className="trending-item">
                                <span className="trending-rank">3</span>
                                <div className="trending-info">
                                    <span className="trending-tag">#ConsciousnessExpansion</span>
                                    <span className="trending-count">15.2K posts</span>
                                </div>
                            </div>
                        </div>
                    </div>
                    
                    <div className="sidebar-card holographic">
                        <h3>â­ Recommended Connections</h3>
                        <div className="connections-list">
                            <div className="connection">
                                <div className="connection-avatar">M</div>
                                <div className="connection-info">
                                    <span className="connection-name">MindfulExplorer</span>
                                    <span className="connection-level">Level 32</span>
                                </div>
                                <button className="connect-btn">Connect</button>
                            </div>
                            <div className="connection">
                                <div className="connection-avatar">S</div>
                                <div className="connection-info">
                                    <span className="connection-name">SerenitySeeker</span>
                                    <span className="connection-level">Level 45</span>
                                </div>
                                <button className="connect-btn">Connect</button>
                            </div>
                            <div className="connection">
                                <div className="connection-avatar">Z</div>
                                <div className="connection-info">
                                    <span className="connection-name">ZenMaster</span>
                                    <span className="connection-level">Level 57</span>
                                </div>
                                <button className="connect-btn">Connect</button>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default Community;
