    let phase = "inhale";
    const phaseDurations = { inhale: 4000, hold: 4000, exhale: 4000 };
    let phaseProgress = 0;
    const intervalTime = 50; // Update every 50ms for smooth animation

    const runCycle = () => {
        phaseProgress += intervalTime;
        const currentPhaseDuration = phaseDurations[phase];
        setBreathingProgress((phaseProgress / currentPhaseDuration) * 100);

        if (phaseProgress >= currentPhaseDuration) {
            phaseProgress = 0;
            if (phase === "inhale") phase = "hold";
            else if (phase === "hold") phase = "exhale";
            else phase = "inhale";
            setBreathingPhase(phase);
        }
