// Update the analyzeMood function to return actual analysis
const analyzeMood = (text) => {
  const lowerText = text.toLowerCase();
  const positiveWords = ['happy', 'great', 'good', 'joy', 'wonderful', 'amazing', 'productive', 'excited', 'love', 'loved', 'like', 'liked', 'enjoy', 'enjoyed', 'fun', 'pleasure', 'nice', 'better', 'best', 'awesome', 'excellent', 'fantastic', 'beautiful', 'perfect', 'positive', 'optimistic', 'hope', 'hopeful', 'proud', 'confident', 'grateful', 'thankful', 'appreciate', 'bliss', 'calm', 'peaceful', 'relaxed', 'content', 'satisfied', 'pleased', 'delighted', 'blissful', 'jubilant', 'ecstatic', 'thrilled', 'overjoyed', 'radiant', 'serene', 'tranquil'];
  const negativeWords = ['sad', 'depressed', 'unhappy', 'down', 'crying', 'miserable', 'angry', 'mad', 'frustrated', 'annoyed', 'irritated', 'upset', 'disappointed', 'disappointing', 'hate', 'hated', 'terrible', 'awful', 'horrible', 'bad', 'worse', 'worst', 'anxious', 'worried', 'stressed', 'nervous', 'scared', 'afraid', 'fear', 'fearful', 'panic', 'panicked', 'overwhelmed', 'dread', 'dreadful', 'guilty', 'ashamed', 'embarrassed', 'humiliated', 'lonely', 'alone', 'isolated', 'hurt', 'pain', 'painful', 'suffer', 'suffering', 'grief', 'grieving', 'heartbroken', 'broken', 'tired', 'exhausted', 'fatigued', 'weary', 'burned out', 'hopeless', 'helpless', 'powerless', 'worthless', 'useless', 'failure', 'fail', 'defeated'];
  const anxiousWords = ['anxious', 'worried', 'stressed', 'nervous', 'scared', 'afraid', 'fear', 'fearful', 'panic', 'panicked', 'overwhelmed', 'dread', 'dreadful'];

  // Count occurrences of each type of word
  const positiveCount = positiveWords.filter(word => lowerText.includes(word)).length;
  const negativeCount = negativeWords.filter(word => lowerText.includes(word)).length;
  const anxiousCount = anxiousWords.filter(word => lowerText.includes(word)).length;

  // Determine mood based on word counts
  if (negativeCount > positiveCount && negativeCount > anxiousCount) return 'sad';
  if (anxiousCount > positiveCount && anxiousCount > negativeCount) return 'anxious';
  if (positiveCount > negativeCount && positiveCount > anxiousCount) return 'happy';
  
  return 'neutral';
};

