    <button onClick={() => setShowSleepCompanion(true)} className="quick-action-btn">
      <span className="action-icon">ðŸŒ™</span>
      <span>Sleep</span>
    </button>
     <button onClick={() => setShowMoodReport(true)} className="quick-action-btn">
      <span className="action-icon">ðŸ“Š</span>
      <span>Report</span>
    </button>
  </div>
);
};

return (
<div className={`app-container ${userData.currentTheme}-theme ${nightMode ? 'night-mode' : ''}`}>
  <EmergencyModal />
  <NewChatModal />
  <LoginBonusModal />
  <MeditationGuideModal />
  <BreathingExerciseModal />
  <TherapistInfoModal />
  <TherapistFormModal />
  <PaymentModal />
  <GamesModal />
  <AICoachModal />
  <SleepCompanionModal />
  <ChallengeModal />
  <JournalAnalysisModal />
  <MoodTrackerModal />
  <MoodReportModal />
  
  <ChatHistorySidebar />
  
  {activeGame === "bubble" && <BubbleGame />}
  {activeGame === "coloring" && <ColoringGame />}
  {activeGame === "stressBalloon" && <StressBalloonGame />}
  {activeGame === "gratitude" && <GratitudeWheelGame />}
  {activeGame === "breathing" && <BreathingExerciseModal />}
  
  <header className="app-header">
    <div className="header-left">
      <button onClick={() => setSidebarOpen(true)} className="menu-btn">â˜°</button>
      <h1>ðŸŒ™ MoodMate</h1>
      <div className="user-stats">
        <span className="streak-display">ðŸ”¥ {userData.streak || 0}</span>
        <span>ðŸª™ {userData.coins || 0}</span>
        {isPremium && <span className="premium-indicator">ðŸŒŸ</span>}
      </div>
    </div>
    <div className="header-right">
      <div className="user-info">
        <span className="user-name">{userData.name}</span>
        <span className="user-status">Online</span>
      </div>
      <button onClick={onLogout} className="logout-btn">Logout</button>
    </div>
  </header>

  <nav className="app-nav">
    <button 
      className={activeTab === "chat" ? "active" : ""} 
      onClick={() => setActiveTab("chat")}
    >
      ðŸ’¬ Chat
    </button>
    <button 
      className={activeTab === "journal" ? "active" : ""} 
      onClick={() => setActiveTab("journal")}
    >
      ðŸ““ Journal
    </button>
    <button 
      className={activeTab === "therapy" ? "active" : ""} 
      onClick={() => setActiveTab("therapy")}
    >
      ðŸ‘¨â€âš•ï¸ Therapy
    </button>
    <button 
      className={activeTab === "community" ? "active" : ""} 
      onClick={() => setActiveTab("community")}
    >
      ðŸŒ Community
    </button>
    <button 
      className={activeTab === "shop" ? "active" : ""} 
      onClick={() => setActiveTab("shop")}
    >
      ðŸ›’ Shop
    </button>
    <button 
      className={activeTab === "profile" ? "active" : ""} 
      onClick={() => setActiveTab("profile")}
    >
      ðŸ‘¤ Profile
    </button>
    <button 
      className={activeTab === "premium" ? "active premium-nav-btn" : "premium-nav-btn"} 
      onClick={() => setActiveTab("premium")}
    >
      {isPremium ? "ðŸŒŸ Premium" : "ðŸ’Ž Premium"}
    </button>
  </nav>
  
  {/* --- NEW LOCATION FOR QUICK ACTIONS --- */}
  {activeTab === "chat" && <QuickActionsMenu />}

  <main className="app-main">
    {activeTab === "chat" && <ChatTab />}
    {activeTab === "journal" && (
      <div className="journal-tab">
        <div className="journal-header">
          <h2>Your Journal</h2>
          <div className="journal-actions">
            <button onClick={addJournalEntry} className="add-entry-btn">
              + New Entry
            </button>
            <button onClick={() => setShowJournalAnalysis(true)} className="analyze-btn">
              ðŸ“Š Analyze
            </button>
            <button onClick={() => setShowMoodTracker(true)} className="mood-btn">
              ðŸ˜Š Track Mood
            </button>
          </div>
        </div>
        
        <div className="journal-stats">
          <div className="stat-card">
            <h3>Total Entries</h3>
            <p>{journalEntries.length}</p>
          </div>
          <div className="stat-card">
            <h3>This Week</h3>
            <p>{journalEntries.filter(e => new Date(e.date) > new Date(Date.now() - 7*24*60*60*1000)).length}</p>
          </div>
          <div className="stat-card">
            <h3>Shared</h3>
            <p>{journalEntries.filter(e => e.shared).length}</p>
          </div>
        </div>
        
        <div className="journal-entries">
          {journalEntries.length === 0 ? (
            <div className="empty-state">
              <p>No journal entries yet. Write your first entry!</p>
              <button onClick={addJournalEntry}>Start Journaling</button>
            </div>
          ) : (
            journalEntries.map((entry, index) => (
              <div key={`entry-${entry.id}-${index}`} className="journal-entry">
                <div className="entry-header">
                  <div className="entry-date">{entry.date}</div>
                  <div className="entry-actions">
                    {!entry.shared && userTribes.length > 0 && (
                      <button 
                        onClick={() => shareJournalEntry(entry.id)}
                        className="share-btn"
                        title="Share to your tribes"
                      >
                        Share
                      </button>
                    )}
                    <button className="edit-btn">Edit</button>
                  </div>
                </div>
                <div className="entry-text">{entry.text}</div>
                {entry.summary && (
                  <div className="entry-summary">
                    <strong>Summary:</strong> {entry.summary}
                  </div>
                )}
                {entry.mood && (
                  <div className="entry-mood">
                    <strong>Mood:</strong> {entry.mood}
                  </div>
                )}
              </div>
            ))
          )}
        </div>
      </div>
    )}
    {activeTab === "therapy" && <TherapySection />}
    {activeTab === "community" && activeTribe ? (
      <TribeCommunity />
    ) : activeTab === "community" ? (
      <CommunitySection />
    ) : null}
    {activeTab === "shop" && <ShopSection />}
    {activeTab === "profile" && <ProfileSection />}
    {activeTab === "premium" && (
      <div className="premium-tab">
        <PremiumFeaturesBox 
          isPremium={isPremium}
          onUpgradeClick={() => setShowPremiumPlans(true)}
        />
        <PremiumPlans 
          user={userData} 
          onSubscribe={handlePremiumSubscribe}
          onClose={() => setShowPremiumPlans(false)} 
        />
      </div>
    )}
  </main>

  {showPremiumPlans && (
    <PremiumPlans 
      user={userData} 
      onSubscribe={handlePremiumSubscribe}
      onClose={() => setShowPremiumPlans(false)} 
    />
  )}
</div> 
);
}

export default App;
