  const achievement365 = newAchievements.find(a => a.id === 5);
  
  if (streak >= 7 && achievement7 && !achievement7.earned) {
    achievement7.earned = true;
    updated = true;
    alert("🎉 Achievement Unlocked: 7-Day Warrior!");
  }
  
  if (streak >= 30 && achievement30 && !achievement30.earned) {
    achievement30.earned = true;
    updated = true;
    alert("🎉 Achievement Unlocked: 30-Day Calm Seeker!");
  }
  
  if (streak >= 365 && achievement365 && !achievement365.earned) {
    achievement365.earned = true;
    updated = true;
    alert("🎉 Achievement Unlocked: 365-Day Legend!");
  }
  
  if (updated) {
    setAchievements(newAchievements);
    localStorage.setItem('achievements', JSON.stringify(newAchievements));
  }
};

