import React, { useState } from "react";
import "./app.css";

// Import all components
import Chat from './components/Chat';
import './components/Chat.css';
import Journal from './components/Journal';
import Therapy from './components/Therapy';
import Community from './components/Community';
import Shop from './components/Shop';
import Profile from './components/Profile';
import Premium from './components/Premium';
import GamesModal from './components/GamesModal';
import AICoachModal from './components/AICoachModal';
import SleepCompanionModal from './components/SleepCompanionModal';
import MoodReportModal from './components/MoodReportModal';
import PremiumPlans from './components/PremiumPlans';
import BreathingExercise from './components/BreathingExercise';
// QuickActionsMenu is no longer needed in this design

function App({ user, onLogout, onUpdateUser }) {
    const [activeTab, setActiveTab] = useState("chat");
    
    // Modal Visibility State
    const [showGamesModal, setShowGamesModal] = useState(false);
    const [showBreathing, setShowBreathing] = useState(false);
    const [showAICoach, setShowAICoach] = useState(false);
    const [showSleepCompanion, setShowSleepCompanion] = useState(false);
    const [showMoodReport, setShowMoodReport] = useState(false);
    const [showPremiumPlans, setShowPremiumPlans] = useState(false);

    const handleUpdateUser = (updatedData) => {
        const newUserData = { ...user, ...updatedData };
        if (onUpdateUser) {
            onUpdateUser(newUserData);
        }
    };

    const handleSubscribe = async (plan) => {
        try {
            console.log(`Subscribing user ${user.id} to ${plan} plan...`);
            handleUpdateUser({ is_premium: 1 });
            alert(`üéâ Welcome to MoodMate Premium! Your ${plan} plan is now active.`);
            setShowPremiumPlans(false);
        } catch (error) {
            alert("Subscription failed. Please try again.");
        }
    };

    const openPremiumPlans = () => {
        setShowGamesModal(false);
        setShowAICoach(false);
        setShowSleepCompanion(false);
        setShowMoodReport(false);
        setShowPremiumPlans(true);
    };

    return (
        <div className={`app-container ${user.currentTheme || 'default'}-theme`}>
            <header className="app-header">
                <div className="header-left">
                    <h1>üåô MoodMate</h1>
                    <span className="status-indicator">Online - Ready to listen</span>
                </div>
                <div className="header-right">
                    <div className="user-stats">
                        <span className="streak-display">üî• {user.streak || 0}</span>
                        <span>ü™ô {user.coins || 0}</span>
                        {user.is_premium ? (
                            <span className="premium-indicator">üåü Premium</span>
                        ) : (
                            <span className="premium-indicator-locked">üíé Basic</span>
                        )}
                    </div>
                    <div className="user-info">
                        <span className="user-name">{user.username}</span>
                    </div>
                    <button onClick={onLogout} className="logout-btn">Logout</button>
                </div>
            </header>
            
            <nav className="app-nav">
                <button className={activeTab === "chat" ? "active" : ""} onClick={() => setActiveTab("chat")}>üí¨ Chat</button>
                <button className={activeTab === "journal" ? "active" : ""} onClick={() => setActiveTab("journal")}>üìì Journal</button>
                <button className={activeTab === "therapy" ? "active" : ""} onClick={() => setActiveTab("therapy")}>üë®‚Äç‚öïÔ∏è Therapy</button>
                <button className={activeTab === "community" ? "active" : ""} onClick={() => setActiveTab("community")}>üåç Community</button>
                <button className={activeTab === "shop" ? "active" : ""} onClick={() => setActiveTab("shop")}>üõí Shop</button>
                <button className={activeTab === "profile" ? "active" : ""} onClick={() => setActiveTab("profile")}>üë§ Profile</button>
                <button className={`premium-nav-btn ${activeTab === "premium" ? "active" : ""}`} onClick={() => setActiveTab("premium")}>
                    {user.is_premium ? "üåü Premium" : "üíé Go Premium"}
                </button>
            </nav>
            
            {/* The main content area is now much cleaner */}
            <main className="app-main">
                {activeTab === 'chat' && (
                    <div className="chat-page-layout">
                        <Chat user={user} />
                        
                        {/* ‚úÖ NEW: This is the elegant features bar at the bottom */}
                        <div className="chat-features-bar">
                            <button className="feature-btn primary" onClick={() => setShowAICoach(true)}>
                                <span className="feature-icon">ü§ñ</span>
                                <span className="feature-label">AI Coach</span>
                            </button>
                            <button className="feature-btn primary" onClick={() => setShowMoodReport(true)}>
                                <span className="feature-icon">üìä</span>
                                <span className="feature-label">Reports</span>
                            </button>
                            <button className="feature-btn" onClick={() => setActiveTab("community")}>
                                <span className="feature-icon">üåç</span>
                                <span className="feature-label">Community</span>
                            </button>
                            <button className="feature-btn" onClick={() => setActiveTab("therapy")}>
                                <span className="feature-icon">üë®‚Äç‚öïÔ∏è</span>
                                <span className="feature-label">Therapist</span>
                            </button>
                        </div>
                    </div>
                )}
                {activeTab === 'journal' && <Journal user={user} />}
                {activeTab === 'therapy' && <Therapy user={user} onUpdateUser={handleUpdateUser} />}
                {activeTab === 'community' && <Community user={user} />}
                {activeTab === 'shop' && <Shop user={user} onUpdateUser={handleUpdateUser} />}
                {activeTab === 'profile' && (
                    <Profile 
                        user={user} 
                        onUpdateUser={handleUpdateUser} 
                        onUpgradeClick={openPremiumPlans} 
                        onShowGames={() => setShowGamesModal(true)}
                        onShowBreathing={() => setShowBreathing(true)}
                        onShowSleep={() => setShowSleepCompanion(true)}
                    />
                )}
                {activeTab === 'premium' && <Premium user={user} onUpdateUser={handleUpdateUser} />}
            </main>

            {/* All top-level Modals are rendered here */}
            {showPremiumPlans && <PremiumPlans user={user} onSubscribe={handleSubscribe} onClose={() => setShowPremiumPlans(false)} />}
            {showGamesModal && <GamesModal user={user} onClose={() => setShowGamesModal(false)} onUpgradeClick={openPremiumPlans} />}
            {showAICoach && <AICoachModal user={user} onClose={() => setShowAICoach(false)} onUpgradeClick={openPremiumPlans} />}
            {showSleepCompanion && <SleepCompanionModal user={user} onClose={() => setShowSleepCompanion(false)} onUpgradeClick={openPremiumPlans} />}
            {showMoodReport && <MoodReportModal user={user} onClose={() => setShowMoodReport(false)} onUpgradeClick={openPremiumPlans} />}
            {showBreathing && <BreathingExercise onClose={() => setShowBreathing(false)} />}
        </div>
    );
}

export default App;
