import React, { useState } from 'react';
import './Community.css'; // The dedicated CSS file for this component

// Sample data that would normally come from your backend
const initialMoodWall = [
    {id: 1, userName: "Anonymous", text: "Finally finished a tough project at work. Feeling so relieved and proud! ‚ú®", likes: 28},
    {id: 2, userName: "HappyUser123", text: "Just had a great chat with a friend I hadn't seen in a while. It's the small things!", likes: 45},
    {id: 3, userName: "Anonymous", text: "Struggling with a bit of anxiety today, but trying to focus on my breathing exercises.", likes: 62}
];

const initialTribes = [
    {id: 1, name: "Positivity Tribe", description: "For those seeking positivity and joy.", members: 245, isMember: false},
    {id: 2, name: "Anxiety Allies", description: "Support for those dealing with anxiety.", members: 312, isMember: true},
    {id: 3, name: "Mindfulness Masters", description: "Practice mindfulness together.", members: 156, isMember: false}
];

const initialChallenges = [
    {id: 1, title: "7 Days of Gratitude", description: "Write one thing you're grateful for each day.", reward: "Gratitude Badge + 30 coins"},
    {id: 2, title: "Mindful Mornings", description: "Start each day with 5 mins of meditation.", reward: "Mindful Badge + 20 coins"}
];

function Community({ user }) {
    const [moodWall, setMoodWall] = useState(initialMoodWall);
    const [moodTribes, setMoodTribes] = useState(initialTribes);

    const handleAddPost = () => {
        const text = prompt("Share your feelings with the community (anonymously):");
        if (text && text.trim() !== "") {
            const newPost = {
                id: Date.now(),
                userName: "Anonymous",
                text: text,
                likes: 0
            };
            setMoodWall(prev => [newPost, ...prev]);
        }
    };

    const handleJoinTribe = (tribeId) => {
        setMoodTribes(prevTribes => 
            prevTribes.map(tribe => 
                tribe.id === tribeId ? { ...tribe, isMember: !tribe.isMember, members: tribe.isMember ? tribe.members - 1 : tribe.members + 1 } : tribe
            )
        );
    };

    return (
        <div className="community-hub tab-content">
            <div className="community-layout">
                <div className="mood-wall-section">
                    <h3>üåç Community Mood Wall</h3>
                    <button className="add-post-btn" onClick={handleAddPost}>+ Share Your Feelings</button>
                    <div className="post-list">
                        {moodWall.map(post => (
                            <div key={post.id} className="post-card">
                                <div className="post-header">
                                    <div className="post-avatar">{post.userName.charAt(0)}</div>
                                    <span className="post-username">{post.userName}</span>
                                </div>
                                <p className="post-content">{post.text}</p>
                                <div className="post-actions">
                                    <button className="like-btn">üíñ {post.likes}</button>
                                    <button className="comment-btn">üí¨ Comment</button>
                                </div>
                            </div>
                        ))}
                    </div>
                </div>

                <div className="community-sidebar">
                    <div className="tribes-section">
                        <h3>üéØ Mood Tribes</h3>
                        <div className="sidebar-list">
                            {moodTribes.map(tribe => (
                                <div key={tribe.id} className="tribe-card">
                                    <h4>{tribe.name}</h4>
                                    <p>{tribe.description}</p>
                                    <div className="tribe-footer">
                                        <span>üë• {tribe.members} members</span>
                                        <button 
                                            className={`join-btn ${tribe.isMember ? 'joined' : ''}`}
                                            onClick={() => handleJoinTribe(tribe.id)}
                                        >
                                            {tribe.isMember ? 'Joined' : 'Join'}
                                        </button>
                                    </div>
                                </div>
                            ))}
                        </div>
                    </div>

                    <div className="challenges-section">
                        <h3>üèÜ Community Challenges</h3>
                        <div className="sidebar-list">
                            {initialChallenges.map(challenge => (
                                <div key={challenge.id} className="challenge-card">
                                    <h4>{challenge.title}</h4>
                                    <p>{challenge.description}</p>
                                    <div className="challenge-reward">{challenge.reward}</div>
                                    <button className="join-challenge-btn">Start Challenge</button>
                                </div>
                            ))}
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default Community;
