import React, { useState, useEffect, useRef } from "react";
import "./app.css";

// API Configuration
const API_CONFIG = {
  BASE_URL: 'http://localhost:5000',
  ENDPOINTS: {
    LOGIN: '/api/login',
    REGISTER: '/api/register',
    CHAT: '/api/chat',
    DOCTORS: '/api/doctors',
    BOOK: '/api/book',
    BOOKINGS: '/api/bookings',
    JOIN_SESSION: '/api/join-session'
  }
};

// API Service
class ApiService {
  constructor() {
    this.baseURL = API_CONFIG.BASE_URL;
  }

  async request(endpoint, options = {}) {
    const url = `${this.baseURL}${endpoint}`;
    const token = localStorage.getItem('accessToken');
    
    const defaultOptions = {
      headers: {
        'Content-Type': 'application/json',
        ...(token && { Authorization: `Bearer ${token}` }),
        ...options.headers,
      },
    };

    try {
      const response = await fetch(url, { ...defaultOptions, ...options });
      return await this.handleResponse(response);
    } catch (error) {
      console.error('API request failed:', error);
      throw new Error('Network error. Please check your connection.');
    }
  }

  async handleResponse(response) {
    if (response.ok) {
      const contentType = response.headers.get('content-type');
      if (contentType && contentType.includes('application/json')) {
        return await response.json();
      }
      return await response.text();
    } else {
      const error = await response.text();
      throw new Error(error || `Request failed with status ${response.status}`);
    }
  }

  // Auth endpoints
  async login(credentials) {
    return this.request(API_CONFIG.ENDPOINTS.LOGIN, {
      method: 'POST',
      body: JSON.stringify(credentials)
    });
  }

  async register(userData) {
    return this.request(API_CONFIG.ENDPOINTS.REGISTER, {
      method: 'POST',
      body: JSON.stringify(userData)
    });
  }

  // Chat endpoint
  async sendChatMessage(message) {
    return this.request(API_CONFIG.ENDPOINTS.CHAT, {
      method: 'POST',
      body: JSON.stringify({ message })
    });
  }

  // Doctor endpoints
  async getDoctors() {
    return this.request(API_CONFIG.ENDPOINTS.DOCTORS);
  }

  async bookAppointment(bookingData) {
    return this.request(API_CONFIG.ENDPOINTS.BOOK, {
      method: 'POST',
      body: JSON.stringify(bookingData)
    });
  }

  async getUserBookings() {
    return this.request(API_CONFIG.ENDPOINTS.BOOKINGS);
  }

  async joinSession(sessionId) {
    return this.request(API_CONFIG.ENDPOINTS.JOIN_SESSION, {
      method: 'POST',
      body: JSON.stringify({ session_id: sessionId })
    });
  }
}

const apiService = new ApiService();

function App() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [isTyping, setIsTyping] = useState(false);
  const [user, setUser] = useState(null);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [showLogin, setShowLogin] = useState(true);
  const [showRegister, setShowRegister] = useState(false);
  const [activeTab, setActiveTab] = useState("chat");
  const [typingIndicator, setTypingIndicator] = useState("");
  const [doctors, setDoctors] = useState([]);
  const [userBookings, setUserBookings] = useState([]);
  const [selectedDoctor, setSelectedDoctor] = useState(null);
  const [bookingStep, setBookingStep] = useState("select-doctor");
  const [selectedSlot, setSelectedSlot] = useState(null);
  const [selectedMode, setSelectedMode] = useState("chat");
  const [loginError, setLoginError] = useState("");
  const [registerError, setRegisterError] = useState("");

  const chatEndRef = useRef(null);

  // Load saved session
  useEffect(() => {
    const token = localStorage.getItem('accessToken');
    const userData = localStorage.getItem('user');
    
    if (token && userData) {
      const userObj = JSON.parse(userData);
      setUser(userObj);
      setIsLoggedIn(true);
      setShowLogin(false);
      loadInitialData();
    }
  }, []);

  // Auto scroll
  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  const loadInitialData = async () => {
    try {
      setMessages([{
        text: "Hello! I'm MoodMate, your mental health companion. How are you feeling today?",
        sender: "bot"
      }]);
      
      // Load doctors and user bookings
      const [doctorsData, bookingsData] = await Promise.allSettled([
        apiService.getDoctors(),
        apiService.getUserBookings()
      ]);
      
      if (doctorsData.status === 'fulfilled') {
        setDoctors(doctorsData.value.doctors || doctorsData.value);
      }
      
      if (bookingsData.status === 'fulfilled') {
        setUserBookings(bookingsData.value.bookings || bookingsData.value);
      }
    } catch (error) {
      console.error("Error loading initial data:", error);
    }
  };

  // Simulate typing animation
  const simulateTyping = async () => {
    const typingPhrases = ["Thinking", "Thinking.", "Thinking..", "Thinking..."];
    for (let i = 0; i < 6; i++) {
      setTypingIndicator(typingPhrases[i % 4]);
      await new Promise(resolve => setTimeout(resolve, 300));
    }
    setTypingIndicator("");
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    setLoginError("");
    const formData = new FormData(e.target);
    const credentials = {
      email: formData.get('email'),
      password: formData.get('password')
    };

    try {
      const data = await apiService.login(credentials);

      if (data.status === "success") {
        localStorage.setItem('accessToken', data.token);
        
        const userData = {
          id: data.user.id,
          email: data.user.email,
          name: data.user.name,
          coins: data.user.coins,
          streak: data.user.streak,
          mentalFitness: data.user.mentalFitness || 75
        };
        
        localStorage.setItem('user', JSON.stringify(userData));
        setUser(userData);
        setIsLoggedIn(true);
        setShowLogin(false);
        loadInitialData();
      } else {
        setLoginError(data.error || "Login failed");
      }
    } catch (error) {
      console.error("Login error:", error);
      setLoginError(error.message || "Login failed. Please try again.");
    }
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    setRegisterError("");
    const formData = new FormData(e.target);
    const userData = {
      name: formData.get('name'),
      email: formData.get('email'),
      password: formData.get('password'),
      confirmPassword: formData.get('confirmPassword')
    };

    try {
      const data = await apiService.register(userData);

      if (data.status === "success") {
        localStorage.setItem('accessToken', data.token);
        
        const userObj = {
          id: data.user.id,
          email: data.user.email,
          name: data.user.name,
          coins: data.user.coins,
          streak: data.user.streak,
          mentalFitness: data.user.mentalFitness || 75
        };
        
        localStorage.setItem('user', JSON.stringify(userObj));
        setUser(userObj);
        setIsLoggedIn(true);
        setShowRegister(false);
        setShowLogin(false);
        loadInitialData();
      } else {
        setRegisterError(data.error || "Registration failed");
      }
    } catch (error) {
      console.error("Registration error:", error);
      setRegisterError(error.message || "Registration failed. Please try again.");
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('accessToken');
    localStorage.removeItem('user');
    setUser(null);
    setIsLoggedIn(false);
    setShowLogin(true);
    setMessages([]);
  };

  const sendMessage = async () => {
    if (!input.trim() || !isLoggedIn) return;

    const userMsg = { text: input, sender: "user" };
    setMessages(prev => [...prev, userMsg]);
    setInput("");
    setIsTyping(true);
    
    // Simulate typing animation
    simulateTyping();

    try {
      const data = await apiService.sendChatMessage(input);
      
      const botMsg = { 
        text: data.reply, 
        sender: "bot"
      };
      setMessages(prev => [...prev, botMsg]);
    } catch (error) {
      console.log("Chat error:", error);
      // Fallback response
      const botMsg = { 
        text: "I'm here for you. Would you like to talk more about how you're feeling?", 
        sender: "bot"
      };
      setMessages(prev => [...prev, botMsg]);
    }

    setIsTyping(false);
    setTypingIndicator("");
  };

  const startNewChat = () => {
    setMessages([{
      text: "Hello! I'm MoodMate, your mental health companion. How are you feeling today?",
      sender: "bot"
    }]);
  };

  const bookAppointment = async () => {
    if (!selectedDoctor || !selectedSlot || !selectedMode) {
      alert("Please select a doctor, time slot, and mode");
      return;
    }

    try {
      const bookingData = {
        doctor_id: selectedDoctor.id,
        slot_time: selectedSlot,
        mode: selectedMode
      };

      const data = await apiService.bookAppointment(bookingData);

      if (data.status === "success") {
        alert("Appointment booked successfully!");
        setBookingStep("select-doctor");
        setSelectedDoctor(null);
        setSelectedSlot(null);
        setSelectedMode("chat");
        
        // Refresh bookings
        const bookingsData = await apiService.getUserBookings();
        setUserBookings(bookingsData.bookings || bookingsData);
      } else {
        alert(data.error || "Failed to book appointment");
      }
    } catch (error) {
      console.error("Booking error:", error);
      alert("Failed to book appointment. Please try again.");
    }
  };

  const joinSession = async (booking) => {
    try {
      const data = await apiService.joinSession(booking.id);
      
      if (data.status === "success") {
        if (booking.mode === "chat") {
          // Open chat interface
          setActiveTab("chat");
        } else if (booking.mode === "audio" || booking.mode === "video") {
          // Open meeting link in new tab
          window.open(data.meeting_link, "_blank");
        }
      } else {
        alert(data.error || "Failed to join session");
      }
    } catch (error) {
      console.error("Join session error:", error);
      alert("Failed to join session. Please try again.");
    }
  };

  const formatDate = (dateString) => {
    const options = { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' };
    return new Date(dateString).toLocaleDateString(undefined, options);
  };

  const formatTime = (timeString) => {
    return new Date(timeString).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
  };

  const BookingWizard = () => {
    if (bookingStep === "select-doctor") {
      return (
        <div className="booking-step">
          <h3>Select a Doctor</h3>
          <div className="doctors-grid">
            {doctors.map(doctor => (
              <div key={doctor.id} className="doctor-card" onClick={() => {
                setSelectedDoctor(doctor);
                setBookingStep("select-slot");
              }}>
                <div className="doctor-avatar">{doctor.name.charAt(0)}</div>
                <h4>{doctor.name}</h4>
                <p>{doctor.specialization}</p>
                <p>Fee: {doctor.fee} coins</p>
                <div className="modes">
                  {doctor.mode.includes("chat") && <span>💬</span>}
                  {doctor.mode.includes("audio") && <span>🎧</span>}
                  {doctor.mode.includes("video") && <span>📹</span>}
                </div>
              </div>
            ))}
          </div>
          <button className="back-button" onClick={() => setActiveTab("therapy")}>Back to Therapy</button>
        </div>
      );
    } else if (bookingStep === "select-slot") {
      return (
        <div className="booking-step">
          <h3>Select Time Slot with Dr. {selectedDoctor.name}</h3>
          <div className="slots-grid">
            {selectedDoctor.availability_slots && selectedDoctor.availability_slots.map(slot => (
              <div 
                key={slot} 
                className={`slot-card ${selectedSlot === slot ? "selected" : ""}`}
                onClick={() => setSelectedSlot(slot)}
              >
                {formatTime(slot)}
              </div>
            ))}
          </div>
          <div className="booking-controls">
            <button className="back-button" onClick={() => setBookingStep("select-doctor")}>Back to Doctors</button>
            {selectedSlot && (
              <button className="next-button" onClick={() => setBookingStep("select-mode")}>Next</button>
            )}
          </div>
        </div>
      );
    } else if (bookingStep === "select-mode") {
      return (
        <div className="booking-step">
          <h3>Select Session Mode with Dr. {selectedDoctor.name}</h3>
          <p>Selected time: {formatTime(selectedSlot)}</p>
          <div className="modes-grid">
            {selectedDoctor.mode.includes("chat") && (
              <div 
                className={`mode-card ${selectedMode === "chat" ? "selected" : ""}`}
                onClick={() => setSelectedMode("chat")}
              >
                <h4>💬 Chat Session</h4>
                <p>Text-based messaging</p>
              </div>
            )}
            {selectedDoctor.mode.includes("audio") && (
              <div 
                className={`mode-card ${selectedMode === "audio" ? "selected" : ""}`}
                onClick={() => setSelectedMode("audio")}
              >
                <h4>🎧 Audio Call</h4>
                <p>Voice conversation</p>
              </div>
            )}
            {selectedDoctor.mode.includes("video") && (
              <div 
                className={`mode-card ${selectedMode === "video" ? "selected" : ""}`}
                onClick={() => setSelectedMode("video")}
              >
                <h4>📹 Video Call</h4>
                <p>Face-to-face conversation</p>
              </div>
            )}
          </div>
          <div className="booking-controls">
            <button className="back-button" onClick={() => setBookingStep("select-slot")}>Back to Time Slots</button>
            {selectedMode && (
              <button className="next-button" onClick={() => setBookingStep("confirm-booking")}>Next</button>
            )}
          </div>
        </div>
      );
    } else if (bookingStep === "confirm-booking") {
      return (
        <div className="booking-step">
          <h3>Confirm Your Booking</h3>
          <div className="booking-summary">
            <h4>Appointment Details</h4>
            <p><strong>Doctor:</strong> Dr. {selectedDoctor.name}</p>
            <p><strong>Specialization:</strong> {selectedDoctor.specialization}</p>
            <p><strong>Date & Time:</strong> {formatTime(selectedSlot)}</p>
            <p><strong>Mode:</strong> 
              {selectedMode === "chat" && " 💬 Chat"}
              {selectedMode === "audio" && " 🎧 Audio Call"}
              {selectedMode === "video" && " 📹 Video Call"}
            </p>
            <p><strong>Fee:</strong> {selectedDoctor.fee} coins</p>
          </div>
          <div className="booking-controls">
            <button className="back-button" onClick={() => setBookingStep("select-mode")}>Back to Mode Selection</button>
            <button className="confirm-button" onClick={bookAppointment}>Confirm Booking</button>
          </div>
        </div>
      );
    }
  };

  const TherapyTab = () => {
    if (bookingStep !== "select-doctor") {
      return <BookingWizard />;
    }

    return (
      <div className="therapy-tab">
        <div className="therapy-header">
          <h2>Professional Therapy Sessions</h2>
          <button className="book-session-btn" onClick={() => setBookingStep("select-doctor")}>
            Book a Session
          </button>
        </div>

        <div className="my-bookings-section">
          <h3>My Upcoming Sessions</h3>
          {userBookings.length === 0 ? (
            <p>You don't have any upcoming sessions.</p>
          ) : (
            <div className="bookings-list">
              {userBookings.map(booking => (
                <div key={booking.id} className="booking-item">
                  <div className="booking-info">
                    <h4>Session with Dr. {booking.doctor_name}</h4>
                    <p>{formatDate(booking.slot_time)} at {formatTime(booking.slot_time)}</p>
                    <p>Mode: 
                      {booking.mode === "chat" && " 💬 Chat"}
                      {booking.mode === "audio" && " 🎧 Audio"}
                      {booking.mode === "video" && " 📹 Video"}
                    </p>
                    <p>Status: <span className={`status ${booking.status}`}>{booking.status}</span></p>
                  </div>
                  <div className="booking-actions">
                    {booking.status === "confirmed" && (
                      <button 
                        className="join-session-btn"
                        onClick={() => joinSession(booking)}
                      >
                        Join Session
                      </button>
                    )}
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>

        <div className="available-doctors-section">
          <h3>Available Doctors</h3>
          <div className="doctors-grid">
            {doctors.map(doctor => (
              <div key={doctor.id} className="doctor-card">
                <div className="doctor-avatar">{doctor.name.charAt(0)}</div>
                <h4>{doctor.name}</h4>
                <p>{doctor.specialization}</p>
                <p>Fee: {doctor.fee} coins</p>
                <div className="modes">
                  {doctor.mode.includes("chat") && <span>💬</span>}
                  {doctor.mode.includes("audio") && <span>🎧</span>}
                  {doctor.mode.includes("video") && <span>📹</span>}
                </div>
                <button 
                  className="book-btn"
                  onClick={() => {
                    setSelectedDoctor(doctor);
                    setBookingStep("select-slot");
                  }}
                >
                  Book Now
                </button>
              </div>
            ))}
          </div>
        </div>
      </div>
    );
  };

  if (!isLoggedIn) {
    return (
      <div className="app-container auth-container">
        <div className="auth-box">
          <h1>🌙 MoodMate</h1>
          <p>Your mental health companion</p>
          
          {showLogin && (
            <form onSubmit={handleLogin} className="auth-form">
              <h2>Login</h2>
              {loginError && <div className="error-message">{loginError}</div>}
              <input type="email" name="email" placeholder="Email" required />
              <input type="password" name="password" placeholder="Password" required />
              <button type="submit">Login</button>
              
              <div className="auth-links">
                <span className="auth-link" onClick={() => setShowRegister(true)}>
                  Create Account
                </span>
              </div>
            </form>
          )}

          {showRegister && (
            <form onSubmit={handleRegister} className="auth-form">
              <h2>Register</h2>
              {registerError && <div className="error-message">{registerError}</div>}
              <input type="text" name="name" placeholder="Full Name" required />
              <input type="email" name="email" placeholder="Email" required />
              <input type="password" name="password" placeholder="Password (min 8 chars)" required />
              <input type="password" name="confirmPassword" placeholder="Confirm Password" required />
              <button type="submit">Register</button>
              <p>
                Already have an account?{" "}
                <span className="auth-link" onClick={() => setShowRegister(false)}>
                  Login
                </span>
              </p>
            </form>
          )}
        </div>
      </div>
    );
  }

  return (
    <div className="app-container">
      <header className="app-header">
        <div className="header-left">
          <h1>🌙 MoodMate</h1>
          <div className="user-stats">
            <span className="streak-display">🔥 {user?.streak || 0} day streak</span>
            <span>🪙 {user?.coins || 0} coins</span>
          </div>
        </div>
        <div className="header-right">
          <div className="user-info">
            <span className="user-name">{user?.name}</span>
            <span className="user-status">Online</span>
          </div>
          <button onClick={handleLogout} className="logout-btn">Logout</button>
        </div>
      </header>

      <nav className="app-nav">
        <button 
          className={activeTab === "chat" ? "active" : ""} 
          onClick={() => setActiveTab("chat")}
        >
          💬 Chat
        </button>
        <button 
          className={activeTab === "therapy" ? "active" : ""} 
          onClick={() => {
            setActiveTab("therapy");
            setBookingStep("select-doctor");
          }}
        >
          👨‍⚕️ Therapy
        </button>
      </nav>

      <main className="app-main">
        {activeTab === "chat" && (
          <div className="chat-tab">
            <div className="chat-header">
              <h2>Chat with MoodMate</h2>
              <button onClick={startNewChat} className="new-chat-btn">
                + New Chat
              </button>
            </div>
            <div className="chat-box">
              {messages.map((msg, i) => (
                <div key={`msg-${i}-${Date.now()}`} className={`chat-message ${msg.sender}`}>
                  {msg.sender === "bot" && (
                    <div className="avatar bot-avatar">🤖</div>
                  )}
                  {msg.sender === "user" && (
                    <div className="avatar user-avatar">👤</div>
                  )}
                  <div className="bubble">
                    {msg.text}
                  </div>
                </div>
              ))}

              {isTyping && (
                <div className="chat-message bot">
                  <div className="avatar bot-avatar">🤖</div>
                  <div className="bubble typing">
                    {typingIndicator || "..."}
                  </div>
                </div>
              )}

              <div ref={chatEndRef}></div>
            </div>

            <div className="input-area">
              <input
                type="text"
                placeholder="How are you feeling today?"
                value={input}
                onChange={(e) => setInput(e.target.value)}
                onKeyDown={(e) => e.key === "Enter" && sendMessage()}
              />
              <button onClick={sendMessage} className="send-btn">Send</button>
            </div>
          </div>
        )}

        {activeTab === "therapy" && (
          <TherapyTab />
        )}
      </main>
    </div>
  );
}

export default App;
