import React, { useState, useEffect } from "react";
import "./app.css";

// Import all components
import Auth from './Auth';
import Chat from './components/Chat';
import Journal from './components/Journal';
import Therapy from './components/Therapy';
import Community from './components/Community';
import Shop from './components/Shop';
import Profile from './components/Profile';
import Premium from './components/PremiumPlans';
import GamesModal from './components/GamesModal';
import AICoachModal from './components/AICoachModal';
import SleepCompanionModal from './components/SleepCompanionModal';
import MoodReportModal from './components/MoodReportModal';
import QuickActionsMenu from "./components/QuickActions";
 import PremiumPlans from './components/PremiumPlans'; // Import PremiumPlans here


const API_BASE_URL = 'http://127.0.0.1:5000';

function App() {
    const [user, setUser] = useState(null);
    const [authError, setAuthError] = useState("");
    const [activeTab, setActiveTab] = useState("chat");

    const handleSubscribe = async (plan) => {
        try {
            // This is a placeholder for your real API call
            console.log(`Subscribing to ${plan} plan...`);
            handleUpdateUser({ is_premium: 1 });
            alert(`🎉 Welcome to MoodMate Premium! Your ${plan} plan is now active.`);
            setShowPremiumPlans(false);
        } catch (error) {
            alert("Subscription failed. Please try again.");
        }
    };

    // This function will be passed to any component that needs to open the plans modal
    const openPremiumPlans = () => {
        setShowGamesModal(false);
        setShowAICoach(false);
        setShowSleepCompanion(false);
        setShowMoodReport(false);
        setShowPremiumPlans(true);
    };

    return (
        <div className={`app-container ${userData.currentTheme || 'default'}-theme`}>
            <header className="app-header">
                <div className="header-left">
                    <h1>🌙 MoodMate</h1>
                </div>
                <div className="header-right">
                    <div className="user-stats">
                        <span className="streak-display">🔥 {userData.streak || 0}</span>
                        <span>🪙 {userData.coins || 0}</span>
                        {userData.is_premium ? <span className="premium-indicator">🌟</span> : <span className="premium-indicator-locked">💎</span>}
                    </div>
                    <div className="user-info">
                        <span className="user-name">{userData.username}</span>
                    </div>
                    <button onClick={onLogout} className="logout-btn">Logout</button>
                </div>
            </header>
            
            <nav className="app-nav">
                <button className={activeTab === "chat" ? "active" : ""} onClick={() => setActiveTab("chat")}>💬 Chat</button>
                <button className={activeTab === "journal" ? "active" : ""} onClick={() => setActiveTab("journal")}>📓 Journal</button>
                <button className={activeTab === "therapy" ? "active" : ""} onClick={() => setActiveTab("therapy")}>👨‍⚕️ Therapy</button>
                <button className={activeTab === "community" ? "active" : ""} onClick={() => setActiveTab("community")}>🌍 Community</button>
                <button className={activeTab === "shop" ? "active" : ""} onClick={() => setActiveTab("shop")}>🛒 Shop</button>
                <button className={activeTab === "profile" ? "active" : ""} onClick={() => setActiveTab("profile")}>👤 Profile</button>
                <button className={`premium-nav-btn ${activeTab === "premium" ? "active" : ""}`} onClick={() => setActiveTab("premium")}>
                    {userData.is_premium ? "🌟 Premium" : "💎 Go Premium"}
                </button>
            </nav>

            {activeTab === 'chat' && (
                <QuickActionsMenu 
                    onShowGames={() => setShowGamesModal(true)}
                    onShowAICoach={() => setShowAICoach(true)}
                    onShowSleep={() => setShowSleepCompanion(true)}
                    onShowReport={() => setShowMoodReport(true)}
                />
            )}

            <main className="app-main">
                {activeTab === 'chat' && <Chat user={userData} />}
                {activeTab === 'journal' && <Journal user={userData} />}
                {activeTab === 'therapy' && <Therapy user={userData} onUpdateUser={handleUpdateUser} />}
                {activeTab === 'community' && <Community user={userData} />}
                {activeTab === 'shop' && <Shop user={userData} onUpdateUser={handleUpdateUser} />}
                {activeTab === 'profile' && <Profile user={userData} onUpdateUser={handleUpdateUser} onUpgradeClick={openPremiumPlans} />}
                {activeTab === 'premium' && <Premium user={userData} onUpgradeClick={openPremiumPlans} />}
            </main>

            {/* All top-level Modals are rendered here so they can be called from anywhere */}
            {showPremiumPlans && <PremiumPlans user={userData} onSubscribe={handleSubscribe} onClose={() => setShowPremiumPlans(false)} />}
            {showGamesModal && <GamesModal user={userData} onClose={() => setShowGamesModal(false)} onUpgradeClick={openPremiumPlans} />}
            {showAICoach && <AICoachModal user={userData} onClose={() => setShowAICoach(false)} onUpgradeClick={openPremiumPlans} />}
            {showSleepCompanion && <SleepCompanionModal user={userData} onClose={() => setShowSleepCompanion(false)} onUpgradeClick={openPremiumPlans} />}
            {showMoodReport && <MoodReportModal user={userData} onClose={() => setShowMoodReport(false)} onUpgradeClick={openPremiumPlans} />}
        </div>
    );
    
    // Modal Visibility State
    
    const [showGamesModal, setShowGamesModal] = useState(false);
    const [showAICoach, setShowAICoach] = useState(false);
    const [showSleepCompanion, setShowSleepCompanion] = useState(false);
    const [showMoodReport, setShowMoodReport] = useState(false);
    const [showPremiumPlans, setShowPremiumPlans] = useState(false);

    const handleLogin = (userData) => {
        setUser(userData);
        localStorage.setItem("moodmateUser", JSON.stringify(userData));
        setAuthError("");
    };

    const handleLogout = async () => {
        try {
            await fetch(`${API_BASE_URL}/api/logout`, { method: 'POST', credentials: 'include' });
        } catch (error) {
            console.error("Logout failed:", error);
        } finally {
            setUser(null);
            localStorage.removeItem("moodmateUser");
        }
    };
    
    const handleUpdateUser = (updatedUserData) => {
        setUser(prevUser => {
            const newUser = { ...prevUser, ...updatedUserData };
            localStorage.setItem("moodmateUser", JSON.stringify(newUser));
            return newUser;
        });
    };

    useEffect(() => {
        const loggedInUser = localStorage.getItem("moodmateUser");
        if (loggedInUser) {
            setUser(JSON.parse(loggedInUser));
        }
    }, []);
    
    if (!user) {
        return <Auth onLogin={handleLogin} authError={authError} setAuthError={setAuthError} />;
    }

    return (
        <div className={`app-container ${user.currentTheme || 'default'}-theme`}>
            <header className="app-header">
                <div className="header-left">
                    <h1>🌙 MoodMate</h1>
                </div>
                <div className="header-right">
                    <div className="user-stats">
                        <span className="streak-display">🔥 {user.streak || 0}</span>
                        <span>🪙 {user.coins || 0}</span>
                        {user.is_premium ? <span className="premium-indicator">🌟</span> : <span className="premium-indicator-locked">💎</span>}
                    </div>
                    <div className="user-info">
                        <span className="user-name">{user.username}</span>
                    </div>
                    <button onClick={handleLogout} className="logout-btn">Logout</button>
                </div>
            </header>
            
            <nav className="app-nav">
                <button className={activeTab === "chat" ? "active" : ""} onClick={() => setActiveTab("chat")}>💬 Chat</button>
                <button className={activeTab === "journal" ? "active" : ""} onClick={() => setActiveTab("journal")}>📓 Journal</button>
                <button className={activeTab === "therapy" ? "active" : ""} onClick={() => setActiveTab("therapy")}>👨‍⚕️ Therapy</button>
                <button className={activeTab === "community" ? "active" : ""} onClick={() => setActiveTab("community")}>🌍 Community</button>
                <button className={activeTab === "shop" ? "active" : ""} onClick={() => setActiveTab("shop")}>🛒 Shop</button>
                <button className={activeTab === "profile" ? "active" : ""} onClick={() => setActiveTab("profile")}>👤 Profile</button>
                <button className={`premium-nav-btn ${activeTab === "premium" ? "active" : ""}`} onClick={() => setActiveTab("premium")}>
                    {user.is_premium ? "🌟 Premium" : "💎 Go Premium"}
                </button>
            </nav>

            {activeTab === 'chat' && (
                <QuickActionsMenu 
                    onShowGames={() => setShowGamesModal(true)}
                    onShowAICoach={() => setShowAICoach(true)}
                    onShowSleep={() => setShowSleepCompanion(true)}
                    onShowReport={() => setShowMoodReport(true)}
                />
            )}

            <main className="app-main">
                {activeTab === 'chat' && <Chat user={user} />}
                {activeTab === 'journal' && <Journal user={user} />}
                {activeTab === 'therapy' && <Therapy user={user} onUpdateUser={handleUpdateUser} />}
                {activeTab === 'community' && <Community user={user} />}
                {activeTab === 'shop' && <Shop user={user} onUpdateUser={handleUpdateUser} />}
                {activeTab === 'profile' && <Profile user={user} />}
                {activeTab === 'premium' && <Premium user={user} onUpdateUser={handleUpdateUser} />}
            </main>

            {/* All Modals are rendered here */}
            {showGamesModal && <GamesModal user={user} onClose={() => setShowGamesModal(false)} onUpgradeClick={() => {setShowGamesModal(false); setShowPremiumPlans(true);}} />}
            {showAICoach && <AICoachModal user={user} onClose={() => setShowAICoach(false)} onUpgradeClick={() => {setShowAICoach(false); setShowPremiumPlans(true);}} />}
            {showSleepCompanion && <SleepCompanionModal user={user} onClose={() => setShowSleepCompanion(false)} onUpgradeClick={() => {setShowSleepCompanion(false); setShowPremiumPlans(true);}} />}
            {showMoodReport && <MoodReportModal user={user} onClose={() => setShowMoodReport(false)} onUpgradeClick={() => {setShowMoodReport(false); setShowPremiumPlans(true);}} />}
        </div>
    );
}

export default App;
