import React, { useState, useEffect, useRef } from "react";
import "./app.css";

// Base URL for API calls
const API_BASE_URL = 'http://127.0.0.1:5000';

function App() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [isTyping, setIsTyping] = useState(false);
  const [user, setUser] = useState({
    id: 1,
    name: "Demo User",
    coins: 100,
    streak: 5,
    mentalFitness: 85,
    purchasedThemes: [],
    appliedTheme: "default",
    ownedPacks: [],
    badges: ["First Steps"],
    loginStreak: 0,
    lastLogin: null,
    meditationMinutes: 45
  });
  const [activeTab, setActiveTab] = useState("chat");
  const [journalEntries, setJournalEntries] = useState([]);
  const [therapists, setTherapists] = useState([]);
  const [resources, setResources] = useState([]);
  const [moodWall, setMoodWall] = useState([]);
  const [moodTribes, setMoodTribes] = useState([]);
  const [shopItems, setShopItems] = useState([]);
  const [userTribes, setUserTribes] = useState([]);
  const [currentTheme, setCurrentTheme] = useState("default");
  const [typingIndicator, setTypingIndicator] = useState("");
  const [showEmergencyModal, setShowEmergencyModal] = useState(false);
  const [achievements, setAchievements] = useState([]);
  const [activeTribe, setActiveTribe] = useState(null);
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [showNewChatModal, setShowNewChatModal] = useState(false);
  const [lastStreakUpdate, setLastStreakUpdate] = useState("");
  const [activeShopCategory, setActiveShopCategory] = useState("All");
  const [showLoginBonus, setShowLoginBonus] = useState(false);
  const [bonusAmount, setBonusAmount] = useState(0);
  const [loginStreak, setLoginStreak] = useState(0);
  const [breathingExercises] = useState([
    { id: 1, name: "4-7-8 Breathing", duration: 5, description: "Inhale 4s, hold 7s, exhale 8s" },
    { id: 2, name: "Box Breathing", duration: 4, description: "Equal parts inhale, hold, exhale, hold" },
    { id: 3, name: "Deep Belly Breathing", duration: 7, description: "Focus on diaphragm breathing" },
    { id: 4, name: "Alternate Nostril", duration: 5, description: "Balance both hemispheres of brain" },
    { id: 5, name: "Lion's Breath", duration: 3, description: "Energizing breathing technique" },
    { id: 6, name: "Humming Bee Breath", duration: 5, description: "Calming vibration technique" },
    { id: 7, name: "Equal Breathing", duration: 5, description: "Equal inhale and exhale" },
    { id: 8, name: "Resonant Breathing", duration: 5, description: "5.5 second inhale/exhale" },
    { id: 9, name: "Breath Counting", duration: 5, description: "Focus on counting breaths" },
    { id: 10, name: "Sitali Breath", duration: 5, description: "Cooling breathing technique" },
    { id: 11, name: "Kapalabhati", duration: 5, description: "Skull shining breath" },
    { id: 12, name: "Bhramari", duration: 5, description: "Bee breath for anxiety" },
    { id: 13, name: "Ujjayi", duration: 5, description: "Ocean sounding breath" },
    { id: 14, name: "Dirga", duration: 5, description: "Three-part breath" },
    { id: 15, name: "Sama Vritti", duration: 5, description: "Equal ratio breathing" },
    { id: 16, name: "Nadi Shodhana", duration: 5, description: "Channel cleaning breath" },
    { id: 17, name: "Bhastrika", duration: 5, description: "Bellows breath" },
    { id: 18, name: "Sheetkari", duration: 5, description: "Hissing breath" },
    { id: 19, name: "Chandra Bhedana", duration: 5, description: "Left nostril breathing" },
    { id: 20, name: "Surya Bhedana", duration: 5, description: "Right nostril breathing" }
  ]);
  const [activeBreathingExercise, setActiveBreathingExercise] = useState(null);
  const [breathingProgress, setBreathingProgress] = useState(0);
  const [breathingPhase, setBreathingPhase] = useState("inhale");
  const [moodGames] = useState([
    { id: 1, name: "Bubble Pop", icon: "ü´ß", description: "Pop stress bubbles" },
    { id: 2, name: "Gratitude Wheel", icon: "üé°", description: "Spin for gratitude prompts" },
    { id: 3, name: "Mindful Coloring", icon: "üé®", description: "Relax with coloring" },
    { id: 4, name: "Breathing Bubble", icon: "üåÄ", description: "Visual breathing guide" },
    { id: 5, name: "Stress Balloon", icon: "üéà", description: "Release stress balloons" }
  ]);

  const [aiProvider, setAiProvider] = useState("gemini");
  const [audioEnabled, setAudioEnabled] = useState(true);
  const [musicEnabled, setMusicEnabled] = useState(true);
  const [tribeChatMessages, setTribeChatMessages] = useState({});
  const [tribeInput, setTribeInput] = useState("");
  
  const chatEndRef = useRef(null);
  const breathingIntervalRef = useRef(null);

  // Sample data
  const sampleTherapists = [
    {id: 1, name: "Dr. Priya Sharma", specialization: "Clinical Psychology", price: 79, rating: 4.7, experience: "8 years", avatar: "üë©‚Äç‚öï"},
    {id: 2, name: "Rajiv Mehta", specialization: "Counseling Psychology", price: 599, rating: 4.5, experience: "5 years", avatar: "üë®‚Äç‚öï"},
    {id: 3, name: "Dr. Anjali Singh", specialization: "Psychiatry", price: 999, rating: 4.8, experience: "12 years", avatar: "üë©‚Äç‚öï"},
    {id: 4, name: "Neha Gupta", specialization: "Art Therapy", price: 499, rating: 4.6, experience: "4 years", avatar: "üé®"}
  ];

  const sampleResources = [
    {type: "crisis", name: "National Suicide Prevention Lifeline", number: "1-800-273-8255", available: "24/7"},
    {type: "crisis", name: "Crisis Text Line", number: "Text HOME to 741741", available: "24/7"},
    {type: "support", name: "Mental Health America", website: "mhanational.org", description: "Resources and screening tools"}
  ];

  const sampleMoodTribes = [
    {id: 1, name: "Positivity Tribe", description: "For those seeking positivity and joy", member_count: 245, isMember: false},
    {id: 2, name: "Calm Collective", description: "Find your inner peace together", member_count: 189, isMember: false},
    {id: 3, name: "Anxiety Allies", description: "Support for those dealing with anxiety", member_count: 312, isMember: false},
    {id: 4, name: "Mindfulness Masters", description: "Practice mindfulness together", member_count: 156, isMember: false},
    {id: 5, name: "Breakup Recovery", description: "Healing after relationship endings", member_count: 128, isMember: false},
    {id: 6, name: "Career Stress", description: "Managing work-related stress", member_count: 201, isMember: false},
    {id: 7, name: "Student Support", description: "Academic pressure and study stress", member_count: 178, isMember: false}
  ];

  const sampleShopItems = [
    {id: 1, name: "Sunset Theme", description: "Warm orange and purple theme", price: 50, category: "theme"},
    {id: 2, name: "Ocean Theme", description: "Calming blue theme", price: 50, category: "theme"},
    {id: 3, name: "Forest Theme", description: "Green nature-inspired theme", price: 50, category: "theme"},
    {id: 4, name: "Neon Theme", description: "Vibrant neon colors", price: 75, category: "theme"},
    {id: 5, name: "Glass Theme", description: "Modern glassmorphism design", price: 100, category: "theme"},
    {id: 6, name: "Exclusive Avatar", description: "Special avatar frame", price: 100, category: "cosmetic"},
    {id: 7, name: "Meditation Pack", description: "Exclusive meditation guides", price: 75, category: "content"},
    {id: 8, name: "Second Chance Token", description: "Save your streak if you miss a day", price: 30, category: "utility"},
    {id: 9, name: "Custom Voice", description: "Personalized AI voice", price: 150, category: "feature"}
  ];

  const sampleAchievements = [
    {id: 1, name: "First Steps", description: "Complete your first journal entry", earned: true, icon: "üìù"},
    {id: 2, name: "7-Day Warrior", description: "Maintain a 7-day streak", earned: false, icon: "üî•"},
    {id: 3, name: "Community Builder", description: "Join your first tribe", earned: false, icon: "üë•"},
    {id: 4, name: "30-Day Calm Seeker", description: "Maintain a 30-day streak", earned: false, icon: "üåø"},
    {id: 5, name: "365-Day Legend", description: "Maintain a 365-day streak", earned: false, icon: "üëë"},
    {id: 6, name: "Mental Fitness Pro", description: "Reach 90+ mental fitness score", earned: false, icon: "üí™"}
  ];

  const aiResponses = {
    happy: [
      "That's wonderful to hear! üòä Your positivity is contagious. What's making you feel so happy today?",
      "I'm so glad you're feeling happy! üåü Celebrating the good moments is important. Want to share what's bringing you joy?",
      "Happiness looks good on you! ‚ú® Let's savor this moment together. What's making you smile today?"
    ],
    sad: [
      "I'm sorry you're feeling down. üòî It's okay to have sad days - they make the happy ones brighter. Want to talk about what's bothering you?",
      "I hear you're feeling sad, and I'm here for you. üíô Sometimes just sharing how we feel can lighten the load. What's on your mind?",
      "Thank you for sharing that you're feeling sad. It takes courage to acknowledge these feelings. üåß Would you like to talk more about what's causing this?"
    ],
    anxious: [
      "I understand that anxious feeling. ü´Ç Let's take a deep breath together. Would you like to try a quick grounding exercise?",
      "Anxiety can be really overwhelming. üí≠ I'm here to help you through it. Want to explore what's triggering these feelings?",
      "It's okay to feel anxious sometimes. üå´ Let's work through this together. Would naming the specific worries help?"
    ],
    angry: [
      "I hear the frustration in your words. üí¢ Anger is a valid emotion - it's what we do with it that matters. Want to talk about what's upsetting you?",
      "It sounds like you're feeling pretty angry right now. üî• Let's channel that energy constructively. What's triggering this response?",
      "Anger can be a powerful messenger. üì£ I'm here to listen without judgment. Want to explore what's beneath the surface?"
    ],
    neutral: [
      "Thanks for checking in with me. ü§ó How has your day been so far?",
      "I'm here to listen whenever you're ready to share. üí´ What's on your mind today?",
      "Hello there! üåà How are you really feeling behind that neutral facade?"
    ]
  };

  useEffect(() => {
    loadInitialData();
    checkDailyLoginBonus();
  }, []);

  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);
  
  const checkDailyLoginBonus = () => {
    const today = new Date().toDateString();
    const lastLogin = localStorage.getItem('lastLogin');
    
    if (lastLogin !== today) {
      // Award daily login bonus
      const streak = parseInt(localStorage.getItem('loginStreak') || 0);
      const newStreak = lastLogin && 
        new Date().getTime() - new Date(lastLogin).getTime() < 2 * 24 * 60 * 60 * 1000 
        ? streak + 1 : 1;
      
      const bonusCoins = 10 + (newStreak * 5); // 10 coins base + 5 per streak day
      
      setUser(prev => ({
        ...prev,
        coins: prev.coins + bonusCoins,
        loginStreak: newStreak
      }));
      
      setBonusAmount(bonusCoins);
      setLoginStreak(newStreak);
      setShowLoginBonus(true);
      
      localStorage.setItem('lastLogin', today);
      localStorage.setItem('loginStreak', newStreak.toString());
      
      setTimeout(() => {
        setShowLoginBonus(false);
      }, 5000);
    }
  };
  
  const startNewChat = () => {
    setMessages([{
      id: Date.now(),
      text: "Hello! I'm MoodMate, your mental health companion. How are you feeling today?",
      sender: "bot",
      timestamp: new Date().toISOString()
    }]);
    setShowNewChatModal(false);
  };

  const loadInitialData = () => {
    setMessages([{
      id: Date.now(),
