import React, { useState, useEffect } from "react";
import App from "./App"; // <-- Your main MoodMate app
import "./AppWrapper.css";

function AppWrapper() {
  const [isSignup, setIsSignup] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [formData, setFormData] = useState({
    username: "",
    email: "",
    phone: "",
    password: "",
    confirmPassword: ""
  });
  const [error, setError] = useState("");
  const [message, setMessage] = useState("");
  const [user, setUser] = useState(null);

  const API_URL = "http://127.0.0.1:5000/auth"; // âœ… keep all auth under /auth

  useEffect(() => {
    const storedUser = localStorage.getItem("loggedInUser");
    if (storedUser) {
      setUser(JSON.parse(storedUser));
      setIsLoggedIn(true);
    }
  }, []);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setMessage("");

    try {
      const endpoint = isSignup ? "signup" : "login";
      const bodyData = isSignup
        ? {
            username: formData.username,
            email: formData.email,
            phone: formData.phone,
            password: formData.password
          }
        : {
            loginId: formData.email || formData.phone,
            password: formData.password
          };

      const res = await fetch(`${API_URL}/${endpoint}`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(bodyData)
      });

      const data = await res.json();

      if (data.success) {
        setMessage(data.message);

        if (!isSignup) {
          setUser(data.user);
          setIsLoggedIn(true);
          localStorage.setItem("loggedInUser", JSON.stringify(data.user));
        } else {
          setIsSignup(false); // after signup, switch to login
        }
      } else {
        setError(data.message);
      }
    } catch (err) {
      console.error("Auth error:", err);
      setError("âš ï¸ Server error. Please try again later.");
    }
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    setUser(null);
    localStorage.removeItem("loggedInUser");
    setFormData({
      username: "",
      email: "",
      phone: "",
      password: "",
      confirmPassword: ""
    });
    setError("");
    setMessage("");
  };

  // --- MAIN APP AFTER LOGIN ---
  if (isLoggedIn && user) {
    return <App user={user} onLogout={handleLogout} />;
  }

  // --- LOGIN / SIGNUP FORM ---
  return (
    <div className="auth-container">
      <div className="auth-box">
        <h1>ðŸŒ™ MoodMate</h1>
        <p>Your mental health companion</p>

        <form onSubmit={handleSubmit} className="auth-form">
          {isSignup && (
            <>
              <input
                type="text"
                name="username"
                placeholder="Username"
                value={formData.username}
                onChange={handleChange}
              />
              <input
                type="email"
                name="email"
                placeholder="Email"
                value={formData.email}
                onChange={handleChange}
              />
              <input
                type="text"
                name="phone"
                placeholder="Phone Number"
                value={formData.phone}
                onChange={handleChange}
              />
            </>
          )}

          {!isSignup && (
            <input
              type="text"
              name="email"
              placeholder="Email or Phone Number"
              value={formData.email}
              onChange={handleChange}
            />
          )}

          <input
            type="password"
            name="password"
            placeholder="Password"
            value={formData.password}
            onChange={handleChange}
          />

          {isSignup && (
            <input
              type="password"
              name="confirmPassword"
              placeholder="Confirm Password"
              value={formData.confirmPassword}
              onChange={handleChange}
            />
          )}

          {error && <div className="error-message">{error}</div>}
          {message && <div className="success-message">{message}</div>}

          <button type="submit">{isSignup ? "Sign Up" : "Login"}</button>
        </form>

        {!isSignup && (
          <button
            className="forgot-btn"
            onClick={() =>
              alert("ðŸ”‘ Password reset link sent to your email!")
            }
          >
            Forgot Password?
          </button>
        )}

        <p className="toggle-text">
          {isSignup ? (
            <>
              Already have an account?{" "}
              <span onClick={() => setIsSignup(false)}>Login</span>
            </>
          ) : (
            <>
              New here? <span onClick={() => setIsSignup(true)}>Sign Up</span>
            </>
          )}
        </p>
      </div>
    </div>
  );
}

export default AppWrapper;
