  const startNewChat = () => {
    setMessages([{
      text: "Hello! I'm MoodMate, your mental health companion. How are you feeling today?",
      sender: "bot"
    }]);
    setShowNewChatModal(false);
  };

  const sendMessage = async () => {
    if (!input.trim() || !isLoggedIn) return;

    const userMsg = { text: input, sender: "user" };
    setMessages(prev => [...prev, userMsg]);
    setInput("");
    setIsTyping(true);
    
    // Check for emergency keywords
    const emergencyKeywords = ['suicide', 'kill myself', 'end it all', 'want to die', 'self harm', 'hurt myself'];
    const hasEmergency = emergencyKeywords.some(keyword => 
      input.toLowerCase().includes(keyword)
    );
    
    if (hasEmergency) {
      setShowEmergencyModal(true);
      setIsTyping(false);
      return;
    }
    
    // Simulate typing animation
    simulateTyping();

    try {
      let token = localStorage.getItem('accessToken');
      // Check if token exists and is valid
      if (!token) {
        throw new Error("No authentication token");
      }

      const requestOptions = {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({ message: input })
      };

      let response = await fetch('http://localhost:5000/api/chat', requestOptions);

      // Handle 401 specifically - try to refresh token
      if (response.status === 401) {
        try {
          token = await refreshToken();
          requestOptions.headers.Authorization = `Bearer ${token}`;
          response = await fetch('http://localhost:5000/api/chat', requestOptions);
        } catch (refreshError) {
          console.error("Token refresh failed, using fallback response");
          // Use fallback response instead of logging out
          throw new Error("Token refresh failed, using fallback");
        }
      }

      if (!response.ok) {
        throw new Error("API error");
      }

      const data = await response.json();
      handleChatResponse(data);

    } catch (error) {
      console.log("Chat error:", error);
      // Fallback to diverse AI responses based on mood detection
      const mood = detectMoodFromInput(input);
      const moodResponses = aiResponses[mood] || aiResponses.neutral;
      const randomResponse = moodResponses[Math.floor(Math.random() * moodResponses.length)];
      
      const botMsg = { 
        text: randomResponse, 
        sender: "bot",
        mood: mood,
        moodEmoji: getMoodEmoji(mood),
        phrase: getMoodPhrase(mood)
      };
      setMessages(prev => [...prev, botMsg]);
    }

    setIsTyping(false);
    setTypingIndicator("");
  };

  const handleChatResponse = (data) => {
    const botMsg = { 
      text: data.reply, 
      sender: "bot",
      mood: data.mood,
      moodEmoji: data.moodEmoji,
      phrase: data.phrase
    };
    setMessages(prev => [...prev, botMsg]);

    // Update user data if coins were earned
    if (data.coinsEarned > 0) {
      const newCoins = (user?.coins || 0) + data.coinsEarned;
      const updatedUser = {...user, coins: newCoins, streak: data.streak};
      setUser(updatedUser);
      localStorage.setItem('user', JSON.stringify(updatedUser));
    }
  };

  const detectMoodFromInput = (text) => {
    const textLower = text.toLowerCase();
    if (textLower.includes('happy') || textLower.includes('good') || textLower.includes('great') || textLower.includes('excited')) return 'happy';
    if (textLower.includes('sad') || textLower.includes('depressed') || textLower.includes('unhappy') || textLower.includes('cry')) return 'sad';
    if (textLower.includes('anxious') || textLower.includes('nervous') || textLower.includes('worried') || textLower.includes('stress')) return 'anxious';
    if (textLower.includes('angry') || textLower.includes('mad') || textLower.includes('frustrated') || textLower.includes('annoyed')) return 'angry';
    return 'neutral';
  };

  const getMoodEmoji = (mood) => {
    const emojis = {
      happy: "😊",
      sad: "😢",
      anxious: "😰",
      angry: "😠",
      neutral: "😊"
    };
    return emojis[mood];
  };

  const getMoodPhrase = (mood) => {
    const phrases = {
