import React, { useState } from "react";

const API_BASE = "http://localhost:5000/api";

function App() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [journal, setJournal] = useState([]);
  const [resources, setResources] = useState([]);

  // Chat with backend
  const sendMessage = async () => {
    if (!input.trim()) return;
    const userMsg = { sender: "You", text: input };
    setMessages([...messages, userMsg]);

    try {
      const res = await fetch(`${API_BASE}/chat`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ message: input }),
      });
      const data = await res.json();
      const botMsg = { sender: "MoodMate", text: data.reply || "..." };
      setMessages((prev) => [...prev, botMsg]);
    } catch (err) {
      console.error("Chat error:", err);
    }
    setInput("");
  };

  // Journal handling
  const addJournal = async (entry) => {
    try {
      const res = await fetch(`${API_BASE}/journal`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ entry }),
      });
      const data = await res.json();
      setJournal((prev) => [...prev, data]);
    } catch (err) {
      console.error("Journal error:", err);
    }
  };

  // Fetch resources
  const getResources = async () => {
    try {
      const res = await fetch(`${API_BASE}/resources`);
      const data = await res.json();
      setResources(data.resources || []);
    } catch (err) {
      console.error("Resources error:", err);
    }
  };

  return (
    <div style={{ padding: "20px", fontFamily: "Arial" }}>
      <h1>MoodMate</h1>

      {/* Chat */}
      <div style={{ border: "1px solid #ccc", padding: "10px", height: "200px", overflowY: "auto" }}>
        {messages.map((msg, i) => (
          <div key={i}><b>{msg.sender}:</b> {msg.text}</div>
        ))}
      </div>
      <input
        value={input}
        onChange={(e) => setInput(e.target.value)}
        placeholder="Type a message..."
      />
      <button onClick={sendMessage}>Send</button>

      {/* Journal */}
      <div style={{ marginTop: "20px" }}>
        <h2>Journal</h2>
        <button onClick={() => addJournal("Feeling good today!")}>Quick Add</button>
        <ul>
          {journal.map((j, i) => <li key={i}>{j.entry}</li>)}
        </ul>
      </div>

      {/* Resources */}
      <div style={{ marginTop: "20px" }}>
        <h2>Resources</h2>
        <button onClick={getResources}>Load Resources</button>
        <ul>
          {resources.map((r, i) => <li key={i}>{r}</li>)}
        </ul>
      </div>
    </div>
  );
}

export default App;
