                    <span className="message-time">{new Date(msg.timestamp).toLocaleTimeString()}</span>
                  </div>
                </div>
              ))}
              <div ref={tribeChatEndRef}></div>
            </div>
            
            <div className="tribe-input">
              <input 
                type="text" 
                placeholder="Share something with the community..." 
                value={tribeInput}
                onChange={(e) => setTribeInput(e.target.value)}
                onKeyDown={(e) => e.key === "Enter" && sendTribeMessage(activeTribe.id)}
              />
              <button onClick={() => sendTribeMessage(activeTribe.id)}>Send</button>
            </div>
          </div>
          
          <div className="tribe-resources">
            <h3>Community Resources</h3>
            <div className="resource-list">
              {tribeResources.map(resource => (
                <div key={resource.id} className="resource-item">
                  <h4>{resource.title}</h4>
                  <p>{resource.description}</p>
                  <button onClick={() => {
                    if (resource.type === "exercise") setShowMeditationGuide(true);
                    if (resource.type === "support") alert("Support threads would open here");
                    if (resource.type === "challenge") setShowChallengeModal(true);
                  }}>
                    {resource.type === "exercise" ? "View" : 
                     resource.type === "support" ? "Explore" : "Participate"}
                  </button>
                </div>
              ))}
            </div>
            
            <div className="tribe-members">
              <h4>Active Members</h4>
              <div className="members-list">
                <div className="member">A</div>
                <div className="member">B</div>
                <div className="member">C</div>
                <div className="member">D</div>
                <div className="member">+{activeTribe.member_count - 4}</div>
              </div>
            </div>
            <div className="tribe-actions">
              <button className="btn-secondary" onClick={() => {
                const updatedTribes = moodTribes.map(t => 
                  t.id === activeTribe.id ? {...t, isMember: false} : t
                );
                setMoodTribes(updatedTribes);
                setUserTribes(prev => prev.filter(t => t.id !== activeTribe.id));
                setActiveTribe(null);
                alert(`You've left the ${activeTribe.name} tribe`);
              }}>
                Leave Tribe
              </button>
            </div>
          </div>
        </div>
      </div>
    );
  };

  const shopCategories = ["All", "theme", "avatar", "cosmetic", "content", "utility", "feature"];

  const filteredShopItems = activeShopCategory === "All"
    ? sampleShopItems
    : sampleShopItems.filter(item => item.category.toLowerCase() === activeShopCategory.toLowerCase());

  // Quick Actions Menu Component
  const QuickActionsMenu = () => {
    return (
      <div className="quick-actions-menu">
        <h3>Quick Actions</h3>
        <div className="quick-actions-grid">
          <button onClick={() => startBreathingExercise()} className="quick-action-btn">
            <span className="action-icon">üßò</span>
            <span>Breathing Exercise</span>
          </button>
          <button onClick={() => setShowMeditationGuide(true)} className="quick-action-btn">
            <span className="action-icon">üåø</span>
            <span>Meditation Guide</span>
          </button>
          <button onClick={() => setShowGamesModal(true)} className="quick-action-btn">
            <span className="action-icon">üéÆ</span>
            <span>Mood Games</span>
          </button>
          <button onClick={() => setShowAICoach(true)} className="quick-action-btn">
            <span className="action-icon">ü§ñ</span>
            <span>AI Coach</span>
          </button>
          <button onClick={() => setShowSleepCompanion(true)} className="quick-action-btn">
            <span className="action-icon">üåô</span>
            <span>Sleep Companion</span>
          </button>
          <button onClick={() => setShowMoodReport(true)} className="quick-action-btn">
            <span className="action-icon">üìä</span>
            <span>Mood Report</span>
          </button>
        </div>
      </div>
    );
  };

  return (
    <div className={`app-container ${userData.currentTheme}-theme`}>
      <EmergencyModal />
      <NewChatModal />
      <LoginBonusModal />
      <MeditationGuideModal />
      <BreathingExerciseModal />
      <TherapistInfoModal />
      <TherapistFormModal />
      <PaymentModal />
      <GamesModal />
      <AICoachModal />
      <SleepCompanionModal />
      <ChallengeModal />
      <JournalAnalysisModal />
      <MoodTrackerModal />
      
      <ChatHistorySidebar />
      
      {activeGame === "bubble" && <BubbleGame />}
      {activeGame === "coloring" && <ColoringGame />}
      {activeGame === "stressBalloon" && <StressBalloonGame />}
      {activeGame === "gratitude" && <GratitudeWheelGame />}
      {activeGame === "breathing" && <BreathingExerciseModal />}
      
      <header className="app-header">
        <div className="header-left">
          <button onClick={() => setSidebarOpen(true)} className="menu-btn">‚ò∞</button>
          <h1>üåô MoodMate</h1>
          <div className="user-stats">
            <span className="streak-display">üî• {userData.streak || 0} day streak</span>
            <span>ü™ô {userData.coins || 0} coins</span>
          </div>
        </div>
        <div className="header-right">
          <div className="user-info">
            <span className="user-name">{userData.name}</span>
            <span className="user-status">Online</span>
          </div>
          <button onClick={onLogout} className="logout-btn">Logout</button>
        </div>
      </header>

      <nav className="app-nav">
        <button 
          className={activeTab === "chat" ? "active" : ""} 
          onClick={() => setActiveTab("chat")}
        >
          üí¨ Chat
        </button>
        <button 
          className={activeTab === "journal" ? "active" : ""} 
          onClick={() => setActiveTab("journal")}
        >
          üìì Journal
        </button>
        <button 
          className={activeTab === "therapy" ? "active" : ""} 
          onClick={() => setActiveTab("therapy")}
        >
          üë®‚Äç‚öïÔ∏è Therapy
        </button>
        <button 
          className={activeTab === "community" ? "active" : ""} 
          onClick={() => setActiveTab("community")}
        >
          üåç Community
        </button>
        <button 
          className={activeTab === "shop" ? "active" : ""} 
          onClick={() => setActiveTab("shop")}
        >
          üõí Shop
        </button>
        <button 
          className={activeTab === "profile" ? "active" : ""} 
          onClick={() => setActiveTab("profile")}
        >
          üë§ Profile
        </button>
      </nav>

      <main className="app-main">
        {activeTab === "chat" && (
          <div className="chat-tab">
            <div className="chat-header">
              <h2>Chat with MoodMate</h2>
              <div className="chat-actions">
                <button onClick={() => setShowNewChatModal(true)} className="new-chat-btn">
                  + New Chat
                </button>
                <button onClick={() => setShowMeditationGuide(true)} className="breathing-btn">
                  üßò Exercises
                </button>
                <button onClick={() => setShowGamesModal(true)} className="games-btn">
                  üéÆ Games
                </button>
              </div>
            </div>

            {/* Quick Actions Menu */}
            <QuickActionsMenu />

            <div className="chat-box">
              {messages.map((msg, i) => (
                <div key={`msg-${i}-${msg.timestamp || Date.now()}`} className={`chat-message ${msg.sender} ${msg.isReward ? 'reward' : ''} ${msg.isExercise ? 'exercise' : ''}`}>
                  {msg.sender === "bot" && (
                    <div className="avatar bot-avatar">ü§ñ</div>
                  )}
                  {msg.sender === "user" && (
                    <div className="avatar user-avatar">üë§</div>
                  )}
                  <div className="bubble">
                    {msg.text}
                    {msg.moodEmoji && (
                      <div className="mood-indicator">
                        {msg.moodEmoji} {msg.phrase}
                      </div>
                    )}
                  </div>
                </div>
              ))}

              {isTyping && (
                <div className="chat-message bot">
                  <div className="avatar bot-avatar">ü§ñ</div>
                  <div className="bubble typing">
                    {typingIndicator || "..."}
                  </div>
                </div>
              )}

              <div ref={chatEndRef}></div>
            </div>

            <div className="input-area">
              <input
                type="text"
                placeholder="How are you feeling today?"
                value={input}
                onChange={(e) => setInput(e.target.value)}
                onKeyDown={(e) => e.key === "Enter" && sendMessage()}
                ref={inputRef}
              />
              <button onClick={sendMessage} className="send-btn">Send</button>
              <button onClick={() => setShowMeditationGuide(true)} className="exercise-btn" title="Meditation Exercises">üßò‚Äç‚ôÇÔ∏è</button>
            </div>
          </div>
        )}

        {activeTab === "journal" && (
          <div className="journal-tab">
            <div className="journal-header">
              <h2>Your Journal</h2>
              <div className="journal-actions">
                <button onClick={addJournalEntry} className="add-entry-btn">
                  + New Entry
                </button>
                <button onClick={() => setShowJournalAnalysis(true)} className="analyze-btn">
                  üìä Analyze
                </button>
                <button onClick={() => setShowMoodTracker(true)} className="mood-btn">
                  üòä Track Mood
                </button>
              </div>
            </div>
            
            <div className="journal-stats">
              <div className="stat-card">
                <h3>Total Entries</h3>
                <p>{journalEntries.length}</p>
              </div>
              <div className="stat-card">
                <h3>This Week</h3>
                <p>{journalEntries.filter(e => new Date(e.date) > new Date(Date.now() - 7*24*60*60*1000)).length}</p>
              </div>
              <div className="stat-card">
                <h3>Shared</h3>
                <p>{journalEntries.filter(e => e.shared).length}</p>
              </div>
            </div>
            
            <div className="journal-entries">
              {journalEntries.length === 0 ? (
                <div className="empty-state">
                  <p>No journal entries yet. Write your first entry!</p>
                  <button onClick={addJournalEntry}>Start Journaling</button>
                </div>
              ) : (
                journalEntries.map((entry, index) => (
                  <div key={`entry-${entry.id}-${index}`} className="journal-entry">
                    <div className="entry-header">
                      <div className="entry-date">{entry.date}</div>
                      <div className="entry-actions">
                        {!entry.shared && userTribes.length > 0 && (
                          <button 
                            onClick={() => shareJournalEntry(entry.id)}
                            className="share-btn"
                            title="Share to your tribes"
                          >
                            Share
                          </button>
                        )}
                        <button className="edit-btn">Edit</button>
                      </div>
                    </div>
                    <div className="entry-text">{entry.text}</div>
                    {entry.summary && (
                      <div className="entry-summary">
                        <strong>Summary:</strong> {entry.summary}
                      </div>
                    )}
                    {entry.mood && (
                      <div className="entry-mood">
                        <strong>Mood:</strong> {entry.mood}
                      </div>
                    )}
                  </div>
                ))
              )}
            </div>
          </div>
        )}

        {activeTab === "therapy" && (
          <div className="therapy-tab">
            <div className="resources-section">
              <h3>üÜò Crisis Resources</h3>
              <div className="resources-grid">
                {resources.filter(r => r.type === 'crisis').map((resource, index) => (
                  <div key={`resource-${index}`} className="resource-card">
                    <h4>{resource.name}</h4>
                    <p>{resource.number}</p>
                    <small>{resource.available}</small>
                    {resource.number && (
                      <button 
                        className="call-btn"
                        onClick={() => window.open(`tel:${resource.number.replace(/\D/g, '')}`)}
                      >
                        üìû Call Now
                      </button>
                    )}
                  </div>
                ))}
              </div>
            </div>

            <div className="therapists-section">
              <h3>üë®‚Äç‚öïÔ∏è Professional Therapists</h3>
              <div className="therapists-grid">
                {therapists.map((therapist) => (
                  <div key={`therapist-${therapist.id}`} className="therapist-card">
                    <div className="therapist-avatar">{therapist.avatar}</div>
                    <div className="therapist-info">
                      <h4>{therapist.name}</h4>
                      <p>{therapist.specialization}</p>
                      <p>‚≠ê {therapist.rating} ‚Ä¢ {therapist.experience}</p>
                      <p>ü™ô {therapist.price} coins/session</p>
                      <p className="languages">{therapist.languages.join(", ")}</p>
                      <p className="therapist-description">{therapist.description}</p>
                    </div>
                    <div className="therapist-actions">
                      <button 
                        className="book-btn"
                        onClick={() => bookTherapist(therapist)}
                        disabled={userData.coins < therapist.price}
                      >
                        {userData.coins >= therapist.price ? "Book Session" : "Not Enough Coins"}
                      </button>
                      <button 
                        className="info-btn"
                        onClick={() => setShowTherapistInfo(therapist)}
                      >
                        More Info
                      </button>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </div>
        )}

        {activeTab === "community" && activeTribe ? (
          <TribeCommunity />
        ) : activeTab === "community" ? (
          <div className="community-tab">
            <div className="community-layout">
              <div className="mood-wall-section">
                <h3>üåç Community Mood Wall</h3>
                <button onClick={addMoodWallPost} className="add-post-btn">+ Share Your Feelings</button>
                <div className="mood-wall">
                  {moodWall.length === 0 ? (
                    <div className="empty-state">
                      <p>Be the first to share your thoughts with the community!</p>
                    </div>
                  ) : (
                    moodWall.slice(0, 3).map((post, index) => (
                      <div key={`post-${post.id}-${index}`} className="mood-post">
                        <p>{post.text}</p>
                        <div className="post-footer">
                          <div className="post-reactions">
                            <button>üíñ {post.likes}</button>
                            <button>ü§ó</button>
                            <button>üëç</button>
                          </div>
                          <span className="post-time">{new Date(post.timestamp).toLocaleDateString()}</span>
                        </div>
                      </div>
                    ))
                  )}
                </div>
              </div>

              <div className="tribes-section">
                <h3>üéØ Mood Tribes</h3>
                <div className="tribes-grid">
                  {moodTribes.map((tribe) => (
                    <div key={`tribe-${tribe.id}`} className="tribe-card">
                      <h4>{tribe.name}</h4>
                      <p>{tribe.description}</p>
                      <p className="member-count">üë• {tribe.member_count} members</p>
                      <button 
                        onClick={() => tribe.isMember ? setActiveTribe(tribe) : joinTribe(tribe.id)}
                        className={tribe.isMember ? "joined-btn" : "join-btn"}
                      >
                        {tribe.isMember ? "Visit Community" : "Join Tribe"}
                      </button>
                    </div>
                  ))}
                </div>
              </div>

              <div className="challenges-section">
                <h3>üèÜ Community Challenges</h3>
                <div className="challenges-grid">
                  {moodChallenges.map(challenge => (
                    <div key={challenge.id} className="challenge-card">
                      <h4>{challenge.title}</h4>
                      <p className="challenge-duration">{challenge.duration}</p>
                      <p>{challenge.description}</p>
                      <div className="challenge-reward">{challenge.reward}</div>
                      <button onClick={() => startChallenge(challenge.id)} className="join-challenge-btn">
                        Join Challenge
                      </button>
                    </div>
                  ))}
                </div>
              </div>
            </div>
          </div>
        ) : null}

        {activeTab === "shop" && (
          <div className="shop-tab">
            <h2>üõí MoodMate Shop</h2>
            <p>Use your Mood Coins to unlock special features and customization options!</p>
            
            <div className="shop-categories">
              {shopCategories.map(category => (
                <button 
                  key={category} 
                  className={`category-btn ${activeShopCategory === category ? 'active' : ''}`}
                  onClick={() => setActiveShopCategory(category)}
                >
                  {category}
                </button>
              ))}
            </div>
            
            <div className="shop-grid">
              {filteredShopItems.length > 0 ? (
                filteredShopItems.map((item) => {
                  const isOwned = userData.purchasedItems && userData.purchasedItems.includes(item.id);
                  const isThemeOwned = item.category === "theme" && userData.ownedThemes && userData.ownedThemes.includes(item.name.toLowerCase().replace(' theme', ''));
                  const isAvatarOwned = item.category === "avatar" && userData.purchasedItems && userData.purchasedItems.includes(item.id);
                  
                  return (
                    <div key={`shop-item-${item.id}`} className="shop-item-card">
                      <div className="item-image">
                        {item.category === "theme" && <div className={`theme-preview ${item.name.toLowerCase().replace(' theme', '')}-theme`}></div>}
                        {item.category === "avatar" && <span className="item-icon">üë§</span>}
                        {item.category === "cosmetic" && <span className="item-icon">üé®</span>}
                        {item.category === "content" && <span className="item-icon">üìö</span>}
                        {item.category === "utility" && <span className="item-icon">‚ö°</span>}
                        {item.category === "feature" && <span className="item-icon">‚öôÔ∏è</span>}
                      </div>
                      <div className="item-info">
                        <h4>{item.name}</h4>
                        <p>{item.description}</p>
                        <div className="item-price">ü™ô {item.price} coins</div>
                        
                        <div className="item-actions">
                          {(isOwned || isThemeOwned || isAvatarOwned) && item.permanent ? (
                            item.category === "theme" ? (
                              userData.currentTheme === item.name.toLowerCase().replace(' theme', '') ? (
                                <button className="applied-btn">Applied</button>
                              ) : (
                                <button 
                                  onClick={() => applyTheme(item.name.toLowerCase().replace(' theme', ''))}
                                  className="apply-btn"
                                >
                                  Apply Theme
                                </button>
                              )
                            ) : item.category === "avatar" ? (
                              userData.avatar === item.name ? (
                                <button className="applied-btn">Applied</button>
                              ) : (
                                <button 
                                  onClick={() => applyAvatar(item.name)}
                                  className="apply-btn"
                                >
                                  Apply Avatar
                                </button>
                              )
                            ) : (
                              <button className="owned-btn">Owned</button>
                            )
                          ) : (
                            <>
                              <button 
                                onClick={() => buyItem(item)}
                                disabled={userData.coins < item.price}
                                className={userData.coins >= item.price ? "buy-btn" : "disabled-btn"}
                              >
                                {userData.coins >= item.price ? "Purchase" : "Not Enough Coins"}
                              </button>
                              <button 
                                onClick={() => showItemDetails(item)}
                                className="info-btn"
                              >
                                Details
                              </button>
                            </>
                          )}
                        </div>
                      </div>
                    </div>
                  );
                })
              ) : (
                <div className="empty-state">
                  <p>No items in this category yet.</p>
                </div>
              )}
            </div>
          </div>
        )}

        {activeTab === "profile" && (
          <div className="profile-tab">
            <div className="profile-header">
              <div className="profile-avatar">
                <div className="avatar-image">
                  {userData.avatar ? userData.avatar.charAt(0) : userData.name?.charAt(0) || 'U'}
                </div>
                <div className="profile-badge">
                  <span>üî• {userData.streak || 0}</span>
                </div>
              </div>
              <div className="profile-info">
                <h2>{userData.name}</h2>
                <p className="member-since">Member since {new Date().toLocaleDateString()}</p>
                
                <div className="profile-stats">
                  <div className="stat">
                    <span className="stat-value">{userData.streak || 0}</span>
                    <span className="stat-label">Day Streak</span>
                  </div>
                  <div className="stat">
                    <span className="stat-value">{userData.coins || 0}</span>
                    <span className="stat-label">Mood Coins</span>
                  </div>
                  <div className="stat">
                    <span className="stat-value">{journalEntries.length}</span>
                    <span className="stat-label">Journal Entries</span>
                  </div>
                  <div className="stat">
                    <span className="stat-value">{userTribes.length}</span>
                    <span className="stat-label">Tribes</span>
                  </div>
                </div>
              </div>
            </div>

            <div className="profile-content">
              <div className="profile-column">
                <div className="profile-section">
                  <h3>üî• Weekly Streak</h3>
                  <div className="weekly-streak">
                    <div className="streak-bar">
                      <div 
                        className="streak-progress" 
                        style={{width: `${Math.min(100, (userData.streak / 7) * 100)}%`}}
                      >
                        <div className="streak-fire"></div>
                      </div>
                    </div>
                    <div className="streak-days">
                      {['S', 'M', 'T', 'W', 'T', 'F', 'S'].map((day, i) => {
                        const dayStreak = i < (userData.streak % 7);
                        return (
                          <div key={i} className={`streak-day ${dayStreak ? 'active' : ''}`}>
                            <span>{day}</span>
                            {dayStreak && <div className="streak-dot"></div>}
                          </div>
                        );
                      })}
                    </div>
                  </div>
                  <div className="streak-motivation">
                    <p>You've maintained your streak for {userData.streak} days!</p>
                    <p>Keep going to unlock achievements.</p>
                  </div>
                </div>

                <div className="profile-section">
                  <h3>üèÖ Achievements</h3>
                  <div className="achievements-grid">
                    {achievements.map(achievement => (
                      <div key={`achievement-${achievement.id}`} className={`achievement ${achievement.earned ? 'earned' : 'locked'}`}>
                        <span className="achievement-icon">{achievement.icon}</span>
                        <div className="achievement-info">
                          <h4>{achievement.name}</h4>
                          <p>{achievement.description}</p>
                          {achievement.earned && <span className="achievement-coins">+{achievement.coins} coins</span>}
                        </div>
                        <span className="achievement-status">
                          {achievement.earned ? '‚úì' : 'üîí'}
                        </span>
                      </div>
                    ))}
                  </div>
                </div>
              </div>

              <div className="profile-column">
                <div className="profile-section">
                  <h3>üé® Customization</h3>
                  <div className="customization-options">
                    <div className="customization-group">
                      <h4>Themes</h4>
                      <div className="themes-grid">
                        {userData.ownedThemes && userData.ownedThemes.map(theme => (
                          <div key={theme} className={`theme-option ${userData.currentTheme === theme ? 'active' : ''}`}>
                            <div className={`theme-preview ${theme}-theme`}></div>
                            <div className="theme-name">{theme.charAt(0).toUpperCase() + theme.slice(1)}</div>
                            {userData.currentTheme === theme ? (
                              <div className="theme-status">Applied</div>
                            ) : (
                              <button onClick={() => applyTheme(theme)} className="theme-apply-btn">
                                Apply
                              </button>
                            )}
                          </div>
                        ))}
                      </div>
                    </div>
                    
                    <div className="customization-group">
                      <h4>Avatars</h4>
                      <div className="avatars-grid">
                        {sampleShopItems.filter(item => item.category === "avatar").map(avatar => {
                          const isOwned = userData.purchasedItems && userData.purchasedItems.includes(avatar.id);
                          return (
                            <div key={avatar.id} className={`avatar-option ${userData.avatar === avatar.name ? 'active' : ''} ${!isOwned ? 'locked' : ''}`}>
                              <div className="avatar-preview">{avatar.name.charAt(0)}</div>
                              <div className="avatar-name">{avatar.name}</div>
                              {isOwned ? (
                                userData.avatar === avatar.name ? (
                                  <div className="avatar-status">Applied</div>
                                ) : (
                                  <button onClick={() => applyAvatar(avatar.name)} className="avatar-apply-btn">
                                    Apply
                                  </button>
                                )
                              ) : (
                                <button 
                                  onClick={() => buyItem(avatar)}
                                  disabled={userData.coins < avatar.price}
                                  className={userData.coins >= avatar.price ? "buy-btn" : "disabled-btn"}
                                >
                                  Buy
                                </button>
                              )}
                            </div>
                          );
                        })}
                      </div>
                    </div>
                  </div>
                </div>

                <div className="profile-section">
                  <h3>üë• My Tribes</h3>
                  {userTribes.length === 0 ? (
                    <p>You haven't joined any tribes yet. Visit the Community tab to join one!</p>
                  ) : (
                    <div className="user-tribes">
                      {userTribes.map(tribe => (
                        <div key={`user-tribe-${tribe.id}`} className="user-tribe">
                          <span>{tribe.name}</span>
                          <button onClick={() => {
                            setActiveTribe(tribe);
                            setActiveTab("community");
                          }}>
                            Visit Community
                          </button>
                        </div>
                      ))}
                    </div>
                  )}
                </div>

                <div className="profile-section">
                  <h3>‚öôÔ∏è Settings</h3>
                  <div className="settings-grid">
                    <div className="setting-item">
                      <span>Voice Responses</span>
                      <label className="switch">
                        <input type="checkbox" checked={audioEnabled} onChange={toggleAudio} />
                        <span className="slider"></span>
                      </label>
                    </div>
                    <div className="setting-item">
                      <span>Background Music</span>
                      <label className="switch">
                        <input type="checkbox" checked={musicEnabled} onChange={toggleMusic} />
                        <span className="slider"></span>
                      </label>
                    </div>
                    <div className="setting-item">
                      <span>AI Provider</span>
                      <select value={aiProvider} onChange={(e) => switchAiProvider(e.target.value)}>
                        <option value="gemini">Gemini AI</option>
                        <option value="fallback">Fallback Mode</option>
                      </select>
                    </div>
                    <div className="setting-item">
                      <span>Notifications</span>
                      <label className="switch">
                        <input type="checkbox" defaultChecked />
                        <span className="slider"></span>
                      </label>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
