import React, { useState, useEffect, useRef } from "react";
import "./app.css";

function App() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [isTyping, setIsTyping] = useState(false);
  const [user, setUser] = useState(null);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [showLogin, setShowLogin] = useState(true);
  const [showRegister, setShowRegister] = useState(false);
  const [showForgotPassword, setShowForgotPassword] = useState(false);
  const [activeTab, setActiveTab] = useState("chat");
  const [journalEntries, setJournalEntries] = useState([]);
  const [therapists, setTherapists] = useState([]);
  const [resources, setResources] = useState([]);
  const [moodWall, setMoodWall] = useState([]);
  const [moodTribes, setMoodTribes] = useState([]);
  const [shopItems, setShopItems] = useState([]);
  const [userTribes, setUserTribes] = useState([]);
  const [currentTheme, setCurrentTheme] = useState("default");
  const [typingIndicator, setTypingIndicator] = useState("");
  const [showEmergencyModal, setShowEmergencyModal] = useState(false);
  const [achievements, setAchievements] = useState([]);
  const [activeTribe, setActiveTribe] = useState(null);
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [showNewChatModal, setShowNewChatModal] = useState(false);
  const [loginError, setLoginError] = useState("");
  const [registerError, setRegisterError] = useState("");
  const [aiProvider, setAiProvider] = useState("gemini");
  const [audioEnabled, setAudioEnabled] = useState(true);
  const [musicEnabled, setMusicEnabled] = useState(true);
  const [tribeChatMessages, setTribeChatMessages] = useState({});
  const [tribeInput, setTribeInput] = useState("");
  const [lastStreakUpdate, setLastStreakUpdate] = useState("");
  const [chatSessions, setChatSessions] = useState([]);
  const [currentChatId, setCurrentChatId] = useState(null);
  const [exercises, setExercises] = useState([]);
  const [activeExercise, setActiveExercise] = useState(null);
  const [exerciseProgress, setExerciseProgress] = useState(0);

  const chatEndRef = useRef(null);
  const audioRef = useRef(null);
  const backgroundMusicRef = useRef(null);

  // Breathing exercises data
  const breathingExercises = [
    {
      id: 1,
      name: "Box Breathing",
      description: "4-4-4-4 technique for stress relief",
      duration: 5,
      steps: [
        "Breathe in for 4 seconds",
        "Hold your breath for 4 seconds",
        "Exhale slowly for 4 seconds",
        "Hold for 4 seconds",
        "Repeat"
      ],
      icon: "🧘‍♂️"
    },
    {
      id: 2,
      name: "4-7-8 Breathing",
      description: "Calming technique for anxiety",
      duration: 7,
      steps: [
        "Breathe in for 4 seconds",
        "Hold your breath for 7 seconds",
        "Exhale completely for 8 seconds",
        "Repeat 4 times"
      ],
      icon: "🌬️"
    },
    {
      id: 3,
      name: "Deep Belly Breathing",
      description: "Diaphragmatic breathing for relaxation",
      duration: 10,
      steps: [
        "Place one hand on your chest, one on belly",
        "Breathe in deeply through nose (4 sec)",
        "Feel your belly expand",
        "Exhale slowly through mouth (6 sec)",
        "Repeat 5-10 times"
      ],
      icon: "🫁"
    },
    {
      id: 4,
      name: "Alternate Nostril Breathing",
      description: "Nadi Shodhana for balance",
      duration: 8,
      steps: [
        "Close right nostril with thumb",
        "Inhale through left nostril (4 sec)",
        "Close both nostrils (hold 4 sec)",
        "Open right, exhale through right (6 sec)",
        "Inhale through right, repeat cycle"
      ],
      icon: "👃"
    }
  ];

  // Meditation exercises
  const meditationExercises = [
    {
      id: 5,
      name: "Body Scan Meditation",
      description: "Progressive relaxation technique",
      duration: 15,
      steps: [
        "Lie down comfortably",
        "Focus attention on your toes",
        "Slowly move attention up through body",
        "Notice sensations without judgment",
        "Release tension in each area"
      ],
      icon: "🔍"
    },
    {
      id: 6,
      name: "Mindfulness Meditation",
      description: "Present moment awareness",
      duration: 10,
      steps: [
        "Sit comfortably with straight back",
        "Focus on your breath",
        "When mind wanders, gently return to breath",
        "Notice thoughts without judgment",
        "Expand awareness to sounds and sensations"
      ],
      icon: "🌿"
    }
  ];

  // Load saved session
  useEffect(() => {
    const token = localStorage.getItem('accessToken');
    const refreshToken = localStorage.getItem('refreshToken');
    const userData = localStorage.getItem('user');
    const savedTheme = localStorage.getItem('theme') || 'default';
    const savedTribes = JSON.parse(localStorage.getItem('userTribes') || '[]');
    const savedAchievements = JSON.parse(localStorage.getItem('achievements') || '[]');
    const savedAiProvider = localStorage.getItem('aiProvider') || 'gemini';
    const savedAudio = localStorage.getItem('audioEnabled') !== 'false';
    const savedMusic = localStorage.getItem('musicEnabled') !== 'false';
    const savedLastStreakUpdate = localStorage.getItem('lastStreakUpdate') || '';
    const savedChatSessions = JSON.parse(localStorage.getItem('chatSessions') || '[]');
