import React, { useState, useEffect } from 'react';
import './Community.css';

// Sample data that would normally come from your backend
const initialMoodWall = [
    {id: 1, userName: "Anonymous", text: "Finally finished a tough project at work. Feeling so relieved and proud! ✨", likes: 28, comments: 3, shares: 2, timestamp: "2 hours ago", userLiked: false},
    {id: 2, userName: "HappyUser123", text: "Just had a great chat with a friend I hadn't seen in a while. It's the small things!", likes: 45, comments: 5, shares: 1, timestamp: "5 hours ago", userLiked: true},
    {id: 3, userName: "Anonymous", text: "Struggling with a bit of anxiety today, but trying to focus on my breathing exercises.", likes: 62, comments: 7, shares: 0, timestamp: "1 day ago", userLiked: false}
];

const initialTribes = [
    {id: 1, name: "Positivity Tribe", description: "For those seeking positivity and joy.", members: 245, isMember: false, online: 23, tags: ["positivity", "joy", "gratitude"]},
    {id: 2, name: "Anxiety Allies", description: "Support for those dealing with anxiety.", members: 312, isMember: true, online: 45, tags: ["support", "anxiety", "mental-health"]},
    {id: 3, name: "Mindfulness Masters", description: "Practice mindfulness together.", members: 156, isMember: false, online: 12, tags: ["mindfulness", "meditation", "peace"]}
];

const initialChallenges = [
    {id: 1, title: "7 Days of Gratitude", description: "Write one thing you're grateful for each day.", reward: "Gratitude Badge + 30 coins", participants: 1245, joined: false},
    {id: 2, title: "Mindful Mornings", description: "Start each day with 5 mins of meditation.", reward: "Mindful Badge + 20 coins", participants: 987, joined: true}
];

function Community({ user }) {
    const [moodWall, setMoodWall] = useState(initialMoodWall);
    const [moodTribes, setMoodTribes] = useState(initialTribes);
    const [challenges, setChallenges] = useState(initialChallenges);
    const [newPostText, setNewPostText] = useState('');
    const [selectedMood, setSelectedMood] = useState('');
    const [activeTribe, setActiveTribe] = useState(null);
    const [tribeChat, setTribeChat] = useState([]);
    const [newMessage, setNewMessage] = useState('');

    // Simulate loading user data
    useEffect(() => {
        // This would be an API call in a real app
        const userData = {
            name: "CurrentUser",
            mood: "calm",
            level: 5,
            achievements: ["7-day streak", "Helper badge"]
        };
    }, []);

    const handleAddPost = () => {
        if (newPostText.trim() !== "") {
            const newPost = {
                id: Date.now(),
                userName: "You",
                text: newPostText,
                likes: 0,
                comments: 0,
                shares: 0,
                timestamp: "Just now",
                userLiked: false,
                mood: selectedMood
            };
            setMoodWall(prev => [newPost, ...prev]);
            setNewPostText('');
            setSelectedMood('');
        }
    };

    const handleJoinTribe = (tribeId) => {
        setMoodTribes(prevTribes => 
            prevTribes.map(tribe => 
                tribe.id === tribeId ? { 
                    ...tribe, 
                    isMember: !tribe.isMember, 
                    members: tribe.isMember ? tribe.members - 1 : tribe.members + 1 
                } : tribe
            )
        );
    };

    const handleLikePost = (postId) => {
        setMoodWall(prev => 
            prev.map(post => 
                post.id === postId ? { 
                    ...post, 
                    likes: post.userLiked ? post.likes - 1 : post.likes + 1,
                    userLiked: !post.userLiked
                } : post
            )
        );
    };

    const handleJoinChallenge = (challengeId) => {
        setChallenges(prevChallenges => 
            prevChallenges.map(challenge => 
                challenge.id === challengeId ? { 
                    ...challenge, 
                    joined: !challenge.joined,
                    participants: challenge.joined ? challenge.participants - 1 : challenge.participants + 1
                } : challenge
            )
        );
    };

    const handleJoinTribeChat = (tribe) => {
        setActiveTribe(tribe);
        // Simulate loading tribe chat messages
        const mockMessages = [
            {id: 1, user: "MindfulUser", text: "Welcome to our tribe! How is everyone today?", timestamp: "10:30 AM"},
            {id: 2, user: "CalmSoul", text: "I'm doing well, just finished my morning meditation", timestamp: "10:32 AM"},
            {id: 3, user: "PeaceSeeker", text: "Has anyone tried the new breathing exercise?", timestamp: "10:45 AM"}
        ];
        setTribeChat(mockMessages);
    };

    const handleSendMessage = () => {
        if (newMessage.trim() !== "" && activeTribe) {
            const message = {
                id: Date.now(),
                user: "You",
                text: newMessage,
                timestamp: new Date().toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'})
            };
            setTribeChat(prev => [...prev, message]);
            setNewMessage('');
        }
    };

    const handleKeyPress = (e) => {
        if (e.key === 'Enter' && !e.shiftKey) {
            e.preventDefault();
            handleSendMessage();
        }
    };

    return (
        <div className="community-hub">
            <div className="community-header">
                <h2>Mood Community</h2>
                <p>Connect with others who understand what you're going through</p>
            </div>

            <div className="community-layout">
                <div className="main-content">
                    <div className="mood-wall-section">
                        <h3>Community Mood Wall</h3>
                        
                        <div className="post-creator">
                            <textarea 
                                placeholder="Share your feelings with the community..."
                                value={newPostText}
                                onChange={(e) => setNewPostText(e.target.value)}
                            />
                            <div className="post-actions">
                                <div className="mood-selector">
                                    <span>I'm feeling:</span>
                                    {['happy', 'calm', 'anxious', 'sad', 'excited', 'grateful'].map(mood => (
                                        <button
                                            key={mood}
                                            className={selectedMood === mood ? 'mood-option selected' : 'mood-option'}
                                            onClick={() => setSelectedMood(mood)}
                                        >
                                            {mood}
                                        </button>
                                    ))}
                                </div>
                                <button 
                                    className="post-btn"
                                    onClick={handleAddPost}
                                    disabled={!newPostText.trim()}
                                >
                                    Post
                                </button>
                            </div>
                        </div>
                        
                        <div className="post-list">
                            {moodWall.map(post => (
                                <div key={post.id} className="post-card">
                                    <div className="post-header">
                                        <div className="user-info">
                                            <div className="post-avatar">{post.userName.charAt(0)}</div>
                                            <div>
                                                <div className="post-username">{post.userName}</div>
                                                <div className="post-timestamp">{post.timestamp}</div>
                                            </div>
                                        </div>
                                        {post.mood && <div className={`post-mood ${post.mood}`}>{post.mood}</div>}
                                    </div>
                                    <p className="post-content">{post.text}</p>
                                    <div className="post-stats">
                                        <button 
                                            className={post.userLiked ? 'like-btn liked' : 'like-btn'}
                                            onClick={() => handleLikePost(post.id)}
                                        >
                                            <i className={post.userLiked ? "fas fa-heart" : "far fa-heart"}></i>
                                            {post.likes}
                                        </button>
                                        <button className="comment-btn">
                                            <i className="far fa-comment"></i>
                                            {post.comments}
                                        </button>
                                        <button className="share-btn">
                                            <i className="far fa-share-square"></i>
                                            {post.shares}
                                        </button>
                                    </div>
                                </div>
                            ))}
                        </div>
                    </div>
                </div>

                <div className="community-sidebar">
                    <div className="tribes-section">
                        <h3>Mood Tribes</h3>
                        <div className="tribe-list">
                            {moodTribes.map(tribe => (
                                <div key={tribe.id} className="tribe-card">
                                    <div className="tribe-header">
                                        <h4>{tribe.name}</h4>
                                        <div className="tribe-meta">
                                            <span className="members-count">{tribe.members} members</span>
                                            <span className="online-count">{tribe.online} online</span>
                                        </div>
                                    </div>
                                    <p className="tribe-description">{tribe.description}</p>
                                    <div className="tribe-tags">
                                        {tribe.tags.map(tag => (
                                            <span key={tag} className="tag">#{tag}</span>
                                        ))}
                                    </div>
                                    <div className="tribe-actions">
                                        <button 
                                            className={tribe.isMember ? 'join-btn joined' : 'join-btn'}
                                            onClick={() => handleJoinTribe(tribe.id)}
                                        >
                                            {tribe.isMember ? 'Joined' : 'Join Tribe'}
                                        </button>
                                        <button 
                                            className="chat-btn"
                                            onClick={() => handleJoinTribeChat(tribe)}
                                        >
                                            Chat
                                        </button>
                                    </div>
                                </div>
                            ))}
                        </div>
                    </div>

                    <div className="challenges-section">
                        <h3>Community Challenges</h3>
                        <div className="challenge-list">
                            {challenges.map(challenge => (
                                <div key={challenge.id} className="challenge-card">
                                    <h4>{challenge.title}</h4>
                                    <p>{challenge.description}</p>
                                    <div className="challenge-meta">
                                        <span className="participants">{challenge.participants} participants</span>
                                        <span className="reward">{challenge.reward}</span>
                                    </div>
                                    <button 
                                        className={challenge.joined ? 'join-challenge-btn joined' : 'join-challenge-btn'}
                                        onClick={() => handleJoinChallenge(challenge.id)}
                                    >
                                        {challenge.joined ? 'Joined' : 'Join Challenge'}
                                    </button>
                                </div>
                            ))}
                        </div>
                    </div>
                </div>
            </div>

            {activeTribe && (
                <div className="tribe-chat-modal">
                    <div className="chat-header">
                        <h3>{activeTribe.name} Chat</h3>
                        <button className="close-chat" onClick={() => setActiveTribe(null)}>
                            <i className="fas fa-times"></i>
                        </button>
                    </div>
                    <div className="chat-messages">
                        {tribeChat.map(message => (
                            <div key={message.id} className={message.user === "You" ? "message own-message" : "message"}>
                                <div className="message-sender">{message.user}</div>
                                <div className="message-text">{message.text}</div>
                                <div className="message-time">{message.timestamp}</div>
                            </div>
                        ))}
                    </div>
                    <div className="chat-input">
                        <input
                            type="text"
                            placeholder="Type a message..."
                            value={newMessage}
                            onChange={(e) => setNewMessage(e.target.value)}
                            onKeyPress={handleKeyPress}
                        />
                        <button onClick={handleSendMessage}>
                            <i className="fas fa-paper-plane"></i>
                        </button>
                    </div>
                </div>
            )}
        </div>
    );
}

export default Community;
