import React, { useState } from 'react';
import PremiumPlans from './PremiumPlans'; 
import PremiumFeaturesBox from './PremiumFeaturesBox';
import './Premium.css';

const API_BASE_URL = 'http://127.0.0.1:5000';

function Premium({ user, onUpdateUser }) {
    const [showPremiumPlans, setShowPremiumPlans] = useState(false);

    const handleSubscribe = async (plan) => {
        try {
            const response = await fetch(`${API_BASE_URL}/api/buy_premium`, {
                method: 'POST',
                credentials: 'include' // Important for sending session cookie
            });
            const data = await response.json();
            if (response.ok && data.success) {
                onUpdateUser({ is_premium: 1 });
                alert(`ðŸŽ‰ Welcome to MoodMate Premium! Your ${plan} plan is now active.`);
                setShowPremiumPlans(false);
            } else {
                alert("Subscription failed. Please try again.");
            }
        } catch (error) {
            alert("An error occurred during subscription.");
        }
    };
    
    return (
        <div className="tab-content premium-tab">
            <PremiumFeaturesBox
                isPremium={user.is_premium}
                onUpgradeClick={() => setShowPremiumPlans(true)}
            />
            {showPremiumPlans && (
                <PremiumPlans
                    user={user}
                    onSubscribe={handleSubscribe}
                    onClose={() => setShowPremiumPlans(false)}
                />
            )}
        </div>
    );
}

export default Premium;
