import React, { useState, useEffect } from 'react';
import App from "./App";
import "./AppWrapper.css";

const API_BASE_URL = 'http://127.0.0.1:5000';

function AppWrapper() {
  const [user, setUser] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [authMode, setAuthMode] = useState('login');
  
  const [formData, setFormData] = useState({
    username: "", email: "", phone: "", password: "",
    confirmPassword: "", loginId: "", otp: ""
  });
  
  const [error, setError] = useState("");
  const [message, setMessage] = useState("");

  useEffect(() => {
    const storedUser = localStorage.getItem("moodmateUser");
    if (storedUser) {
      setUser(JSON.parse(storedUser));
    }
    setIsLoading(false);
  }, []);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleAuthAction = async (endpoint, payload) => {
    setIsLoading(true);
    setError("");
    setMessage("");
    try {
      const response = await fetch(`${API_BASE_URL}/api/auth${endpoint}`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        credentials: 'include',
        body: JSON.stringify(payload),
      });
      
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      
      const data = await response.json();
      if (data.success) {
        return data;
      } else {
        setError(data.message || 'An unknown error occurred.');
        return null;
      }
    } catch (err) {
      setError('Failed to connect to the server. Please check if it is running.');
      console.error('API Error:', err);
      return null;
    } finally {
      setIsLoading(false);
    }
  };

  const handleSignup = async (e) => {
    e.preventDefault();
    if (formData.password !== formData.confirmPassword) {
      setError("Passwords do not match!");
      return;
    }
    if (formData.password.length < 6) {
      setError("Password must be at least 6 characters long!");
      return;
    }
    
    const data = await handleAuthAction('/signup', {
      username: formData.username, 
      email: formData.email, 
      phone: formData.phone, 
      password: formData.password
    });
    
    if (data) {
      setMessage(data.message || "Account created successfully!");
      switchMode('login');
    }
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    const data = await handleAuthAction('/login', {
      loginId: formData.loginId, 
      password: formData.password
    });
    
    if (data && data.user) {
      setUser(data.user);
      localStorage.setItem("moodmateUser", JSON.stringify(data.user));
      setMessage(data.message || "Login successful!");
    }
  };
  
  const handleLogout = () => {
    setUser(null);
    localStorage.removeItem("moodmateUser");
    setMessage("Logged out successfully!");
  };

  const updateUser = (updatedUser) => {
    setUser(updatedUser);
    localStorage.setItem("moodmateUser", JSON.stringify(updatedUser));
  };

  const switchMode = (mode) => {
    setAuthMode(mode);
    setError("");
    setMessage("");
    setFormData({ 
      username: "", email: "", phone: "", password: "", 
      confirmPassword: "", loginId: "", otp: "" 
    });
  };

  if (isLoading) {
    return (
      <div className="loading-screen">
        <div className="loading-spinner">üåô</div>
        <p>Loading MoodMate...</p>
      </div>
    );
  }
  
  if (user) {
    return <App user={user} onLogout={handleLogout} onUpdateUser={updateUser} />;
  }

  return (
    <div className="auth-container">
      <div className="auth-box">
        <div className="auth-header">
          <h1>üåô MoodMate</h1>
          <p>Your mental wellness companion</p>
        </div>
        
        {error && <div className="error-message">‚ö†Ô∏è {error}</div>}
        {message && <div className="success-message">‚úÖ {message}</div>}

        {authMode === 'login' && (
          <form onSubmit={handleLogin} className="auth-form">
            <div className="form-group">
              <input 
                type="text" 
                name="loginId" 
                placeholder="Email or Phone Number" 
                value={formData.loginId} 
                onChange={handleChange} 
                required 
                disabled={isLoading} 
              />
            </div>
            <div className="form-group">
              <input 
                type="password" 
                name="password" 
                placeholder="Password" 
                value={formData.password} 
                onChange={handleChange} 
                required 
                disabled={isLoading} 
              />
            </div>
            <button type="submit" disabled={isLoading} className="auth-button">
              {isLoading ? 'Logging in...' : 'Login'}
            </button>
            <p className="toggle-text">
              New here? <span onClick={() => switchMode('signup')} className="toggle-link">Sign Up</span>
            </p>
          </form>
        )}

        {authMode === 'signup' && (
          <form onSubmit={handleSignup} className="auth-form">
            <div className="form-group">
              <input 
                type="text" 
                name="username" 
                placeholder="Username" 
                value={formData.username} 
                onChange={handleChange} 
                required 
                disabled={isLoading} 
              />
            </div>
            <div className="form-group">
              <input 
                type="email" 
                name="email" 
                placeholder="Email" 
                value={formData.email} 
                onChange={handleChange} 
                required 
                disabled={isLoading} 
              />
            </div>
            <div className="form-group">
              <input 
                type="tel" 
                name="phone" 
                placeholder="Phone Number" 
                value={formData.phone} 
                onChange={handleChange} 
                required 
                disabled={isLoading} 
              />
            </div>
            <div className="form-group">
              <input 
                type="password" 
                name="password" 
                placeholder="Password (min. 6 characters)" 
                value={formData.password} 
                onChange={handleChange} 
                required 
                disabled={isLoading} 
              />
            </div>
            <div className="form-group">
              <input 
                type="password" 
                name="confirmPassword" 
                placeholder="Confirm Password" 
                value={formData.confirmPassword} 
                onChange={handleChange} 
                required 
                disabled={isLoading} 
              />
            </div>
            <button type="submit" disabled={isLoading} className="auth-button">
              {isLoading ? 'Creating Account...' : 'Sign Up'}
            </button>
            <p className="toggle-text">
              Already have an account? <span onClick={() => switchMode('login')} className="toggle-link">Login</span>
            </p>
          </form>
        )}
      </div>
    </div>
  );
}

export default AppWrapper;
