import React, { useState } from "react";
import "./login.css";

function LoginSignup() {
  const [isSignup, setIsSignup] = useState(false);
  const [formData, setFormData] = useState({
    username: "",
    email: "",
    phone: "",
    password: "",
    confirmPassword: ""
  });
  const [error, setError] = useState("");
  const [message, setMessage] = useState("");

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setError("");
    setMessage("");

    if (isSignup) {
      // âœ… Sign up validation
      if (!formData.username || !formData.email || !formData.phone || !formData.password) {
        setError("All fields are required!");
        return;
      }
      if (formData.password !== formData.confirmPassword) {
        setError("Passwords do not match!");
        return;
      }
      setMessage("ðŸŽ‰ Account created successfully! You can now log in.");
      setIsSignup(false);
    } else {
      // âœ… Login validation (mock logic)
      if (!formData.email && !formData.phone) {
        setError("Enter email or phone to login!");
        return;
      }
      if (formData.password.length < 6) {
        setError("Password must be at least 6 characters!");
        return;
      }
      // Example mock condition
      if (formData.email !== "test@example.com" || formData.password !== "123456") {
        setError("No account found. Please sign up first.");
        return;
      }
      setMessage("âœ… Logged in successfully!");
    }
  };

  return (
    <div className="auth-container">
      <div className="auth-box">
        <h1>ðŸŒ™ MoodMate</h1>
        <p>Your mental health companion</p>

        <form onSubmit={handleSubmit} className="auth-form">
          {isSignup && (
            <>
              <input
                type="text"
                name="username"
                placeholder="Username"
                value={formData.username}
                onChange={handleChange}
              />
              <input
                type="email"
                name="email"
                placeholder="Email"
                value={formData.email}
                onChange={handleChange}
              />
              <input
                type="text"
                name="phone"
                placeholder="Phone Number"
                value={formData.phone}
                onChange={handleChange}
              />
            </>
          )}

          {!isSignup && (
            <>
              <input
                type="text"
                name="email"
                placeholder="Email or Phone Number"
                value={formData.email}
                onChange={handleChange}
              />
            </>
          )}

          <input
            type="password"
            name="password"
            placeholder="Password"
            value={formData.password}
            onChange={handleChange}
          />

          {isSignup && (
            <input
              type="password"
              name="confirmPassword"
              placeholder="Confirm Password"
              value={formData.confirmPassword}
              onChange={handleChange}
            />
          )}

          {error && <div className="error-message">{error}</div>}
          {message && <div className="success-message">{message}</div>}

          <button type="submit">{isSignup ? "Sign Up" : "Login"}</button>
        </form>

        {!isSignup && (
          <button
            className="forgot-btn"
            onClick={() => alert("ðŸ”‘ Password reset link sent to your email!")}
          >
            Forgot Password?
          </button>
        )}

        <p className="toggle-text">
          {isSignup ? (
            <>
              Already have an account?{" "}
              <span onClick={() => setIsSignup(false)}>Login</span>
            </>
          ) : (
            <>
              New here? <span onClick={() => setIsSignup(true)}>Sign Up</span>
            </>
          )}
        </p>
      </div>
    </div>
  );
}

export default LoginSignup;
