import React, { useState, useEffect, useRef } from "react";
import "./app.css";

function App() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [isTyping, setIsTyping] = useState(false);
  const [user, setUser] = useState(null);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [showLogin, setShowLogin] = useState(true);
  const [showRegister, setShowRegister] = useState(false);
  const [activeTab, setActiveTab] = useState("chat");
  const [therapists, setTherapists] = useState([]);
  const [resources, setResources] = useState([]);
  const [storeItems, setStoreItems] = useState([]);
  const [isDarkMode, setIsDarkMode] = useState(false);
  const [showCrisis, setShowCrisis] = useState(false);
  const [mood, setMood] = useState("normal");
  const [journalEntries, setJournalEntries] = useState([]);
  const [challenges, setChallenges] = useState([]);
  const [insights, setInsights] = useState(null);

  const chatEndRef = useRef(null);

  useEffect(() => {
    const token = localStorage.getItem('token');
    const userData = localStorage.getItem('user');
    if (token && userData) {
      const userObj = JSON.parse(userData);
      setUser(userObj);
      setIsLoggedIn(true);
      setShowLogin(false);
      loadInitialData();
      loadJournalEntries(userObj.email);
      loadChallenges(userObj.email);
      loadMoodInsights(userObj.email);
    }
  }, []);

  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  useEffect(() => {
    document.body.classList.toggle("dark-mode", isDarkMode);
    if (user && user.theme) {
      document.body.classList.add(`theme-${user.theme}`);
    }
  }, [isDarkMode, user]);

  const loadInitialData = () => {
    setMessages([{
      text: "Hello! I'm MoodMate. How are you feeling today?",
      sender: "bot"
    }]);
  };

  const loadJournalEntries = async (email) => {
    try {
      const response = await fetch(`http://localhost:5000/api/journal_entries?email=${email}`);
      const data = await response.json();
      if (data.success) setJournalEntries(data.entries);
    } catch (error) {
      console.error("Failed to load journal entries:", error);
    }
  };

  const loadChallenges = async (email) => {
    try {
      const response = await fetch(`http://localhost:5000/api/challenges?email=${email}`);
      const data = await response.json();
      if (data.success) setChallenges(data.challenges);
    } catch (error) {
      console.error("Failed to load challenges:", error);
    }
  };

  const loadMoodInsights = async (email) => {
    try {
      const response = await fetch(`http://localhost:5000/api/mood_insights?email=${email}`);
      const data = await response.json();
      if (data.success) setInsights(data);
    } catch (error) {
      console.error("Failed to load insights:", error);
    }
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    const formData = new FormData(e.target);
    const credentials = { email: formData.get('email'), password: formData.get('password') };
    try {
      const response = await fetch("http://localhost:5000/api/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(credentials)
      });
      const data = await response.json();
      if (data.success) {
        localStorage.setItem('token', data.token);
        localStorage.setItem('user', JSON.stringify(data.user));
        setUser(data.user);
        setIsLoggedIn(true);
        setShowLogin(false);
        loadInitialData();
        loadJournalEntries(data.user.email);
        loadChallenges(data.user.email);
        loadMoodInsights(data.user.email);
      } else {
        showPopup("Login Failed", data.error || "Invalid credentials");
      }
    } catch { 
      showPopup("Login Error", "Login failed. Check if the server is running.");
    }
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    const formData = new FormData(e.target);
    const userData = { name: formData.get('name'), email: formData.get('email'), password: formData.get('password') };
    try {
      const response = await fetch("http://localhost:5000/api/register", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(userData)
      });
      const data = await response.json();
      if (data.success) {
        showPopup("Registration Successful", "Account created! You can now login.");
        setShowRegister(false);
        setShowLogin(true);
      } else {
        showPopup("Registration Failed", data.error || "Registration failed");
      }
    } catch { 
      showPopup("Registration Error", "Registration failed. Please try again.");
    }
  };

  const showPopup = (title, message) => {
    // Create a custom popup instead of using alert
    const popup = document.createElement("div");
    popup.className = "custom-popup";
    popup.innerHTML = `
      <div class="popup-content">
        <h3>${title}</h3>
        <p>${message}</p>
        <button onclick="this.parentElement.parentElement.remove()">OK</button>
      </div>
    `;
    document.body.appendChild(popup);
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setUser(null);
    setIsLoggedIn(false);
    setShowLogin(true);
    setMessages([]);
    setJournalEntries([]);
  };

  const sendMessage = async () => {
    if (!input.trim() || !isLoggedIn) return;
    const userMsg = { text: input, sender: "user" };
    setMessages(prev => [...prev, userMsg]);
    setInput("");
    setIsTyping(true);
    try {
      const response = await fetch("http://localhost:5000/api/chat", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ message: input, email: user.email })
      });
      const data = await response.json();
      if (data.success) {
        const botMsg = { 
          text: data.response, 
          sender: "bot",
          mood: data.mood,
          voice: data.voice 
        };
        setMessages(prev => [...prev, botMsg]);
        setMood(data.mood || "normal");
        
        // Update user coins
        setUser(prev => ({ ...prev, coins: data.coins }));
        
        // Play voice response if available
        if (data.voice) {
          const audio = new Audio(`data:audio/mp3;base64,${data.voice}`);
          audio.play().catch(e => console.log("Audio play failed:", e));
        }
      } else {
        setMessages(prev => [...prev, { text: data.error, sender: "bot" }]);
      }
    } catch { 
      setMessages(prev => [...prev, { text: "Connection error.", sender: "bot" }]);
    }
    setIsTyping(false);
  };

  const addJournalEntry = async () => {
    const entryText = prompt("What's on your mind today?");
    if (!entryText) return;
    try {
      const response = await fetch("http://localhost:5000/api/journal", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ content: entryText, email: user.email })
      });
      const data = await response.json();
      if (data.success) {
        setUser(prev => ({ ...prev, coins: data.totalCoins, streak: data.streak }));
        showPopup("Journal Saved", "Entry saved! +3 coins.");
        loadJournalEntries(user.email);
      }
    } catch { 
      showPopup("Journal Error", "Failed to save journal entry.");
    }
  };

  const loadTherapists = async () => {
    try {
      const response = await fetch("http://localhost:5000/api/therapists");
      const data = await response.json();
      setTherapists(data.therapists || []);
      const resResp = await fetch("http://localhost:5000/api/resources");
      const resData = await resResp.json();
      setResources(resData.resources || []);
    } catch (error) { 
      console.error(error);
      showPopup("Load Error", "Failed to load therapists and resources.");
    }
  };

  const loadStore = async () => {
    try {
      const response = await fetch("http://localhost:5000/api/store");
      const data = await response.json();
      setStoreItems(data.items || []);
    } catch (error) { 
      console.error(error);
      showPopup("Load Error", "Failed to load store items.");
    }
  };

  const bookTherapist = async (therapistId) => {
    const therapist = therapists.find(t => t.id === therapistId);
    if (!therapist) return;
    
    if (!window.confirm(`Book ${therapist.name} for ${therapist.price} coins?`)) return;
    try {
      const response = await fetch("http://localhost:5000/api/therapists/book", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ therapist_id: therapistId, email: user.email })
      });
      const data = await response.json();
      if (data.success) {
        setUser(prev => ({ ...prev, coins: data.remainingCoins }));
        showPopup("Booking Successful", data.message);
      } else {
        showPopup("Booking Failed", data.error || "Failed to book therapist.");
      }
    } catch { 
      showPopup("Booking Error", "Failed to book therapist. Please try again.");
    }
  };

  const buyItem = async (itemId) => {
    const item = storeItems.find(i => i.id === itemId);
    if (!item) return;
    
    if (!window.confirm(`Buy ${item.name} for ${item.price} coins?`)) return;
    try {
      const response = await fetch("http://localhost:5000/api/store/buy", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ item_id: itemId, email: user.email })
      });
      const data = await response.json();
      if (data.success) {
        setUser(prev => ({ 
          ...prev, 
          coins: data.remainingCoins, 
          inventory: data.inventory,
          theme: data.theme || prev.theme
        }));
        showPopup("Purchase Successful", "Item purchased successfully!");
      } else {
        showPopup("Purchase Failed", data.error || "Not enough coins.");
      }
    } catch { 
      showPopup("Purchase Error", "Failed to complete purchase. Please try again.");
    }
  };

  const applyTheme = async (theme) => {
    try {
      const response = await fetch("http://localhost:5000/api/apply_theme", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ theme, email: user.email })
      });
      const data = await response.json();
      if (data.success) {
        setUser(prev => ({ ...prev, theme }));
        showPopup("Theme Applied", "Theme applied successfully!");
      } else {
        showPopup("Theme Error", data.error || "Failed to apply theme.");
      }
    } catch { 
      showPopup("Theme Error", "Failed to apply theme. Please try again.");
    }
  };

  const completeChallenge = async (challengeId) => {
    try {
      const response = await fetch("http://localhost:5000/api/complete_challenge", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ challenge_id: challengeId, email: user.email })
      });
      const data = await response.json();
      if (data.success) {
        setUser(prev => ({ ...prev, coins: data.coins }));
        showPopup("Challenge Complete", `You earned ${data.reward} coins!`);
        loadChallenges(user.email);
      }
    } catch { 
      showPopup("Challenge Error", "Failed to complete challenge.");
    }
  };

  if (!isLoggedIn) {
    return (
      <div className="app-container auth-container">
        <div className="auth-box">
          <h1 className="app-title">🌙 MoodMate <span className="ultra-badge">ULTRA</span></h1>
          {showLogin && (
            <form className="auth-form" onSubmit={handleLogin}>
              <input name="email" type="email" placeholder="Email" required />
              <input name="password" type="password" placeholder="Password" required />
              <button className="glow-button">Login</button>
              <p>Don't have an account? <span className="auth-link" onClick={() => { setShowLogin(false); setShowRegister(true); }}>Register</span></p>
            </form>
          )}
          {showRegister && (
            <form className="auth-form" onSubmit={handleRegister}>
              <input name="name" placeholder="Name" required />
              <input name="email" type="email" placeholder="Email" required />
              <input name="password" type="password" placeholder="Password" required />
              <button className="glow-button">Register</button>
              <p>Already have an account? <span className="auth-link" onClick={() => { setShowRegister(false); setShowLogin(true); }}>Login</span></p>
            </form>
          )}
        </div>
      </div>
    );
  }

  return (
    <div className="app-container">
      <header className="app-header">
        <h1 className="app-title">🌙 MoodMate <span className="ultra-badge">ULTRA</span></h1>
        <div className="user-stats">
          <span className="coin-count">🪙 {user.coins}</span>
          <span className="streak-count">🔥 {user.streak}</span>
          <button className="mode-toggle" onClick={() => setIsDarkMode(!isDarkMode)}>
            {isDarkMode ? "☀️" : "🌙"}
          </button>
          <button className="logout-btn" onClick={handleLogout}>Logout</button>
        </div>
      </header>

      <nav className="app-nav">
        <button 
          className={activeTab === "chat" ? "nav-btn active" : "nav-btn"} 
          onClick={() => setActiveTab("chat")}
        >
          💬 Chat
        </button>
        <button 
          className={activeTab === "journal" ? "nav-btn active" : "nav-btn"} 
          onClick={() => setActiveTab("journal")}
        >
          📔 Journal
        </button>
        <button 
          className={activeTab === "therapy" ? "nav-btn active" : "nav-btn"} 
          onClick={() => { setActiveTab("therapy"); loadTherapists(); }}
        >
          🩺 Therapy
        </button>
        <button 
          className={activeTab === "store" ? "nav-btn active" : "nav-btn"} 
          onClick={() => { setActiveTab("store"); loadStore(); }}
        >
          🛒 Store
        </button>
        <button 
          className={activeTab === "challenges" ? "nav-btn active" : "nav-btn"} 
          onClick={() => setActiveTab("challenges")}
        >
          🏆 Challenges
        </button>
        <button 
          className="crisis-btn"
          onClick={() => setShowCrisis(true)}
        >
          🆘 Crisis Help
        </button>
      </nav>

      <main className="app-main">
        {activeTab === "chat" && (
          <div className="chat-tab">
            <div className="chat-box">
              {messages.map((m, i) => (
                <div key={i} className={`message ${m.sender} ${m.mood}`}>
                  <div className="message-bubble">
                    {m.text}
                    {m.sender === "bot" && m.mood && (
                      <span className="mood-indicator">{m.mood}</span>
                    )}
                  </div>
                </div>
              ))}
              {isTyping && (
                <div className="message bot">
                  <div className="typing-indicator">
                    <span></span>
                    <span></span>
                    <span></span>
                  </div>
                </div>
              )}
              <div ref={chatEndRef}></div>
            </div>
            <div className="input-area">
              <input 
                value={input} 
                onChange={e => setInput(e.target.value)} 
                onKeyDown={e => e.key === "Enter" && sendMessage()} 
                placeholder="Type your message..." 
              />
              <button className="send-btn" onClick={sendMessage}>
                Send
              </button>
            </div>
          </div>
        )}

        {activeTab === "journal" && (
          <div className="journal-tab">
            <button className="journal-btn glow-button" onClick={addJournalEntry}>
              + New Entry
            </button>
            
            {insights && (
              <div className="mood-insights">
                <h3>📊 Your Mood Insights</h3>
                <p>{insights.insights}</p>
                {insights.prediction && (
                  <div className="prediction">
                    <strong>Prediction:</strong> {insights.prediction}
                  </div>
                )}
              </div>
            )}
            
            <div className="journal-entries">
              {journalEntries.length > 0 ? (
                journalEntries.map(entry => (
                  <div key={entry.id} className="journal-entry">
                    <div className="entry-date">
                      {new Date(entry.created_at).toLocaleDateString()}
                    </div>
                    <div className="entry-content">{entry.content}</div>
                    <div className="entry-mood">Mood: {entry.mood}</div>
                  </div>
                ))
              ) : (
                <p className="placeholder-text">Your journal entries will appear here</p>
              )}
            </div>
          </div>
        )}

        {activeTab === "therapy" && (
          <div className="therapy-tab">
            <h3>🆘 Crisis Resources</h3>
            <div className="resources-grid">
              {resources.map((r, i) => (
                <div key={i} className="resource-card">
                  <h4>{r.name}</h4>
                  <div className="crisis-number">{r.number}</div>
                  <p>{r.description}</p>
                </div>
              ))}
            </div>
            
            <h3>👨‍⚕️ Therapists</h3>
            <div className="therapists-grid">
              {therapists.map(t => (
                <div key={t.id} className="therapist-card">
                  <img src={`http://localhost:5000${t.photo}`} alt={t.name} className="therapist-photo" />
                  <h4>{t.name}</h4>
                  <p className="specialization">{t.specialization}</p>
                  <p className="therapist-bio">{t.bio}</p>
                  <p className="price">🪙 {t.price} coins</p>
                  <button 
                    className="book-btn"
                    onClick={() => bookTherapist(t.id)}
                    disabled={user.coins < t.price}
                  >
                    {user.coins < t.price ? "Not Enough Coins" : "Book Session"}
                  </button>
                </div>
              ))}
            </div>
          </div>
        )}

        {activeTab === "store" && (
          <div className="store-tab">
            <div className="store-grid">
              {storeItems.map(item => (
                <div key={item.id} className="store-item">
                  <div className="item-preview">
                    <img src={`http://localhost:5000${item.preview}`} alt={item.name} />
                  </div>
                  <h4>{item.name}</h4>
                  <p className="item-price">🪙 {item.price} coins</p>
                  <p className="item-type">{item.type}</p>
                  
                  {user.inventory.includes(item.name) ? (
                    item.type === "theme" ? (
                      <button 
                        className="apply-btn"
                        onClick={() => applyTheme(item.name.toLowerCase().replace(" ", "_"))}
                      >
                        {user.theme === item.name.toLowerCase().replace(" ", "_") ? "Applied" : "Apply"}
                      </button>
                    ) : (
                      <button className="owned-btn" disabled>Owned</button>
                    )
                  ) : (
                    <button 
                      className="buy-btn"
                      onClick={() => buyItem(item.id)}
                      disabled={user.coins < item.price}
                    >
                      {user.coins < item.price ? "Not Enough Coins" : "Buy Now"}
                    </button>
                  )}
                </div>
              ))}
            </div>
          </div>
        )}

        {activeTab === "challenges" && (
          <div className="challenges-tab">
            <h3>🏆 Your Challenges</h3>
            <div className="challenges-grid">
              {challenges.map(challenge => (
                <div key={challenge.id} className="challenge-card">
                  <h4>{challenge.name}</h4>
                  <p>{challenge.description}</p>
                  <div className="progress-bar">
                    <div 
                      className="progress-fill" 
                      style={{width: `${(challenge.progress / challenge.target) * 100}%`}}
                    ></div>
                  </div>
                  <p>{challenge.progress}/{challenge.target} completed</p>
                  <p>Reward: 🪙 {challenge.reward} coins</p>
                  {challenge.completed ? (
                    <button className="completed-btn" disabled>Completed</button>
                  ) : (
                    <button 
                      className="complete-btn"
                      onClick={() => completeChallenge(challenge.id)}
                    >
                      Complete
                    </button>
                  )}
                </div>
              ))}
            </div>
          </div>
        )}
      </main>

      {showCrisis && (
        <div className="crisis-modal">
          <div className="crisis-content">
            <h2>🆘 IMMEDIATE HELP AVAILABLE</h2>
            <p>You're not alone. These resources are available 24/7:</p>
            
            <div className="crisis-resources">
              {resources.map(resource => (
                <div key={resource.name} className="crisis-resource">
                  <h3>{resource.name}</h3>
                  <div className="crisis-number">{resource.number}</div>
                  <p>{resource.description}</p>
                </div>
              ))}
            </div>
            
            <button 
              className="close-crisis"
              onClick={() => setShowCrisis(false)}
            >
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
