import React, { useState, useEffect, useRef } from "react";
import "./app.css";

function App({ user, onLogout, chatHistory = [], currentChat, onNewChat, onLoadChat, onDeleteChat, onUpdateChat }) {
  const [messages, setMessages] = useState(currentChat?.messages || []);
  const [input, setInput] = useState("");
  const [isTyping, setIsTyping] = useState(false);
  const [activeTab, setActiveTab] = useState("chat");
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [showMeditationGuide, setShowMeditationGuide] = useState(false);
  const [breathingActive, setBreathingActive] = useState(false);
  const [breathingPhase, setBreathingPhase] = useState("inhale");
  const [breathingProgress, setBreathingProgress] = useState(0);
  const [exerciseTimer, setExerciseTimer] = useState(0);
  const [exerciseDuration, setExerciseDuration] = useState(0);
  
  const chatEndRef = useRef(null);
  const breathingTimerRef = useRef(null);
  const exerciseTimerRef = useRef(null);
  const inputRef = useRef(null);

  const meditationExercises = [
    {id: 1, title: "4-7-8 Breathing", duration: "5 min", description: "Inhale for 4, hold for 7, exhale for 8", steps: ["Sit comfortably", "Inhale through nose for 4 seconds", "Hold breath for 7 seconds", "Exhale through mouth for 8 seconds", "Repeat 5 times"], image: "ðŸŒ¬ï¸"},
    {id: 2, title: "Box Breathing", duration: "4 min", description: "4x4 square breathing technique", steps: ["Inhale for 4 seconds", "Hold for 4 seconds", "Exhale for 4 seconds", "Hold for 4 seconds", "Repeat 5-10 times"], image: "ðŸ§Š"}
  ];

  // Initialize user data with defaults
  const userData = user || {
    id: 1,
    name: "Demo User",
    coins: 100,
    streak: 0,
    mentalFitness: 85,
    purchasedItems: [],
    ownedThemes: ["default"],
    currentTheme: "default",
    avatar: "default",
    achievements: [],
    loginStreak: 0,
    lastLogin: new Date().toISOString().split('T')[0]
  };

  // Update messages when currentChat changes
  useEffect(() => {
    setMessages(currentChat?.messages || []);
  }, [currentChat]);

  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  useEffect(() => {
    // Clean up timers on unmount
    return () => {
      if (breathingTimerRef.current) {
        clearInterval(breathingTimerRef.current);
      }
      if (exerciseTimerRef.current) {
        clearInterval(exerciseTimerRef.current);
      }
    };
  }, []);

  const sendMessage = async () => {
    if (!input.trim()) return;

    const userMsg = { 
      id: Date.now(),
      text: input, 
      sender: "user",
      timestamp: new Date().toISOString()
    };
    
    setMessages(prev => [...prev, userMsg]);
    setInput("");
    setIsTyping(true);
    
    // Simulate typing
    setTimeout(() => {
      const botMsg = { 
        id: Date.now(),
        text: "I'm here to listen. How does that make you feel?", 
        sender: "bot",
        timestamp: new Date().toISOString()
      };
      setMessages(prev => [...prev, botMsg]);
      setIsTyping(false);
    }, 1000);
  };

  const startBreathingExercise = (exerciseId = 1) => {
    setBreathingActive(true);
    setBreathingPhase("inhale");
    setBreathingProgress(0);
    
    const exercise = meditationExercises.find(e => e.id === exerciseId) || meditationExercises[0];
    
    // Parse duration (e.g., "5 min" -> 300 seconds)
    const durationMinutes = parseInt(exercise.duration);
    setExerciseDuration(durationMinutes * 60);
    setExerciseTimer(durationMinutes * 60);
    
    // Start exercise timer
    exerciseTimerRef.current = setInterval(() => {
      setExerciseTimer(prev => {
        if (prev <= 1) {
          clearInterval(exerciseTimerRef.current);
          stopBreathingExercise();
          return 0;
        }
        return prev - 1;
      });
    }, 1000);
    
    // Start breathing animation - PROPERLY TIMED
    let progress = 0;
    const phaseDuration = 4000; // 4 seconds per phase
    
    breathingTimerRef.current = setInterval(() => {
      progress += 1; // Much slower increment
      if (progress >= 100) {
        progress = 0;
        setBreathingPhase(prev => {
          if (prev === "inhale") return "hold";
          if (prev === "hold") return "exhale";
          return "inhale";
        });
      }
      setBreathingProgress(progress);
    }, phaseDuration / 100); // Update every 40ms for smooth animation
    
    // Add message about the exercise
    setMessages(prev => [...prev, {
      id: Date.now(),
      text: `Let's do the ${exercise.title} exercise. ${exercise.description}. I'll guide you through it...`,
      sender: "bot",
      isExercise: true,
      timestamp: new Date().toISOString()
    }]);
  };

  const stopBreathingExercise = () => {
    setBreathingActive(false);
    if (breathingTimerRef.current) {
      clearInterval(breathingTimerRef.current);
    }
    if (exerciseTimerRef.current) {
      clearInterval(exerciseTimerRef.current);
    }
    
    // Add completion message
    setMessages(prev => [...prev, {
      id: Date.now(),
      text: "âœ… Exercise complete! How do you feel now?",
      sender: "bot",
      timestamp: new Date().toISOString()
    }]);
  };

  // Format time for exercise timer
  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs < 10 ? '0' : ''}${secs}`;
  };

  // Group chats by date for the sidebar
  const groupChatsByDate = () => {
    const groups = {
      today: [],
      yesterday: [],
      week: [],
      month: [],
      older: []
    };
    
    const now = new Date();
    const today = new Date(now.getFullYear(), now.getMonth(), now.getDate());
    const yesterday = new Date(today);
    yesterday.setDate(yesterday.getDate() - 1);
    const weekAgo = new Date(today);
    weekAgo.setDate(weekAgo.getDate() - 7);
    const monthAgo = new Date(today);
    monthAgo.setDate(monthAgo.getDate() - 30);
    
    (chatHistory || []).forEach(chat => {
      const chatDate = new Date(chat.timestamp);
      
      if (chatDate >= today) {
        groups.today.push(chat);
      } else if (chatDate >= yesterday) {
        groups.yesterday.push(chat);
      } else if (chatDate >= weekAgo) {
        groups.week.push(chat);
      } else if (chatDate >= monthAgo) {
        groups.month.push(chat);
      } else {
        groups.older.push(chat);
      }
    });
    
    return groups;
  };

  // Chat History Sidebar Component
  const ChatHistorySidebar = () => {
    if (!sidebarOpen) return null;
    
    const groupedChats = groupChatsByDate();
    
    return (
      <div className="sidebar-overlay" onClick={() => setSidebarOpen(false)}>
        <div className="sidebar chat-history-sidebar" onClick={(e) => e.stopPropagation()}>
          <div className="sidebar-header">
            <h2>Chat History</h2>
            <button onClick={() => setSidebarOpen(false)} className="close-btn">Ã—</button>
          </div>
          
          <div className="new-chat-btn-container">
            <button onClick={() => { onNewChat && onNewChat(); setSidebarOpen(false); }} className="new-chat-btn">
              + New Chat
            </button>
          </div>
          
          <div className="chat-history-list">
            {groupedChats.today.length > 0 && (
              <div className="chat-group">
                <h3>Today</h3>
                {groupedChats.today.map(chat => (
                  <div key={chat.id} className="chat-item">
                    <div className="chat-item-content">
                      <h4>{chat.title}</h4>
                      <p className="chat-preview">{chat.preview}</p>
                      <span className="chat-time">
                        {new Date(chat.timestamp).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                      </span>
                    </div>
                    <button 
                      className="delete-chat-btn"
                      onClick={(e) => {
                        e.stopPropagation();
                        onDeleteChat && onDeleteChat(chat.id);
                      }}
                      title="Delete chat"
                    >
                      Ã—
                    </button>
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>
      </div>
    );
  };

  const MeditationGuideModal = () => {
    if (!showMeditationGuide) return null;
    
    return (
      <div className="modal-overlay">
        <div className="modal-content large">
          <div className="modal-header">
            <h2>ðŸ§˜ Meditation & Breathing Exercises</h2>
            <button onClick={() => setShowMeditationGuide(false)} className="close-btn">Ã—</button>
          </div>
          
          <div className="meditation-grid">
            {meditationExercises.map(exercise => (
              <div key={exercise.id} className="meditation-card">
                <div className="meditation-image">{exercise.image}</div>
                <h3>{exercise.title}</h3>
                <p className="duration">{exercise.duration}</p>
                <p className="description">{exercise.description}</p>
                <div className="steps">
                  {exercise.steps.map((step, i) => (
                    <div key={i} className="step">{i+1}. {step}</div>
                  ))}
                </div>
                <button 
                  onClick={() => {
                    setShowMeditationGuide(false);
                    startBreathingExercise(exercise.id);
                  }}
                  className="start-btn"
                >
                  Start Exercise
                </button>
              </div>
            ))}
          </div>
        </div>
      </div>
    );
  };

  const BreathingExerciseModal = () => {
    if (!breathingActive) return null;
    
    const phaseText = {
      inhale: "Breathe In...",
      hold: "Hold...",
      exhale: "Breathe Out..."
    };
    
    const phaseDescription = {
      inhale: "Fill your lungs completely",
      hold: "Feel the stillness",
      exhale: "Release all tension"
    };
    
    return (
      <div className="breathing-overlay">
        <div className="breathing-container">
          <div className="breathing-circle">
            <div 
              className="breathing-animation"
              style={{ 
                transform: `scale(${breathingPhase === 'inhale' ? 1 + (breathingProgress/100) : 
                  breathingPhase === 'hold' ? 2 : 
                  2 - (breathingProgress/100)})`,
                opacity: breathingPhase === 'hold' ? 0.8 : 0.6
              }}
            ></div>
          </div>
          
          <div className="breathing-instructions">
            <h2>{phaseText[breathingPhase]}</h2>
            <p>{phaseDescription[breathingPhase]}</p>
            <div className="exercise-timer">
              <p>Time remaining: {formatTime(exerciseTimer)}</p>
              <div className="timer-bar">
                <div 
                  className="timer-progress" 
                  style={{ width: `${(exerciseTimer / exerciseDuration) * 100}%` }}
                ></div>
              </div>
            </div>
          </div>
          
          <button onClick={stopBreathingExercise} className="stop-breathing-btn">
            Stop Exercise
          </button>
        </div>
      </div>
    );
  };

  // Quick Actions Menu Component
  const QuickActionsMenu = () => {
    return (
      <div className="quick-actions-menu">
        <h3>Quick Actions</h3>
        <div className="quick-actions-grid">
          <button onClick={() => startBreathingExercise()} className="quick-action-btn">
            <span className="action-icon">ðŸ§˜</span>
            <span>Breathing Exercise</span>
          </button>
          <button onClick={() => setShowMeditationGuide(true)} className="quick-action-btn">
            <span className="action-icon">ðŸŒ¿</span>
            <span>Meditation Guide</span>
          </button>
          <button onClick={() => alert("Mood games would open here")} className="quick-action-btn">
            <span className="action-icon">ðŸŽ®</span>
            <span>Mood Games</span>
          </button>
        </div>
      </div>
    );
  };

  return (
    <div className={`app-container ${userData.currentTheme}-theme`}>
      <MeditationGuideModal />
      <BreathingExerciseModal />
      
      <ChatHistorySidebar />
      
      <header className="app-header">
        <div className="header-left">
          <button onClick={() => setSidebarOpen(true)} className="menu-btn">â˜°</button>
          <h1>ðŸŒ™ MoodMate</h1>
          <div className="user-stats">
            <span className="streak-display">ðŸ”¥ {userData.streak || 0} day streak</span>
            <span>ðŸª™ {userData.coins || 0} coins</span>
          </div>
        </div>
        <div className="header-right">
          <div className="user-info">
            <span className="user-name">{userData.name}</span>
            <span className="user-status">Online</span>
          </div>
          <button onClick={onLogout} className="logout-btn">Logout</button>
        </div>
      </header>

      <nav className="app-nav">
        <button 
          className={activeTab === "chat" ? "active" : ""} 
          onClick={() => setActiveTab("chat")}
        >
          ðŸ’¬ Chat
        </button>
        <button 
          className={activeTab === "journal" ? "active" : ""} 
          onClick={() => setActiveTab("journal")}
        >
          ðŸ““ Journal
        </button>
        <button 
          className={activeTab === "therapy" ? "active" : ""} 
          onClick={() => setActiveTab("therapy")}
        >
          ðŸ‘¨â€âš•ï¸ Therapy
        </button>
        <button 
          className={activeTab === "community" ? "active" : ""} 
          onClick={() => setActiveTab("community")}
        >
          ðŸŒ Community
        </button>
        <button 
          className={activeTab === "shop" ? "active" : ""} 
          onClick={() => setActiveTab("shop")}
        >
          ðŸ›’ Shop
        </button>
        <button 
          className={activeTab === "profile" ? "active" : ""} 
          onClick={() => setActiveTab("profile")}
        >
          ðŸ‘¤ Profile
        </button>
      </nav>

      <main className="app-main">
        {activeTab === "chat" && (
          <div className="chat-tab">
            <div className="chat-header">
              <h2>Chat with MoodMate</h2>
              <div className="chat-actions">
                <button onClick={() => onNewChat && onNewChat()} className="new-chat-btn">
                  + New Chat
                </button>
                <button onClick={() => setShowMeditationGuide(true)} className="breathing-btn">
                  ðŸ§˜ Exercises
                </button>
              </div>
            </div>

            {/* Quick Actions Menu */}
            <QuickActionsMenu />

            <div className="chat-box">
              {messages.map((msg, i) => (
                <div key={`msg-${i}-${msg.timestamp || Date.now()}`} className={`chat-message ${msg.sender} ${msg.isReward ? 'reward' : ''} ${msg.isExercise ? 'exercise' : ''}`}>
                  {msg.sender === "bot" && (
                    <div className="avatar bot-avatar">ðŸ¤–</div>
                  )}
                  {msg.sender === "user" && (
                    <div className="avatar user-avatar">ðŸ‘¤</div>
                  )}
                  <div className="bubble">
                    {msg.text}
                    {msg.moodEmoji && (
                      <div className="mood-indicator">
                        {msg.moodEmoji} {msg.phrase}
                      </div>
                    )}
                  </div>
                </div>
              ))}

              {isTyping && (
                <div className="chat-message bot">
                  <div className="avatar bot-avatar">ðŸ¤–</div>
                  <div className="bubble typing">
                    Thinking...
                  </div>
                </div>
              )}

              <div ref={chatEndRef}></div>
            </div>

            <div className="input-area">
              <input
                type="text"
                placeholder="How are you feeling today?"
                value={input}
                onChange={(e) => setInput(e.target.value)}
                onKeyDown={(e) => e.key === "Enter" && sendMessage()}
                ref={inputRef}
              />
              <button onClick={sendMessage} className="send-btn">Send</button>
              <button onClick={() => setShowMeditationGuide(true)} className="exercise-btn" title="Meditation Exercises">ðŸ§˜â€â™‚ï¸</button>
            </div>
          </div>
        )}

        {activeTab === "journal" && (
          <div className="journal-tab">
            <div className="journal-header">
              <h2>Your Journal</h2>
              <button onClick={() => alert("Journal entry feature")} className="add-entry-btn">
                + New Entry
              </button>
            </div>
            
            <div className="empty-state">
              <p>No journal entries yet. Write your first entry!</p>
              <button onClick={() => alert("Journal entry feature")}>Start Journaling</button>
            </div>
          </div>
        )}

        {activeTab === "therapy" && (
          <div className="therapy-tab">
            <div className="empty-state">
              <p>Therapy features coming soon!</p>
            </div>
          </div>
        )}

        {activeTab === "community" && (
          <div className="community-tab">
            <div className="empty-state">
              <p>Community features coming soon!</p>
            </div>
          </div>
        )}

        {activeTab === "shop" && (
          <div className="shop-tab">
            <div className="empty-state">
              <p>Shop features coming soon!</p>
            </div>
          </div>
        )}

        {activeTab === "profile" && (
          <div className="profile-tab">
            <div className="empty-state">
              <p>Profile features coming soon!</p>
            </div>
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
