import React, { useState } from 'react';

const communitySpaces = [
    { id: 'gratitude', name: 'Daily Gratitude', icon: 'üå∏', description: 'Share what you\'re thankful for.' },
    { id: 'success', name: 'Success Stories', icon: 'üèÜ', description: 'Share your mental health wins.' },
    { id: 'vent', name: 'Vent Anonymously', icon: 'üòî', description: 'A safe space to let it out.' },
    { id: 'mindfulness', name: 'Mindfulness Tips', icon: 'üß†', description: 'Share techniques that work.' }
];

const samplePosts = {
    gratitude: [{ id: 1, user: 'Priya', text: 'Grateful for the warm sun today!', likes: 12 }],
    success: [{ id: 2, user: 'Rohan', text: 'I finally finished my project without feeling overwhelmed!', likes: 25 }],
    vent: [{ id: 3, user: 'Anonymous', text: 'Exams are so stressful right now.', likes: 45 }],
    mindfulness: [{ id: 4, user: 'Aisha', text: 'Tried box breathing today, it really helped calm me down.', likes: 18 }],
};

function Community({ user }) {
    const [activeSpace, setActiveSpace] = useState(communitySpaces[0]);
    const [posts, setPosts] = useState(samplePosts[activeSpace.id]);

    const selectSpace = (space) => {
        setActiveSpace(space);
        setPosts(samplePosts[space.id] || []);
    };

    return (
        <div className="tab-content community-hub">
            <div className="community-sidebar">
                <h3>Community Spaces</h3>
                {communitySpaces.map(space => (
                    <button 
                        key={space.id} 
                        className={`space-btn ${activeSpace.id === space.id ? 'active' : ''}`}
                        onClick={() => selectSpace(space)}
                    >
                        <span className="space-icon">{space.icon}</span>
                        {space.name}
                    </button>
                ))}
            </div>
            <div className="community-feed">
                <div className="feed-header">
                    <h2>{activeSpace.icon} {activeSpace.name}</h2>
                    <p>{activeSpace.description}</p>
                    <button className="create-post-btn">+ Create Post</button>
                </div>
                <div className="post-list">
                    {posts.map(post => (
                        <div key={post.id} className="post-card">
                            <div className="post-header">
                                <div className="post-avatar">{post.user.charAt(0)}</div>
                                <span className="post-username">{post.user}</span>
                            </div>
                            <p className="post-text">{post.text}</p>
                            <div className="post-actions">
                                <button className="like-btn">‚ù§Ô∏è {post.likes}</button>
                                <button className="comment-btn">üí¨ Comment</button>
                            </div>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
}

export default Community;
