import React, { useState, useEffect } from "react";
import "./AppWrapper.css";

function AppWrapper() {
  const [isSignup, setIsSignup] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [formData, setFormData] = useState({
    username: "",
    email: "",
    phone: "",
    password: "",
    confirmPassword: ""
  });
  const [error, setError] = useState("");
  const [message, setMessage] = useState("");
  const [user, setUser] = useState(""); // logged-in username/email

  const API_URL = "http://127.0.0.1:5000"; // your backend

  useEffect(() => {
    // Check if user already logged in
    const loggedInUser = localStorage.getItem("loggedInUser");
    if (loggedInUser) {
      setUser(loggedInUser);
      setIsLoggedIn(true);
    }
  }, []);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setMessage("");

    try {
      if (isSignup) {
        // ✅ Sign up validation
        if (!formData.username || !formData.email || !formData.phone || !formData.password) {
          setError("All fields are required!");
          return;
        }
        if (formData.password !== formData.confirmPassword) {
          setError("Passwords do not match!");
          return;
        }

        const res = await fetch(`${API_URL}/signup`, {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({
            username: formData.username,
            email: formData.email,
            phone: formData.phone,
            password: formData.password
          })
        });

        const data = await res.json();
        if (data.success) {
          setMessage(data.message);
          setIsSignup(false); // go to login
        } else setError(data.message);

      } else {
        // ✅ Login
        if (!formData.email && !formData.phone) {
          setError("Enter email or phone to login!");
          return;
        }
        if (formData.password.length < 6) {
          setError("Password must be at least 6 characters!");
          return;
        }

        const res = await fetch(`${API_URL}/login`, {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({
            loginId: formData.email || formData.phone,
            password: formData.password
          })
        });

        const data = await res.json();
        if (data.success) {
          setMessage(data.message);
          setUser(formData.email || formData.phone);
          setIsLoggedIn(true);
          localStorage.setItem("loggedInUser", formData.email || formData.phone);
        } else setError(data.message);
      }
    } catch (err) {
      setError("⚠️ Server error. Please try again.");
      console.error("Auth error:", err);
    }
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    localStorage.removeItem("loggedInUser");
    setFormData({ username: "", email: "", phone: "", password: "", confirmPassword: "" });
    setMessage("");
    setError("");
  };

  // --- Dashboard after login ---
  if (isLoggedIn) {
    return (
      <div className="dashboard">
        <h1>🌙 MoodMate Dashboard</h1>
        <p>Welcome, {user}!</p>
        <button onClick={handleLogout}>Logout</button>
        {/* Here you can add chat, mood tracker, etc. */}
      </div>
    );
  }

  // --- Login / Signup form ---
  return (
    <div className="auth-container">
      <div className="auth-box">
        <h1>🌙 MoodMate</h1>
        <p>Your mental health companion</p>

        <form onSubmit={handleSubmit} className="auth-form">
          {isSignup && (
            <>
              <input type="text" name="username" placeholder="Username" value={formData.username} onChange={handleChange} />
              <input type="email" name="email" placeholder="Email" value={formData.email} onChange={handleChange} />
              <input type="text" name="phone" placeholder="Phone Number" value={formData.phone} onChange={handleChange} />
            </>
          )}

          {!isSignup && (
            <input type="text" name="email" placeholder="Email or Phone Number" value={formData.email} onChange={handleChange} />
          )}

          <input type="password" name="password" placeholder="Password" value={formData.password} onChange={handleChange} />

          {isSignup && <input type="password" name="confirmPassword" placeholder="Confirm Password" value={formData.confirmPassword} onChange={handleChange} />}

          {error && <div className="error-message">{error}</div>}
          {message && <div className="success-message">{message}</div>}

          <button type="submit">{isSignup ? "Sign Up" : "Login"}</button>
        </form>

        {!isSignup && (
          <button className="forgot-btn" onClick={() => alert("🔑 Password reset link sent to your email!")}>
            Forgot Password?
          </button>
        )}

        <p className="toggle-text">
          {isSignup ? (
            <>Already have an account? <span onClick={() => setIsSignup(false)}>Login</span></>
          ) : (
            <>New here? <span onClick={() => setIsSignup(true)}>Sign Up</span></>
          )}
        </p>
      </div>
    </div>
  );
}

export default AppWrapper;
