import React, { useState, useEffect } from 'react';
import './AICoach.css'; // We will create this new CSS file

// Sample data for demonstration
const allTasks = {
  stress: [
    { id: 1, activity: '5-Min Box Breathing', description: 'Calm your nervous system.', duration: '5 min', icon: '🌬️' },
    { id: 2, activity: 'Mindful Body Scan', description: 'Release physical tension.', duration: '10 min', icon: '🧘' },
  ],
  focus: [
    { id: 3, activity: 'Pomodoro Session', description: 'Work for 25 mins, then 5 min break.', duration: '30 min', icon: '🍅' },
    { id: 4, activity: 'Single-Tasking Practice', description: 'Choose one task and stick to it.', duration: '15 min', icon: '🎯' },
  ],
  sleep: [
    { id: 5, activity: 'Digital Wind-Down', description: 'No screens 30 mins before bed.', duration: '30 min', icon: '📵' },
    { id: 6, activity: 'Sleep Story', description: 'Listen to a calming bedtime story.', duration: '15 min', icon: '📖' },
  ],
  general: [
    { id: 7, activity: 'Morning Affirmation', description: 'Set a positive tone for the day.', duration: '2 min', icon: '🌞' },
    { id: 8, activity: 'Gratitude Journaling', description: 'Write down three good things.', duration: '5 min', icon: '🌙' },
  ]
};

function AICoachModal({ user, onClose, onUpgradeClick }) {
  const [view, setView] = useState('daily_plan'); // 'onboarding', 'daily_plan', 'programs', 'report'
  const [userGoals, setUserGoals] = useState(['stress']); // Default goal
  const [dailyPlan, setDailyPlan] = useState([]);
  
  // This effect generates a new plan for the day
  useEffect(() => {
    const generatePlan = () => {
      let plan = [...allTasks.general]; // Always include general tasks
      userGoals.forEach(goal => {
        if (allTasks[goal]) {
          plan.push(allTasks[goal][Math.floor(Math.random() * allTasks[goal].length)]);
        }
      });
      setDailyPlan(plan.map(task => ({ ...task, completed: false })));
    };
    generatePlan();
  }, [userGoals]);

  const handleToggleComplete = (taskId) => {
    setDailyPlan(currentPlan =>
      currentPlan.map(task =>
        task.id === taskId ? { ...task, completed: !task.completed } : task
      )
    );
  };

  const completedTasks = dailyPlan.filter(task => task.completed).length;
  const progressPercentage = (completedTasks / dailyPlan.length) * 100;
  
 

        <div className="ai-coach-container">
          <div className="ai-coach-progress">
            <h3>Today's Progress</h3>
            <div className="progress-bar-container">
              <div className="progress-bar-fill" style={{ width: `${progressPercentage}%` }}></div>
            </div>
            <p>{completedTasks} / {dailyPlan.length} tasks completed</p>
          </div>

          <div className="ai-coach-plan">
            {dailyPlan.map((item) => (
              <div key={item.id} className={`coach-item ${item.completed ? 'completed' : ''}`}>
                <div className="coach-icon">{item.icon}</div>
                <div className="coach-details">
                  <h3>{item.activity}</h3>
                  <p>{item.description}</p>
                </div>
                <div className="coach-duration">{item.duration}</div>
                <button
                  className={`coach-action-btn ${item.completed ? 'done' : ''}`}
                  onClick={() => handleToggleComplete(item.id)}
                >
                  {item.completed ? '✓ Done' : 'Complete'}
                </button>
              </div>
            ))}
          </div>
        </div>
    
}

export default AICoachModal;
