import React, { useState, useEffect, useRef } from "react";
import "./app.css";

// Base URL for API calls
const API_BASE_URL = 'http://127.0.0.1:5000';

function App({ user, onLogout, chatHistory = [], currentChat, onNewChat, onLoadChat, onDeleteChat, onUpdateChat }) {
  // ... (existing state variables)

  // NEW: Premium status state
  const [isPremium, setIsPremium] = useState(false);
  const [nightMode, setNightMode] = useState(false);
  const [doctorAvailability, setDoctorAvailability] = useState({});
  
  // Sample sleep stories data
  const sleepStories = [
    {id: 1, title: "Calm Beach Night", duration: "8 min", description: "Visualize a peaceful beach at night with gentle waves"},
    {id: 2, title: "Starry Sky Meditation", duration: "10 min", description: "Float among the stars in a peaceful night sky"},
    {id: 3, title: "Mountain Cabin Relaxation", duration: "12 min", description: "Imagine a cozy cabin in the quiet mountains"},
    {id: 4, title: "Forest Walk at Dusk", duration: "15 min", description: "Take a calming walk through a peaceful forest at dusk"}
  ];

  // Sample doctor availability data
  const sampleDoctorAvailability = {
    1: ["Mon 2-5PM", "Wed 10AM-1PM", "Fri 4-7PM"],
    2: ["Tue 9AM-12PM", "Thu 3-6PM", "Sat 11AM-2PM"],
    3: ["Mon 9AM-12PM", "Wed 2-5PM", "Fri 10AM-1PM"],
    4: ["Tue 1-4PM", "Thu 5-8PM", "Sat 3-6PM"],
    5: ["Mon 3-6PM", "Tue 10AM-1PM", "Fri 2-5PM"],
    6: ["Wed 11AM-2PM", "Thu 4-7PM", "Sat 9AM-12PM"]
  };

  // Initialize user data with premium status
  const [userData, setUserData] = useState(user || {
    id: 1,
    name: "Demo User",
    coins: 100,
    streak: 0,
    mentalFitness: 85,
    purchasedItems: [],
    ownedThemes: ["default"],
    currentTheme: "default",
    avatar: "default",
    achievements: [],
    loginStreak: 0,
    lastLogin: new Date().toISOString().split('T')[0],
    isPremium: false // Added premium status
  });

  // ... (existing useEffect hooks and functions)

  useEffect(() => {
    loadInitialData();
    checkDailyLoginBonus();
    // Initialize doctor availability
    setDoctorAvailability(sampleDoctorAvailability);
  }, []);

  // NEW: Function to toggle premium status (for demo purposes)
  const togglePremium = () => {
    setIsPremium(!isPremium);
    setUserData(prev => ({...prev, isPremium: !prev.isPremium}));
    alert(isPremium ? "Premium features disabled" : "Premium features enabled!");
  };

  // NEW: Function to toggle night mode
  const toggleNightMode = () => {
    setNightMode(!nightMode);
  };

  // NEW: Function to play sleep story
  const playSleepStory = (story) => {
    if (!isPremium) {
      alert("Sleep stories are available for premium users only. Upgrade to premium to access this feature.");
      return;
    }
    
    alert(`Playing sleep story: ${story.title}\n${story.description}`);
    // In a real implementation, this would play audio
  };

  // ... (rest of the existing functions)

  // UPDATED: Profile section with improved layout
  const ProfileSection = () => (
    <div className="profile-tab">
      <div className="profile-card">
        <div className="profile-header">
          <div className="profile-avatar">
            <div className="avatar-image">
              {userData.avatar ? userData.avatar.charAt(0) : userData.name?.charAt(0) || 'U'}
            </div>
          </div>
          
          <div className="profile-stats-grid">
            <div className="stat-item">
              <span className="stat-value">{userData.streak || 0}</span>
              <span className="stat-label">Streak</span>
            </div>
            <div className="stat-item">
              <span className="stat-value">{userData.coins || 0}</span>
              <span className="stat-label">Coins</span>
            </div>
            <div className="stat-item">
              <span className="stat-value">{userData.achievements?.filter(a => a.earned).length || 0}</span>
              <span className="stat-label">Badges</span>
            </div>
          </div>
          
          <button className="edit-profile-btn">Edit Profile</button>
        </div>
        
        <div className="premium-section">
          <h3>Account Status</h3>
          <div className={`premium-status ${isPremium ? 'premium' : 'basic'}`}>
            {isPremium ? 'ðŸŒŸ Premium Member' : 'Basic Member'}
            <button onClick={togglePremium} className="upgrade-btn">
              {isPremium ? 'Manage Subscription' : 'Upgrade to Premium'}
            </button>
          </div>
        </div>
        
        {/* Rest of the profile content remains the same */}
      </div>
    </div>
  );

  // UPDATED: Chat message component without hover effects
  const ChatMessage = ({ msg }) => (
    <div className={`chat-message ${msg.sender} ${nightMode ? 'night-mode' : ''}`}>
      {msg.sender === "bot" && (
        <div className="avatar bot-avatar">ðŸ¤–</div>
      )}
      <div className="message-bubble">
        <div className="message-text">{msg.text}</div>
        {msg.moodEmoji && (
          <div className="mood-indicator">
            {msg.moodEmoji} {msg.phrase}
          </div>
        )}
        <div className="message-time">
          {new Date(msg.timestamp).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
        </div>
      </div>
      {msg.sender === "user" && (
        <div className="avatar user-avatar">ðŸ‘¤</div>
      )}
    </div>
  );

  // UPDATED: Sleep Companion with stories
  const SleepCompanionModal = () => {
    if (!showSleepCompanion) return null;
    
    return (
      <div className="modal-overlay">
        <div className="modal-content">
          <div className="modal-header">
            <h2>ðŸŒ™ Sleep Companion</h2>
            <button onClick={() => setShowSleepCompanion(false)} className="close-btn">Ã—</button>
          </div>
          
          <div className="sleep-content">
            <div className="sleep-options">
              <h3>Sleep Stories {!isPremium && <span className="premium-badge">Premium</span>}</h3>
              <div className="stories-list">
                {sleepStories.map(story => (
                  <div key={story.id} className="story-item">
                    <div className="story-info">
                      <h4>{story.title}</h4>
                      <p>{story.description}</p>
                      <span className="story-duration">{story.duration}</span>
                    </div>
                    <button 
                      className={`play-story-btn ${!isPremium ? 'premium-locked' : ''}`}
                      onClick={() => playSleepStory(story)}
                    >
                      {isPremium ? 'Play' : 'ðŸ”’'}
                    </button>
                  </div>
                ))}
              </div>
