  // UPDATED: Mood Report with graphical elements
  const MoodReportModal = () => {
    if (!showMoodReport) return null;
    
    // Only show for premium users
    if (!isPremium) {
      return (
        <div className="modal-overlay">
          <div className="modal-content">
            <div className="modal-header">
              <h2>ðŸ“Š Mood Report</h2>
              <button onClick={() => setShowMoodReport(false)} className="close-btn">Ã—</button>
