    }

    const totalEntries = journalEntries.length;
    const moodCounts = journalEntries.reduce((acc, entry) => {
      acc[entry.mood] = (acc[entry.mood] || 0) + 1;
      return acc;
    }, {});

    // Calculate percentages for chart
    const moodPercentages = {};
    Object.keys(moodCounts).forEach(mood => {
      moodPercentages[mood] = Math.round((moodCounts[mood] / totalEntries) * 100);
    });

    return (
      <div className="modal-overlay">
        <div className="modal-content large">
          <div className="modal-header">
            <h2>ðŸ“Š Mood Report</h2>
            <button onClick={() => setShowMoodReport(false)} className="close-btn">Ã—</button>
          </div>
          
          <div className="report-content">
            <div className="report-summary">
              <h3>Your Mood Overview</h3>
              <p>Based on your <strong>{totalEntries}</strong> journal entries</p>
