                    <div className="legend-item"><span className="color-dot calm"></span> Calm: {moodPercentages.calm || 0}%</div>
                    <div className="legend-item"><span className="color-dot neutral"></span> Neutral: {moodPercentages.neutral || 0}%</div>
                    <div className="legend-item"><span className="color-dot anxious"></span> Anxious: {moodPercentages.anxious || 0}%</div>
                    <div className="legend-item"><span className="color-dot sad"></span> Sad: {moodPercentages.sad || 0}%</div>
