      {id: 23, name: "Gratitude Journal", description: "Special journal covers", price: 30, category: "cosmetic", permanent: true, previewIcon: "ðŸ“”"}
    ];
    
    const allShopItems = [...sampleShopItems, ...additionalShopItems];
    
    return (
