import './AICoachModal.css'; // We will create this dedicated CSS file

function AICoachModal({ user, onClose, onUpgradeClick }) {
  
  // If the user is NOT premium, show the "billion-dollar" upgrade pop-up
  if (!user || !user.is_premium) {
    return (
      <div className="modal-overlay" onClick={onClose}>
        <div className="premium-upsell-box" onClick={(e) => e.stopPropagation()}>
          <button className="close-btn" onClick={onClose}>Ã—</button>
          <div className="lock-icon">ðŸ¤–</div>
          <h3>Unlock Your Personal AI Coach</h3>
          <p>Get dynamic daily plans, multi-day programs, and progress reports by upgrading to Premium.</p>
          <button onClick={onUpgradeClick} className="upgrade-btn">âœ¨ Upgrade Now</button>
        </div>
      </div>
    );
  }

  // If the user IS premium, show the actual feature
  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal-content large" onClick={(e) => e.stopPropagation()}>
        <div className="modal-header">
          <h2>ðŸ¤– AI Wellness Coach</h2>
          <button onClick={onClose} className="close-btn">Ã—</button>
        </div>
        <div className="ai-coach-plan">
          <p>Welcome to your personalized AI Coach experience! Your daily plan is ready.</p>
          {/* The full AI Coach feature would be rendered here */}
        </div>
      </div>
    </div>
  );
}

export default AICoachModal;
