import React, { useState, useMemo } from 'react';

// In src/components/Therapy.jsx

const sampleTherapists = [
    {id: 1, name: "Dr. Arnav Singh", specialization: "Clinical Psychology", price: 799, rating: 5.0, experience: "8 years", avatar: "üë®‚Äç‚öïÔ∏è", languages: ["Hindi", "English"], modes: ["Video", "Chat"], bio: "Specialized in anxiety and depression treatment using evidence-based techniques like CBT."},
    {id: 2, name: "Aaryan Kumar", specialization: "Counseling", price: 599, rating: 4.5, experience: "5 years", avatar: "üë®‚Äçüíº", languages: ["Hindi", "English"], modes: ["Voice", "Chat"], bio: "Focuses on helping young adults navigate career stress and major life transitions."},
    {id: 3, name: "Dr. Ankur Verma", specialization: "Psychiatry", price: 9, rating: 4.8, experience: "12 years", avatar: "üë®‚Äç‚öïÔ∏è", languages: ["English"], modes: ["Video"], bio: "Board-certified psychiatrist with extensive experience in medication management for complex cases."},
    {id: 4, name: "Aanchal Sharma", specialization: "Art Therapy", price: 499, rating: 4.6, experience: "4 years", avatar: "üë©‚Äçüé®", languages: ["Hindi"], modes: ["Video", "Chat"], bio: "Uses creative processes to help clients explore emotions, reduce anxiety, and improve self-esteem."},
    {id: 5, name: "Dr. Aakash Patel", specialization: "Cognitive Behavioral Therapy", price: 899, rating: 4.9, experience: "10 years", avatar: "üë®‚Äçüè´", languages: ["Hindi", "English", "Gujarati"], modes: ["Video", "Chat"], bio: "CBT expert specializing in identifying and changing negative thought patterns that lead to distress."},
    {id: 6, name: "Nitin Rao", specialization: "Mindfulness & Meditation", price: 649, rating: 4.7, experience: "6 years", avatar: "üßò‚Äç‚ôÇÔ∏è", languages: ["English", "Telugu", "Hindi"], modes: ["Video", "Voice"], bio: "Teaches mindfulness techniques to help clients manage stress and improve overall well-being."},
    {id: 7, name: "Dr. Meera Desai", specialization: "Relationship Counseling", price: 850, rating: 4.9, experience: "9 years", avatar: "üë©‚Äç‚öïÔ∏è", languages: ["English", "Marathi"], modes: ["Video"], bio: "Helps couples and individuals navigate relationship challenges and improve communication skills."},
    {id: 8, name: "Kabir Khan", specialization: "Grief & Loss", price: 699, rating: 4.8, experience: "7 years", avatar: "üë®‚Äçüíº", languages: ["Hindi", "English", "Urdu"], modes: ["Voice", "Chat"], bio: "Provides a compassionate space for individuals coping with bereavement and loss."},
    {id: 9, "name": "Dr. Aisha Rahman", specialization: "Student Wellness", price: 550, rating: 4.7, experience: "6 years", avatar: "üë©‚Äçüè´", languages: ["English", "Bengali"], modes: ["Chat", "Video"], bio: "Specializes in academic pressure, exam anxiety, and the unique challenges faced by students."},
    {id: 10, name: "Sameer Joshi", specialization: "Affirming Therapy", price: 750, rating: 5.0, experience: "8 years", avatar: "üë®‚Äç‚öïÔ∏è", languages: ["English", "Hindi"], modes: ["Video", "Chat"], bio: "Provides an affirming and supportive environment for individuals to explore their identity and mental health."}
];

function Therapy({ user, onUpdateUser }) {
    const [therapists, setTherapists] = useState(sampleTherapists);
    const [filters, setFilters] = useState({ specialization: 'All', language: 'All', mode: 'All', sort: 'rating' });
    const [selectedTherapist, setSelectedTherapist] = useState(null);

    const handleFilterChange = (e) => {
        setFilters(prev => ({ ...prev, [e.target.name]: e.target.value }));
    };

    const filteredAndSortedTherapists = useMemo(() => {
        return therapists
            .filter(t => filters.specialization === 'All' || t.specialization === filters.specialization)
            .filter(t => filters.language === 'All' || t.languages.includes(filters.language))
            .filter(t => filters.mode === 'All' || t.modes.includes(filters.mode))
            .sort((a, b) => {
                if (filters.sort === 'price_asc') return a.price - b.price;
                if (filters.sort === 'price_desc') return b.price - a.price;
                return b.rating - a.rating; // Default to highest rating
            });
    }, [therapists, filters]);

    return (
        <div className="tab-content therapy-hub">
            <div className="therapy-header">
                <h2>Find Your Therapist</h2>
                <p>Connect with certified professionals tailored to your needs.</p>
            </div>

            <div className="filter-bar">
                <select name="specialization" value={filters.specialization} onChange={handleFilterChange}>
                    <option value="All">All Specializations</option>
                    <option value="Clinical Psychology">Clinical Psychology</option>
                    <option value="Counseling">Counseling</option>
                    <option value="Psychiatry">Psychiatry</option>
                    <option value="Art Therapy">Art Therapy</option>
                </select>
                <select name="language" value={filters.language} onChange={handleFilterChange}>
                    <option value="All">All Languages</option>
                    <option value="English">English</option>
                    <option value="Hindi">Hindi</option>
                </select>
                <select name="mode" value={filters.mode} onChange={handleFilterChange}>
                    <option value="All">All Modes</option>
                    <option value="Video">Video</option>
                    <option value="Voice">Voice</option>
                    <option value="Chat">Chat</option>
                </select>
                <select name="sort" value={filters.sort} onChange={handleFilterChange}>
                    <option value="rating">Sort by Rating</option>
                    <option value="price_asc">Price: Low to High</option>
                    <option value="price_desc">Price: High to Low</option>
                </select>
            </div>

            <div className="therapists-grid">
                {filteredAndSortedTherapists.map((therapist) => (
                    <div key={therapist.id} className="therapist-card">
                        <div className="therapist-card-header">
                            <div className="therapist-avatar">{therapist.avatar}</div>
                            <div className="therapist-header-info">
                                <h4>{therapist.name}</h4>
                                <p>{therapist.specialization}</p>
                            </div>
                        </div>
                        <div className="therapist-card-body">
                            <p className="therapist-bio">"{therapist.bio}"</p>
                            <div className="therapist-tags">
                                {therapist.languages.map(lang => <span key={lang}>{lang}</span>)}
                                {therapist.modes.map(mode => <span key={mode}>{mode}</span>)}
                            </div>
                        </div>
                        <div className="therapist-card-footer">
                            <span className="therapist-rating">‚≠ê {therapist.rating}</span>
                            <span className="therapist-price">ü™ô {therapist.price}</span>
                            <button className="book-btn" disabled={user.coins < therapist.price}>Book</button>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default Therapy;
