import React, { useState, useEffect, useRef } from 'react';
import './Chat.css';

function Chat({ user }) {
    const [messages, setMessages] = useState([]);
    const [input, setInput] = useState("");
    const [isTyping, setIsTyping] = useState(false);
    const chatEndRef = useRef(null);

    useEffect(() => {
        if (user && user.username) {
            setMessages([{
                id: Date.now(),
                text: `Hello ${user.username}! I'm MoodMate. How can I help you today?`,
                sender: 'bot',
                timestamp: new Date()
            }]);
        }
    }, [user]);

    useEffect(() => {
        chatEndRef.current?.scrollIntoView({ behavior: "smooth" });
    }, [messages]);

    const sendMessage = async () => {
        if (!input.trim()) return;

        const userMessage = { id: Date.now(), text: input, sender: 'user', timestamp: new Date() };
        setMessages(prev => [...prev, userMessage]);
        setInput("");
        setIsTyping(true);

        try {
            const response = await fetch(`/api/chat`, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ message: input })
            });

            if (!response.ok) {
                const errData = await response.json().catch(() => ({}));
                throw new Error(errData.error || `HTTP error! status: ${response.status}`);
            }

            const data = await response.json();
            const botMessage = { id: Date.now() + 1, text: data.reply, sender: 'bot', timestamp: new Date() };
            setMessages(prev => [...prev, botMessage]);

        } catch (error) {
            console.error("Chat error:", error);
            const errorMsg = { id: Date.now() + 1, text: "⚠️ Cannot connect. Please try again.", sender: 'bot', timestamp: new Date() };
            setMessages(prev => [...prev, errorMsg]);
        } finally {
            setIsTyping(false);
        }
    };

    const formatTime = (timestamp) => {
        if (!timestamp) return '';
        const date = timestamp instanceof Date ? timestamp : new Date(timestamp);
        return date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
    };

    return (
        <div className="chat-fullpage-container">
            <header className="chat-header">
                <h1>🌙 MoodMate</h1>
                <p>Online - Ready to listen</p>
            </header>

            <div className="chat-messages">
                {messages.map((msg) => (
                    <div key={msg.id} className={`chat-message ${msg.sender}`}>
                        <div className="bubble">
                            {msg.text}
                            <div className="message-time">{formatTime(msg.timestamp)}</div>
                        </div>
                    </div>
                ))}

                {isTyping && (
                    <div className="chat-message bot typing">
                        <div className="bubble"><span>.</span><span>.</span><span>.</span></div>
                    </div>
                )}

                <div ref={chatEndRef} />
            </div>

            <div className="chat-input-area">
                <input
                    type="text"
                    value={input}
                    onChange={(e) => setInput(e.target.value)}
                    onKeyDown={(e) => e.key === 'Enter' && sendMessage()}
                    placeholder="Type your message here..."
                />
                <button onClick={sendMessage} disabled={isTyping}>
                    {isTyping ? '...' : '➤'}
                </button>
            </div>
        </div>
    );
}

export default Chat;
