  const sendMessage = async () => {
    if (!input.trim() || !isLoggedIn) return;

    const userMsg = { text: input, sender: "user" };
    setMessages(prev => [...prev, userMsg]);
    setInput("");
    setIsTyping(true);
    
    // Check for emergency keywords
    const emergencyKeywords = ['suicide', 'kill myself', 'end it all', 'want to die', 'self harm', 'hurt myself'];
    const hasEmergency = emergencyKeywords.some(keyword => 
      input.toLowerCase().includes(keyword)
    );
    
    if (hasEmergency) {
      setShowEmergencyModal(true);
      setIsTyping(false);
      return;
    }
    
    // Simulate typing animation
    simulateTyping();

    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5000/api/chat', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': token ? `Bearer ${token}` : ''
        },
        body: JSON.stringify({ message: input })
      });

      let data;
      if (response.ok) {
        data = await response.json();
      } else {
        // Fallback to mock response if backend is not available
        data = {
          reply: "I'm here for you. Would you like to talk more about how you're feeling?",
          mood: "neutral",
          moodEmoji: "ðŸ˜Š",
          phrase: "You're doing great. Keep going!",
