/**
 * MoodMate - Complete AI Mental Health Companion Frontend
 * =======================================================
 * 
 * A comprehensive React frontend for MoodMate featuring:
 * - Gemini Ultra AI integration with natural conversations
 * - Advanced memory and chat history management
 * - Streak tracking with rewards and gamification
 * - Real-time typing animations for AI responses
 * - Glassmorphism modern UI with smooth animations
 * - Auto-scroll chat functionality
 * - Complete authentication system
 * - Anonymous community wall for peer support
 * - Music/YouTube/Spotify mood-based suggestions
 * - Dynamic theming based on user moods
 * - Mini healing games integration
 * - Full Hinglish conversation support
 * - Comprehensive mood tracking and analytics
 * - Crisis detection and intervention features
 * 
 * This frontend provides a complete user interface for all
 * MoodMate features with advanced React patterns, hooks,
 * and state management for optimal user experience.
 * 
 * Architecture:
 * - React 18+ with functional components and hooks
 * - Custom state management with useReducer and Context
 * - Real-time UI updates with WebSocket-style polling
 * - Responsive design optimized for desktop usage
 * - Advanced animation system with CSS transitions
 * - Comprehensive error handling and user feedback
 * - Accessibility features and keyboard navigation
 * 
 * Author: MoodMate Development Team
 * Version: 2.0.0
 * Created: August 2025
 */

// React core imports for component functionality
import React, { 
    useState, 
    useEffect, 
    useReducer, 
    useRef, 
    useCallback, 
    useMemo,
    createContext,
    useContext,
    Suspense,
    memo
} from 'react';

// Additional React utilities for advanced features
import { 
    createPortal 
} from 'react-dom';

/**
 * ========================================================================
 * GLOBAL STATE MANAGEMENT AND CONTEXT
 * ========================================================================
 */

// Main application state context for global data management
const AppContext = createContext();

// Authentication context for user session management
const AuthContext = createContext();

// Chat context for conversation state management
const ChatContext = createContext();

// Theme context for dynamic theming and mood-based styling
const ThemeContext = createContext();

/**
 * Initial application state structure
 * Comprehensive state management for all MoodMate features
 */
const initialAppState = {
    // User authentication and profile data
    user: null,
    isAuthenticated: false,
    isLoading: true,

    // Chat and conversation management
    currentSession: null,
    messages: [],
    isTyping: false,
    typingText: '',

    // UI state and navigation
    activeTab: 'chat',
    sidebarCollapsed: false,
    modalOpen: null,
    theme: 'mood_adaptive',

    // Gamification and progress tracking
    coins: 0,
    streak: 0,
    level: 1,
    experience: 0,

    // Community and social features
    communityPosts: [],
    notifications: [],

    // Mini games and challenges
    activeGame: null,
    gameScores: [],
    challenges: [],

    // Mood tracking and analytics
    moodHistory: [],
    currentMood: 'neutral',
    analytics: {},

    // Music and media suggestions
    musicSuggestions: [],
    currentlyPlaying: null,

    // Error handling and system status
    errors: [],
    systemStatus: 'healthy',
    connectionStatus: 'connected'
};

/**
 * Main application state reducer
 * Handles all state updates with proper immutability
 */
function appStateReducer(state, action) {
    switch (action.type) {
        // Authentication actions
        case 'LOGIN_SUCCESS':
            return {
                ...state,
                user: action.payload.user,
                isAuthenticated: true,
                isLoading: false,
                coins: action.payload.user.total_coins,
                streak: action.payload.user.current_streak,
                level: action.payload.user.level,
                experience: action.payload.user.experience_points
            };

        case 'LOGOUT':
            return {
                ...initialAppState,
                isLoading: false
            };

        case 'UPDATE_USER_STATS':
            return {
                ...state,
                coins: action.payload.coins,
                streak: action.payload.streak,
                level: action.payload.level,
                experience: action.payload.experience
            };

        // Chat and messaging actions
        case 'ADD_MESSAGE':
            return {
                ...state,
                messages: [...state.messages, action.payload]
            };

        case 'SET_MESSAGES':
            return {
                ...state,
                messages: action.payload
            };

        case 'START_TYPING':
            return {
                ...state,
                isTyping: true,
                typingText: ''
            };

        case 'UPDATE_TYPING':
            return {
                ...state,
                typingText: action.payload
            };

        case 'STOP_TYPING':
            return {
                ...state,
                isTyping: false,
                typingText: ''
            };

        case 'SET_CURRENT_SESSION':
            return {
                ...state,
                currentSession: action.payload
            };

        // UI and navigation actions
        case 'SET_ACTIVE_TAB':
            return {
                ...state,
                activeTab: action.payload
            };

        case 'TOGGLE_SIDEBAR':
            return {
                ...state,
                sidebarCollapsed: !state.sidebarCollapsed
            };

        case 'OPEN_MODAL':
            return {
                ...state,
                modalOpen: action.payload
            };

        case 'CLOSE_MODAL':
            return {
                ...state,
                modalOpen: null
            };

        case 'SET_THEME':
            return {
                ...state,
                theme: action.payload
            };

        // Community and social actions
        case 'SET_COMMUNITY_POSTS':
            return {
                ...state,
                communityPosts: action.payload
            };

        case 'ADD_COMMUNITY_POST':
            return {
                ...state,
                communityPosts: [action.payload, ...state.communityPosts]
            };

        case 'ADD_NOTIFICATION':
            return {
                ...state,
                notifications: [action.payload, ...state.notifications.slice(0, 9)]
            };
        case 'REMOVE_NOTIFICATION':
            return {
                ...state,
                notifications: state.notifications.filter(notification => notification.id !== action.payload)
            };

        // Mood and analytics actions
        case 'SET_CURRENT_MOOD':
            return {
                ...state,
                currentMood: action.payload
            };

        case 'SET_MOOD_HISTORY':
            return {
                ...state,
                moodHistory: action.payload
            };

        case 'SET_ANALYTICS':
            return {
                ...state,
                analytics: action.payload
            };

        // Music and media actions
        case 'SET_MUSIC_SUGGESTIONS':
            return {
                ...state,
                musicSuggestions: action.payload
            };

        case 'SET_CURRENTLY_PLAYING':
            return {
                ...state,
                currentlyPlaying: action.payload
            };

        // Game and challenge actions
        case 'SET_ACTIVE_GAME':
            return {
                ...state,
                activeGame: action.payload
            };

        case 'ADD_GAME_SCORE':
            return {
                ...state,
                gameScores: [action.payload, ...state.gameScores.slice(0, 49)]
            };

        case 'SET_CHALLENGES':
            return {
                ...state,
                challenges: action.payload
            };

        // Error handling and system actions
        case 'ADD_ERROR':
            return {
                ...state,
                errors: [action.payload, ...state.errors.slice(0, 4)]
            };

        case 'REMOVE_ERROR':
            return {
                ...state,
                errors: state.errors.filter(error => error.id !== action.payload)
            };

        case 'SET_LOADING':
            return {
                ...state,
                isLoading: action.payload
            };

        case 'SET_CONNECTION_STATUS':
            return {
                ...state,
                connectionStatus: action.payload
            };

        default:
            console.warn('Unknown action type:', action.type);
            return state;
    }
}

/**
 * ========================================================================
 * CUSTOM HOOKS FOR ADVANCED FUNCTIONALITY
 * ========================================================================
 */

/**
 * Main application context provider hook
 * Provides global state and dispatch functions to all components
 */
function useAppState() {
    const context = useContext(AppContext);
    if (!context) {
        throw new Error('useAppState must be used within AppStateProvider');
    }
    return context;
}

/**
 * API communication hook with error handling and retry logic
 * Centralized HTTP client with authentication and error management
 */
function useAPI() {
    const { dispatch } = useAppState();

    const apiCall = useCallback(async (endpoint, options = {}) => {
        try {
            // Set default options with authentication
            const defaultOptions = {
                method: 'GET',
                headers: {
                    'Content-Type': 'application/json',
                },
                credentials: 'include', // Include cookies for session auth
                ...options
            };

            // Make API request
            const response = await fetch(`http://127.0.0.1:5000/api${endpoint}`, defaultOptions);

            // Handle HTTP errors
            if (!response.ok) {
                const errorData = await response.json().catch(() => ({}));
                throw new Error(errorData.message || `HTTP ${response.status} ${response.statusText}`);
            }

            // Parse response data
            const data = await response.json();
            return data;

        } catch (error) {
            // Add error to global state for user feedback
            dispatch({
                type: 'ADD_ERROR',
                payload: {
                    id: Date.now(),
                    message: error.message,
                    type: 'api_error',
                    timestamp: new Date().toISOString()
                }
            });

            // Re-throw error for component handling
            throw error;
        }
    }, [dispatch]);

    return { apiCall };
}

/**
 * Real-time typing animation hook
 * Creates typewriter effect for AI responses with customizable speed
 */
function useTypingAnimation(text, speed = 50, startDelay = 200) {
    const [displayedText, setDisplayedText] = useState('');
    const [isTyping, setIsTyping] = useState(false);
    const timeoutRef = useRef(null);
    const indexRef = useRef(0);

    useEffect(() => {
        if (!text) {
            setDisplayedText('');
            setIsTyping(false);
            return;
        }

        // Reset animation state
        setDisplayedText('');
        setIsTyping(true);
        indexRef.current = 0;

        // Start typing after delay
        const startTyping = () => {
            const typeNextChar = () => {
                if (indexRef.current < text.length) {
                    setDisplayedText(text.slice(0, indexRef.current + 1));
                    indexRef.current++;
                    timeoutRef.current = setTimeout(typeNextChar, speed);
                } else {
                    setIsTyping(false);
                }
            };

            timeoutRef.current = setTimeout(typeNextChar, startDelay);
        };

        startTyping();

        // Cleanup timeout on unmount or text change
        return () => {
            if (timeoutRef.current) {
                clearTimeout(timeoutRef.current);
            }
        };
    }, [text, speed, startDelay]);

    return { displayedText, isTyping };
}

/**
 * Auto-scroll hook for chat messages
 * Automatically scrolls to bottom when new messages arrive
 */
function useAutoScroll(dependency, behavior = 'smooth') {
    const scrollRef = useRef(null);

    useEffect(() => {
        if (scrollRef.current) {
            scrollRef.current.scrollTo({
                top: scrollRef.current.scrollHeight,
                behavior: behavior
            });
        }
    }, [dependency, behavior]);

    return scrollRef;
}

/**
 * Local storage hook with JSON serialization
 * Persists state to localStorage with automatic serialization
 */
function useLocalStorage(key, defaultValue) {
    const [value, setValue] = useState(() => {
        try {
            const item = window.localStorage.getItem(key);
            return item ? JSON.parse(item) : defaultValue;
        } catch (error) {
            console.warn(`Error reading localStorage key "${key}":`, error);
            return defaultValue;
        }
    });

    const setStoredValue = useCallback((newValue) => {
        try {
            setValue(newValue);
            window.localStorage.setItem(key, JSON.stringify(newValue));
        } catch (error) {
            console.warn(`Error setting localStorage key "${key}":`, error);
        }
    }, [key]);

    return [value, setStoredValue];
}

/**
 * Mood detection hook with sentiment analysis
 * Analyzes text input to detect mood and emotional state
 */
function useMoodDetection() {
    const detectMood = useCallback((text) => {
        if (!text) return { mood: 'neutral', confidence: 0, keywords: [] };

        const moodPatterns = {
            happy: ['happy', 'joy', 'excited', 'great', 'awesome', 'amazing', 'wonderful', 'fantastic', 'love', 'khushi', 'mazaa', 'badhiya'],
            sad: ['sad', 'depressed', 'down', 'upset', 'crying', 'tears', 'lonely', 'dukhi', 'pareshaan', 'udaas'],
            anxious: ['anxious', 'worried', 'nervous', 'scared', 'panic', 'stress', 'tension', 'fear', 'pareshani', 'dar'],
            angry: ['angry', 'mad', 'furious', 'irritated', 'annoyed', 'rage', 'hate', 'gussa', 'naraz'],
            excited: ['excited', 'thrilled', 'pumped', 'energetic', 'motivated', 'enthusiastic', 'josh'],
            calm: ['calm', 'peaceful', 'relaxed', 'serene', 'tranquil', 'zen', 'shanti', 'sukoon']
        };

        const textLower = text.toLowerCase();
        const words = textLower.split(/\s+/);
        const moodScores = {};
        const foundKeywords = [];

        // Calculate mood scores based on keyword matches
        Object.entries(moodPatterns).forEach(([mood, keywords]) => {
            let score = 0;
            keywords.forEach(keyword => {
                const count = (textLower.match(new RegExp(`\\b${keyword}\\b`, 'g')) || []).length;
                if (count > 0) {
                    score += count;
                    foundKeywords.push(keyword);
                }
            });
            moodScores[mood] = score;
        });

        // Find dominant mood
        const totalMatches = Object.values(moodScores).reduce((sum, score) => sum + score, 0);

        if (totalMatches === 0) {
            return { mood: 'neutral', confidence: 0, keywords: [] };
        }

        const dominantMood = Object.entries(moodScores).reduce((a, b) => 
            moodScores[a[0]] > moodScores[b[0]] ? a : b
        )[0];

        const confidence = Math.min(moodScores[dominantMood] / words.length * 10, 1);

        return {
            mood: dominantMood,
            confidence: Math.round(confidence * 100) / 100,
            keywords: foundKeywords.slice(0, 5),
            allScores: moodScores
        };
    }, []);

    return { detectMood };
}

