/**
 * Login Form Component
 * Handles user authentication with validation and error handling
 */
const LoginForm = memo(({ onSuccess, onSwitchToRegister }) => {
    const [formData, setFormData] = useState({
        username: '',
        password: ''
    });
    const [isLoading, setIsLoading] = useState(false);
    const [errors, setErrors] = useState({});
    const { apiCall } = useAPI();

    // Handle input changes with real-time validation
    const handleInputChange = useCallback((e) => {
        const { name, value } = e.target;
        setFormData(prev => ({ ...prev, [name]: value }));

        // Clear field-specific errors on change
        if (errors[name]) {
            setErrors(prev => ({ ...prev, [name]: '' }));
        }
    }, [errors]);

    // Form validation logic
    const validateForm = useCallback(() => {
        const newErrors = {};

        if (!formData.username.trim()) {
            newErrors.username = 'Username is required';
        } else if (formData.username.length < 3) {
            newErrors.username = 'Username must be at least 3 characters';
        }

        if (!formData.password) {
            newErrors.password = 'Password is required';
        } else if (formData.password.length < 6) {
            newErrors.password = 'Password must be at least 6 characters';
        }

        setErrors(newErrors);
        return Object.keys(newErrors).length === 0;
    }, [formData]);

    // Handle form submission with API call
    const handleSubmit = useCallback(async (e) => {
        e.preventDefault();

        if (!validateForm()) {
            return;
        }

        setIsLoading(true);

        try {
            const response = await apiCall('/auth/login', {
                method: 'POST',
                body: JSON.stringify(formData)
            });

            if (response.success) {
                onSuccess(response.data);
            } else {
                setErrors({ submit: response.message || 'Login failed' });
            }
        } catch (error) {
            setErrors({ submit: error.message || 'Login failed. Please try again.' });
        } finally {
            setIsLoading(false);
        }
    }, [formData, validateForm, apiCall, onSuccess]);

    return (
        <div className="auth-form login-form">
            <div className="auth-header">
                <h2 className="auth-title">Welcome Back to MoodMate</h2>
                <p className="auth-subtitle">Sign in to continue your mental wellness journey</p>
            </div>

            <form onSubmit={handleSubmit} className="auth-form-content">
                <div className="form-group">
                    <label htmlFor="username" className="form-label">
                        Username or Email
                    </label>
                    <input
                        type="text"
                        id="username"
                        name="username"
                        value={formData.username}
                        onChange={handleInputChange}
                        className={`form-input ${errors.username ? 'error' : ''}`}
                        placeholder="Enter your username or email"
                        disabled={isLoading}
                        autoComplete="username"
                    />
                    {errors.username && (
                        <span className="form-error">{errors.username}</span>
                    )}
                </div>

                <div className="form-group">
                    <label htmlFor="password" className="form-label">
                        Password
                    </label>
                    <input
                        type="password"
                        id="password"
                        name="password"
                        value={formData.password}
                        onChange={handleInputChange}
                        className={`form-input ${errors.password ? 'error' : ''}`}
                        placeholder="Enter your password"
                        disabled={isLoading}
                        autoComplete="current-password"
                    />
                    {errors.password && (
                        <span className="form-error">{errors.password}</span>
                    )}
                </div>

                {errors.submit && (
                    <div className="form-error-message">
                        {errors.submit}
                    </div>
                )}

                <button
                    type="submit"
                    className="btn btn-primary btn-auth"
                    disabled={isLoading}
                >
                    {isLoading ? (
                        <span className="btn-loading">
                            <span className="loading-spinner"></span>
                            Signing in...
                        </span>
                    ) : (
                        'Sign In'
                    )}
                </button>

                <div className="auth-switch">
                    <p>
                        Don't have an account?{' '}
                        <button
                            type="button"
                            onClick={onSwitchToRegister}
