/**
 * Registration Form Component
 * Handles new user account creation with comprehensive validation
 */
const RegisterForm = memo(({ onSuccess, onSwitchToLogin }) => {
    const [formData, setFormData] = useState({
        username: '',
        email: '',
        password: '',
        confirmPassword: '',
        displayName: '',
        acceptTerms: false
    });
    const [isLoading, setIsLoading] = useState(false);
    const [errors, setErrors] = useState({});
    const [passwordStrength, setPasswordStrength] = useState(0);
    const { apiCall } = useAPI();

    // Password strength calculation
    const calculatePasswordStrength = useCallback((password) => {
        let strength = 0;
        if (password.length >= 8) strength += 1;
        if (/[A-Z]/.test(password)) strength += 1;
        if (/[a-z]/.test(password)) strength += 1;
        if (/[0-9]/.test(password)) strength += 1;
        if (/[^A-Za-z0-9]/.test(password)) strength += 1;
        return strength;
    }, []);

    // Handle input changes with real-time validation
    const handleInputChange = useCallback((e) => {
        const { name, value, type, checked } = e.target;
        const newValue = type === 'checkbox' ? checked : value;

        setFormData(prev => ({ ...prev, [name]: newValue }));

        // Real-time password strength calculation
        if (name === 'password') {
            setPasswordStrength(calculatePasswordStrength(value));
        }

        // Clear field-specific errors on change
        if (errors[name]) {
            setErrors(prev => ({ ...prev, [name]: '' }));
        }
    }, [errors, calculatePasswordStrength]);

    // Comprehensive form validation
    const validateForm = useCallback(() => {
        const newErrors = {};

        // Username validation
        if (!formData.username.trim()) {
            newErrors.username = 'Username is required';
        } else if (formData.username.length < 3) {
            newErrors.username = 'Username must be at least 3 characters';
        } else if (!/^[a-zA-Z0-9_]+$/.test(formData.username)) {
            newErrors.username = 'Username can only contain letters, numbers, and underscores';
        }

        // Email validation
        if (!formData.email.trim()) {
            newErrors.email = 'Email is required';
        } else if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(formData.email)) {
            newErrors.email = 'Please enter a valid email address';
        }

        // Display name validation
        if (!formData.displayName.trim()) {
            newErrors.displayName = 'Display name is required';
        } else if (formData.displayName.length < 2) {
            newErrors.displayName = 'Display name must be at least 2 characters';
        }

        // Password validation
        if (!formData.password) {
            newErrors.password = 'Password is required';
        } else if (formData.password.length < 6) {
            newErrors.password = 'Password must be at least 6 characters';
        }

        // Confirm password validation
        if (!formData.confirmPassword) {
            newErrors.confirmPassword = 'Please confirm your password';
        } else if (formData.password !== formData.confirmPassword) {
            newErrors.confirmPassword = 'Passwords do not match';
        }

        // Terms acceptance validation
        if (!formData.acceptTerms) {
            newErrors.acceptTerms = 'You must accept the terms and conditions';
        }

        setErrors(newErrors);
        return Object.keys(newErrors).length === 0;
    }, [formData]);

    // Handle form submission
    const handleSubmit = useCallback(async (e) => {
        e.preventDefault();

        if (!validateForm()) {
            return;
        }

        setIsLoading(true);

        try {
            const response = await apiCall('/auth/register', {
                method: 'POST',
                body: JSON.stringify({
                    username: formData.username,
                    email: formData.email,
                    password: formData.password,
                    display_name: formData.displayName
                })
            });

            if (response.success) {
                onSuccess(response.data);
            } else {
                setErrors({ submit: response.message || 'Registration failed' });
            }
        } catch (error) {
            setErrors({ submit: error.message || 'Registration failed. Please try again.' });
        } finally {
            setIsLoading(false);
        }
    }, [formData, validateForm, apiCall, onSuccess]);

    return (
        <div className="auth-form register-form">
            <div className="auth-header">
                <h2 className="auth-title">Join MoodMate</h2>
                <p className="auth-subtitle">Start your journey to better mental wellness</p>
            </div>

            <form onSubmit={handleSubmit} className="auth-form-content">
                <div className="form-row">
                    <div className="form-group">
                        <label htmlFor="displayName" className="form-label">
                            Display Name
                        </label>
                        <input
                            type="text"
                            id="displayName"
                            name="displayName"
                            value={formData.displayName}
                            onChange={handleInputChange}
                            className={`form-input ${errors.displayName ? 'error' : ''}`}
                            placeholder="Your display name"
                            disabled={isLoading}
                            autoComplete="name"
                        />
                        {errors.displayName && (
                            <span className="form-error">{errors.displayName}</span>
                        )}
                    </div>

                    <div className="form-group">
                        <label htmlFor="username" className="form-label">
                            Username
                        </label>
                        <input
                            type="text"
                            id="username"
                            name="username"
                            value={formData.username}
                            onChange={handleInputChange}
                            className={`form-input ${errors.username ? 'error' : ''}`}
                            placeholder="Choose a username"
                            disabled={isLoading}
                            autoComplete="username"
                        />
                        {errors.username && (
                            <span className="form-error">{errors.username}</span>
                        )}
                    </div>
                </div>

                <div className="form-group">
                    <label htmlFor="email" className="form-label">
                        Email Address
                    </label>
                    <input
                        type="email"
                        id="email"
                        name="email"
                        value={formData.email}
                        onChange={handleInputChange}
                        className={`form-input ${errors.email ? 'error' : ''}`}
                        placeholder="your.email@example.com"
                        disabled={isLoading}
                        autoComplete="email"
                    />
                    {errors.email && (
                        <span className="form-error">{errors.email}</span>
                    )}
                </div>

                <div className="form-group">
                    <label htmlFor="password" className="form-label">
                        Password
                    </label>
                    <input
                        type="password"
                        id="password"
                        name="password"
                        value={formData.password}
                        onChange={handleInputChange}
                        className={`form-input ${errors.password ? 'error' : ''}`}
                        placeholder="Create a secure password"
                        disabled={isLoading}
                        autoComplete="new-password"
                    />
                    {formData.password && (
                        <div className="password-strength">
                            <div className="password-strength-bar">
                                <div 
                                    className={`password-strength-fill strength-${passwordStrength}`}
                                    style={{ width: `${(passwordStrength / 5) * 100}%` }}
                                ></div>
                            </div>
                            <span className="password-strength-text">
                                {passwordStrength <= 2 ? 'Weak' : passwordStrength <= 3 ? 'Medium' : 'Strong'}
                            </span>
                        </div>
                    )}
                    {errors.password && (
                        <span className="form-error">{errors.password}</span>
                    )}
                </div>

                <div className="form-group">
                    <label htmlFor="confirmPassword" className="form-label">
                        Confirm Password
                    </label>
                    <input
                        type="password"
                        id="confirmPassword"
                        name="confirmPassword"
                        value={formData.confirmPassword}
                        onChange={handleInputChange}
                        className={`form-input ${errors.confirmPassword ? 'error' : ''}`}
                        placeholder="Confirm your password"
                        disabled={isLoading}
                        autoComplete="new-password"
                    />
                    {errors.confirmPassword && (
                        <span className="form-error">{errors.confirmPassword}</span>
                    )}
                </div>

                <div className="form-group checkbox-group">
                    <label className="checkbox-label">
                        <input
                            type="checkbox"
                            name="acceptTerms"
                            checked={formData.acceptTerms}
