            {/* Mood indicator */}
            {detectedMood && detectedMood.confidence > 0.3 && (
                <div className="mood-indicator">
                    <span className="mood-emoji">
                        {detectedMood.mood === 'happy' ? 'ðŸ˜Š' :
                         detectedMood.mood === 'sad' ? 'ðŸ˜¢' :
                         detectedMood.mood === 'anxious' ? 'ðŸ˜°' :
                         detectedMood.mood === 'excited' ? 'ðŸ¤©' :
                         detectedMood.mood === 'angry' ? 'ðŸ˜ ' : 'ðŸ˜Š'}
                    </span>
                    <span className="mood-text">
                        Detected mood: {detectedMood.mood}
                    </span>
                    <button
                        className="suggestions-toggle"
                        onClick={() => setShowSuggestions(!showSuggestions)}
                    >
                        ðŸ’¡
                    </button>
                </div>
            )}

            {/* Message suggestions */}
            {showSuggestions && detectedMood && moodSuggestions[detectedMood.mood] && (
                <div className="message-suggestions">
                    <div className="suggestions-header">Suggested messages:</div>
                    {moodSuggestions[detectedMood.mood].map((suggestion, index) => (
                        <button
                            key={index}
                            className="suggestion-item"
                            onClick={() => useSuggestion(suggestion)}
                        >
                            {suggestion}
                        </button>
                    ))}
                </div>
            )}

            {/* Input form */}
            <form onSubmit={handleSubmit} className="chat-input-form">
                <div className="input-wrapper">
                    <textarea
                        ref={textareaRef}
                        value={message}
                        onChange={handleMessageChange}
                        onKeyPress={handleKeyPress}
                        placeholder={placeholder}
                        className="chat-textarea"
                        disabled={isLoading}
                        rows={1}
                        maxLength={2000}
                    />

                    {/* Character counter */}
                    <div className="char-counter">
                        {message.length}/2000
                    </div>

                    {/* Send button */}
                    <button
                        type="submit"
                        className="send-button"
                        disabled={!message.trim() || isLoading}
                        title="Send message (Enter)"
                    >
                        {isLoading ? (
                            <span className="loading-spinner small"></span>
                        ) : (
                            <span className="send-icon">âž¤</span>
                        )}
                    </button>
                </div>

                {/* Input hints */}
                <div className="input-hints">
                    <span>Press Enter to send, Shift+Enter for new line</span>
                    {detectedMood && (
                        <span className="mood-hint">
                            ðŸ’¡ Click the bulb for mood-based suggestions
                        </span>
                    )}
                </div>
            </form>
        </div>
    );
});

/**
 * Main Chat Interface Component
 * Central chat component managing conversation flow and AI interactions
 */
const ChatInterface = memo(() => {
    const { state, dispatch } = useAppState();
    const { apiCall } = useAPI();
    const [isLoading, setIsLoading] = useState(false);
    const [crisisDetected, setCrisisDetected] = useState(null);
    const messagesEndRef = useAutoScroll(state.messages);

    // Load chat history on component mount
    useEffect(() => {
        loadChatHistory();
    }, []);

    // Load chat history from API
    const loadChatHistory = useCallback(async () => {
        try {
            const response = await apiCall('/chat/sessions');
            if (response.success && response.data.sessions.length > 0) {
                const latestSession = response.data.sessions[0];
                dispatch({ type: 'SET_CURRENT_SESSION', payload: latestSession });

                // Load messages for latest session
                const messagesResponse = await apiCall(`/chat/sessions/${latestSession.id}/messages`);
                if (messagesResponse.success) {
                    dispatch({ type: 'SET_MESSAGES', payload: messagesResponse.data.messages });
                }
            }
        } catch (error) {
            console.error('Failed to load chat history:', error);
        }
    }, [apiCall, dispatch]);

    // Send message to AI with comprehensive processing
    const handleSendMessage = useCallback(async (messageContent, detectedMood = null) => {
        if (!messageContent.trim()) return;

        // Add user message to state immediately for responsive UI
        const userMessage = {
            id: Date.now(),
            session_id: state.currentSession?.id,
            message_type: 'user',
            content: messageContent,
            timestamp: new Date().toISOString(),
            detected_mood: detectedMood
        };

        dispatch({ type: 'ADD_MESSAGE', payload: userMessage });
        dispatch({ type: 'START_TYPING' });
        setIsLoading(true);

        try {
            // Send message to backend
            const response = await apiCall('/chat/send', {
                method: 'POST',
                body: JSON.stringify({
                    message: messageContent,
                    session_id: state.currentSession?.id,
                    mood_context: detectedMood?.mood
                })
            });

            if (response.success) {
                // Update session if new
                if (!state.currentSession) {
                    dispatch({ 
                        type: 'SET_CURRENT_SESSION', 
                        payload: { id: response.data.session_id }
                    });
                }

                // Add AI response with typing animation
                dispatch({ type: 'ADD_MESSAGE', payload: response.data.ai_response });

                // Handle crisis detection
                if (response.data.crisis_detected) {
                    setCrisisDetected({
                        message: response.data.ai_response.content,
                        resources: response.data.crisis_resources || []
                    });
                }

                // Update user stats if provided
                if (response.data.engagement_reward) {
                    const reward = response.data.engagement_reward;
                    dispatch({
                        type: 'UPDATE_USER_STATS',
                        payload: {
                            coins: state.coins + (reward.coins_awarded || 0),
                            streak: state.streak,
                            level: reward.level_up?.new_level || state.level,
                            experience: state.experience + (reward.xp_awarded || 0)
                        }
                    });

                    // Show level up notification
                    if (reward.level_up) {
                        dispatch({
                            type: 'ADD_NOTIFICATION',
                            payload: {
                                id: Date.now(),
                                type: 'level_up',
                                title: 'Level Up!',
                                message: `Congratulations! You reached level ${reward.level_up.new_level}!`,
                                timestamp: new Date().toISOString()
                            }
                        });
                    }
                }

                // Update current mood if detected
                if (response.data.mood_analysis?.mood) {
                    dispatch({
                        type: 'SET_CURRENT_MOOD',
                        payload: response.data.mood_analysis.mood
                    });
                }

                // Set music suggestions if provided
                if (response.data.music_suggestions?.length > 0) {
                    dispatch({
                        type: 'SET_MUSIC_SUGGESTIONS',
                        payload: response.data.music_suggestions
                    });
                }

            } else {
                // Handle API error
                dispatch({
                    type: 'ADD_MESSAGE',
                    payload: {
                        id: Date.now(),
                        message_type: 'system',
                        content: 'Sorry, I encountered an error processing your message. Please try again.',
                        timestamp: new Date().toISOString(),
                        isError: true
                    }
                });
            }

        } catch (error) {
            // Handle network or other errors
            dispatch({
                type: 'ADD_MESSAGE',
                payload: {
                    id: Date.now(),
                    message_type: 'system',
                    
                    timestamp: new Date().toISOString(),
                    isError: true
                }
            });
        } finally {
            dispatch({ type: 'STOP_TYPING' });
            setIsLoading(false);
        }
    }, [state.currentSession, state.coins, state.streak, state.level, state.experience, apiCall, dispatch]);

    // Handle message reactions
    const handleMessageReaction = useCallback((messageId, reaction) => {
        // Update message in local state
        dispatch({
            type: 'SET_MESSAGES',
            payload: state.messages.map(msg => 
                msg.id === messageId
                    ? {
                        ...msg,
                        user_reactions: [
                            ...(msg.user_reactions || []),
                            { reaction, timestamp: new Date().toISOString() }
                        ]
                    }
                    : msg
            )
        });

        // Could send to backend for persistence
        // apiCall(`/chat/messages/${messageId}/react`, { method: 'POST', body: JSON.stringify({ reaction }) });
    }, [state.messages, dispatch]);

    // Handle music suggestion clicks
    const handleMusicSuggestion = useCallback((suggestion) => {
        // Open music platform based on suggestion
        let url = '';
        switch (suggestion.platform) {
            case 'spotify':
                url = `https://open.spotify.com/search/${encodeURIComponent(suggestion.title)}`;
                break;
            case 'youtube':
                url = `https://www.youtube.com/results?search_query=${encodeURIComponent(suggestion.title)}`;
                break;
            default:
                url = `https://www.google.com/search?q=${encodeURIComponent(suggestion.title + ' music')}`;
        }

        window.open(url, '_blank');

        // Track music suggestion usage
        dispatch({
            type: 'SET_CURRENTLY_PLAYING',
            payload: suggestion
        });
    }, [dispatch]);

    return (
        <div className="chat-interface">
            {/* Chat header with session info */}
            <div className="chat-header">
                <div className="chat-title">
                    <h2>ðŸ’¬ Chat with MoodMate</h2>
                    {state.currentSession && (
                        <span className="session-info">
                            Session: {state.currentSession.session_name || 'Current Chat'}
                        </span>
                    )}
                </div>

                {/* Current mood indicator */}
                {state.currentMood && state.currentMood !== 'neutral' && (
                    <div className="current-mood-indicator">
                        <span className="mood-label">Current mood:</span>
                        <span className={`mood-badge mood-${state.currentMood}`}>
                            {state.currentMood}
                        </span>
                    </div>
                )}
            </div>

            {/* Messages container */}
            <div className="chat-messages" ref={messagesEndRef}>
                {state.messages.length === 0 ? (
                    // Welcome message for new chats
                    <div className="welcome-message">
                        <div className="welcome-avatar">ðŸ¤–</div>
                        <div className="welcome-text">
                            <h3>Hi {state.user?.display_name || 'there'}! ðŸ‘‹</h3>
                            <p>
                                I'm MoodMate, your AI mental health companion. I'm here to listen, 
                                support, and help you on your wellness journey.
                            </p>
                            <p>
                                How are you feeling today? You can share in English, Hindi, or 
                                even mix both (Hinglish) - I understand all of them!
                            </p>
                        </div>
                    </div>
                ) : (
                    // Render all messages
                    state.messages.map((message, index) => (
                        <ChatMessage
                            key={message.id}
                            message={message}
                            isOwn={message.message_type === 'user'}
                            showAvatar={index === 0 || state.messages[index - 1]?.message_type !== message.message_type}
                            onReaction={handleMessageReaction}
                            onMusicSuggestion={handleMusicSuggestion}
                        />
                    ))
                )}

                {/* Typing indicator */}
                {state.isTyping && <TypingIndicator />}
            </div>

            {/* Chat input */}
            <ChatInput
                onSendMessage={handleSendMessage}
                isLoading={isLoading}
                placeholder="Share what's on your mind... à¤®à¤¨ à¤•à¥€ à¤¬à¤¾à¤¤ à¤•à¤¹à¥‡à¤‚..."
            />

            {/* Crisis Detection Modal */}
            {crisisDetected && (
                <CrisisModal
                    isOpen={true}
                    onClose={() => setCrisisDetected(null)}
                    message={crisisDetected.message}
                    resources={crisisDetected.resources}
                />
            )}
        </div>
    );
});

