/**
 * Main Application Component
 * Root component that manages authentication and app routing
 */
const App = () => {
    const [state, dispatch] = useReducer(appStateReducer, initialAppState);
    const [authMode, setAuthMode] = useState('login'); // 'login' or 'register'

    // Initialize app and check authentication
    useEffect(() => {
        initializeApp();
    }, []);

    // Initialize application with authentication check
    const initializeApp = async () => {
        try {
            const response = await fetch('http://127.0.0.1:5000/api/auth/profile', {
                credentials: 'include'
            });

            if (response.ok) {
                const data = await response.json();
                if (data.success) {
                    dispatch({
                        type: 'LOGIN_SUCCESS',
                        payload: { user: data.data.profile }
                    });
                }
            }
        } catch (error) {
            console.log('Not authenticated:', error);
        } finally {
            dispatch({ type: 'SET_LOADING', payload: false });
        }
    };

    // Handle successful authentication
    const handleAuthSuccess = useCallback((authData) => {
        dispatch({
            type: 'LOGIN_SUCCESS',
            payload: authData
        });

        // Show welcome notification
        dispatch({
            type: 'ADD_NOTIFICATION',
            payload: {
                id: Date.now(),
                type: 'welcome',
                title: 'Welcome to MoodMate!',
                message: `Hi ${authData.user.display_name}! Ready to start your wellness journey?`,
                timestamp: new Date().toISOString()
            }
        });
    }, []);

    // Handle logout
    const handleLogout = useCallback(async () => {
        try {
            await fetch('http://127.0.0.1:5000/api/auth/logout', {
                method: 'POST',
                credentials: 'include'
            });
        } catch (error) {
            console.error('Logout error:', error);
        } finally {
            dispatch({ type: 'LOGOUT' });
        }
    }, []);

    // Provide context to all components
    const contextValue = useMemo(() => ({
        state,
        dispatch,
        handleLogout
    }), [state, handleLogout]);

    // Show loading screen during initialization
    if (state.isLoading) {
        return (
            <div className="app-loading">
                <div className="loading-content">
                    <div className="loading-logo">ðŸ§ </div>
                    <h2>MoodMate</h2>
                    <div className="loading-spinner"></div>
                    <p>Loading your mental wellness companion...</p>
                </div>
            </div>
        );
    }

    return (
        <AppContext.Provider value={contextValue}>
            <div className={`app ${state.theme}`}>
                {!state.isAuthenticated ? (
                    // Authentication screens
                    <div className="auth-container">
                        <div className="auth-background"></div>
                        <div className="auth-content">
                            {authMode === 'login' ? (
                                <LoginForm
                                    onSuccess={handleAuthSuccess}
                                    onSwitchToRegister={() => setAuthMode('register')}
                                />
                            ) : (
                                <RegisterForm
                                    onSuccess={handleAuthSuccess}
                                    onSwitchToLogin={() => setAuthMode('login')}
                                />
                            )}
                        </div>
                    </div>
                ) : (
                    // Main application interface
                    <div className="app-main">
                        {/* Global notifications */}
                        {state.notifications.length > 0 && (
                            <div className="notifications-container">
                                {state.notifications.slice(0, 3).map(notification => (
                                    <div key={notification.id} className={`notification ${notification.type}`}>
                                        <div className="notification-content">
                                            <div className="notification-title">{notification.title}</div>
                                            <div className="notification-message">{notification.message}</div>
                                        </div>
                                        <button
                                            className="notification-close"
                                            onClick={() => dispatch({
                                                type: 'REMOVE_NOTIFICATION',
                                                payload: notification.id
                                            })}
                                        >
                                            Ã—
                                        </button>
                                    </div>
                                ))}
                            </div>
                        )}

                        {/* Main chat interface */}
                        <ChatInterface />

                        {/* Additional UI components would go here */}
                    </div>
                )}

                {/* Error display */}
                {state.errors.length > 0 && (
                    <div className="error-container">
                        {state.errors.slice(0, 2).map(error => (
                            <div key={error.id} className="error-notification">
                                <span className="error-message">{error.message}</span>
                                <button
                                    className="error-close"
                                    onClick={() => dispatch({ type: 'REMOVE_ERROR', payload: error.id })}
                                >
                                    Ã—
                                </button>
                            </div>
                        ))}
                    </div>
                )}
            </div>
        </AppContext.Provider>
    );
};

export default App;
