import React, { useState, useEffect, useRef } from "react";
import "./app.css";
import "./auth.css"; // Make sure to keep this import for styling the forms

const API_BASE_URL = 'http://127.0.0.1:5000';

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [authMode, setAuthMode] = useState("login");
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [isTyping, setIsTyping] = useState(false);
  const [user, setUser] = useState(null);
  const [activeTab, setActiveTab] = useState("chat");
  const [journalEntries, setJournalEntries] = useState([]);
  const [therapists, setTherapists] = useState([]);
  const [resources, setResources] = useState([]);
  const [moodWall, setMoodWall] = useState([]);
  const [moodTribes, setMoodTribes] = useState([]);
  const [shopItems, setShopItems] = useState([]);
  const [userTribes, setUserTribes] = useState([]);
  const [currentTheme, setCurrentTheme] = useState("default");
  const [typingIndicator, setTypingIndicator] = useState("");
  const [showEmergencyModal, setShowEmergencyModal] = useState(false);
  const [achievements, setAchievements] = useState([]);
  const [activeTribe, setActiveTribe] = useState(null);
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [showNewChatModal, setShowNewChatModal] = useState(false);
  const [lastStreakUpdate, setLastStreakUpdate] = useState("");
  const [activeShopCategory, setActiveShopCategory] = useState("All");
  const [aiProvider, setAiProvider] = useState("gemini");
  const [audioEnabled, setAudioEnabled] = useState(true);
  const [musicEnabled, setMusicEnabled] = useState(true);
  const [tribeChatMessages, setTribeChatMessages] = useState({});
  const [tribeInput, setTribeInput] = useState("");

  const [authUsername, setAuthUsername] = useState("");
  const [authPassword, setAuthPassword] = useState("");
  const [authError, setAuthError] = useState("");
  const [authSuccess, setAuthSuccess] = useState("");

  const chatEndRef = useRef(null);

  const sampleTherapists = [
    {id: 1, name: "Dr. Priya Sharma", specialization: "Clinical Psychology", price: 79, rating: 4.7, experience: "8 years", avatar: "👩‍⚕️"},
    {id: 2, name: "Rajiv Mehta", specialization: "Counseling Psychology", price: 599, rating: 4.5, experience: "5 years", avatar: "👨‍⚕️"},
    {id: 3, name: "Dr. Anjali Singh", specialization: "Psychiatry", price: 999, rating: 4.8, experience: "12 years", avatar: "👩‍⚕️"},
    {id: 4, name: "Neha Gupta", specialization: "Art Therapy", price: 499, rating: 4.6, experience: "4 years", avatar: "🎨"}
  ];

  const sampleResources = [
    {type: "crisis", name: "National Suicide Prevention Lifeline", number: "1-800-273-8255", available: "24/7"},
    {type: "crisis", name: "Crisis Text Line", number: "Text HOME to 741741", available: "24/7"},
    {type: "support", name: "Mental Health America", website: "mhanational.org", description: "Resources and screening tools"}
  ];

  const sampleMoodTribes = [
    {id: 1, name: "Positivity Tribe", description: "For those seeking positivity and joy", member_count: 245, isMember: false},
    {id: 2, name: "Calm Collective", description: "Find your inner peace together", member_count: 189, isMember: false},
    {id: 3, name: "Anxiety Allies", description: "Support for those dealing with anxiety", member_count: 312, isMember: false},
    {id: 4, name: "Mindfulness Masters", description: "Practice mindfulness together", member_count: 156, isMember: false},
    {id: 5, name: "Breakup Recovery", description: "Healing after relationship endings", member_count: 128, isMember: false},
    {id: 6, name: "Career Stress", description: "Managing work-related stress", member_count: 201, isMember: false},
    {id: 7, name: "Student Support", description: "Academic pressure and study stress", member_count: 178, isMember: false}
  ];

  const sampleShopItems = [
    {id: 1, name: "Sunset Theme", description: "Warm orange and purple theme", price: 50, category: "theme"},
    {id: 2, name: "Ocean Theme", description: "Calming blue theme", price: 50, category: "theme"},
    {id: 3, name: "Forest Theme", description: "Green nature-inspired theme", price: 50, category: "theme"},
    {id: 4, name: "Neon Theme", description: "Vibrant neon colors", price: 75, category: "theme"},
    {id: 5, name: "Glass Theme", description: "Modern glassmorphism design", price: 100, category: "theme"},
    {id: 6, name: "Exclusive Avatar", description: "Special avatar frame", price: 100, category: "cosmetic"},
    {id: 7, name: "Meditation Pack", description: "Exclusive meditation guides", price: 75, category: "content"},
    {id: 8, name: "Second Chance Token", description: "Save your streak if you miss a day", price: 30, category: "utility"},
    {id: 9, name: "Custom Voice", description: "Personalized AI voice", price: 150, category: "feature"}
  ];

  const sampleAchievements = [
    {id: 1, name: "First Steps", description: "Complete your first journal entry", earned: true, icon: "📝"},
    {id: 2, name: "7-Day Warrior", description: "Maintain a 7-day streak", earned: false, icon: "🔥"},
    {id: 3, name: "Community Builder", description: "Join your first tribe", earned: false, icon: "👥"},
    {id: 4, name: "30-Day Calm Seeker", description: "Maintain a 30-day streak", earned: false, icon: "🌿"},
    {id: 5, name: "365-Day Legend", description: "Maintain a 365-day streak", earned: false, icon: "👑"},
    {id: 6, name: "Mental Fitness Pro", description: "Reach 90+ mental fitness score", earned: false, icon: "💪"}
  ];

  const aiResponses = {
    happy: [
      "That's wonderful to hear! 😊 Your positivity is contagious. What's making you feel so happy today?",
      "I'm so glad you're feeling happy! 🌟 Celebrating the good moments is important. Want to share what's bringing you joy?",
      "Happiness looks good on you! ✨ Let's savor this moment together. What's making you smile today?"
    ],
    sad: [
      "I'm sorry you're feeling down. 😔 It's okay to have sad days - they make the happy ones brighter. Want to talk about what's bothering you?",
      "I hear you're feeling sad, and I'm here for you. 💙 Sometimes just sharing how we feel can lighten the load. What's on your mind?",
      "Thank you for sharing that you're feeling sad. It takes courage to acknowledge these feelings. 🌧️ Would you like to talk more about what's causing this?"
    ],
    anxious: [
      "I understand that anxious feeling. 🫂 Let's take a deep breath together. Would you like to try a quick grounding exercise?",
      "Anxiety can be really overwhelming. 💭 I'm here to help you through it. Want to explore what's triggering these feelings?",
      "It's okay to feel anxious sometimes. 🌫️ Let's work through this together. Would naming the specific worries help?"
    ],
    angry: [
      "I hear the frustration in your words. 💢 Anger is a valid emotion - it's what we do with it that matters. Want to talk about what's upsetting you?",
      "It sounds like you're feeling pretty angry right now. 🔥 Let's channel that energy constructively. What's triggering this response?",
      "Anger can be a powerful messenger. 📣 I'm here to listen without judgment. Want to explore what's beneath the surface?"
    ],
    neutral: [
      "Thanks for checking in with me. 🤗 How has your day been so far?",
      "I'm here to listen whenever you're ready to share. 💫 What's on your mind today?",
      "Hello there! 🌈 How are you really feeling behind that neutral facade?"
    ]
  };

  useEffect(() => {
    const checkLoginStatus = async () => {
      try {
        const response = await fetch(`${API_BASE_URL}/api/health`, {credentials: 'include'});
        if (response.ok) {
          // Assuming the user is logged in if the server health check is successful
          // A real app would have an endpoint like /api/user to get details.
          // For now, we'll use a placeholder user.
          setIsAuthenticated(true);
          setUser({ id: 1, name: "Demo User", coins: 100, streak: 5, mentalFitness: 85 });
          loadInitialData();
        } else {
          setIsAuthenticated(false);
          setUser(null);
        }
      } catch (error) {
        console.error("Session check failed:", error);
        setIsAuthenticated(false);
        setUser(null);
      }
    };
    checkLoginStatus();
  }, []);

  useEffect(() => {
    if (isAuthenticated) {
      loadInitialData();
    }
  }, [isAuthenticated]);

  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  const handleLogin = async (e) => {
    e.preventDefault();
    setAuthError("");
    setAuthSuccess("");
    try {
      const response = await fetch(`${API_BASE_URL}/api/login`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username: authUsername, password: authPassword }),
      });
      const data = await response.json();
      if (response.ok) {
        setIsAuthenticated(true);
        setUser({ id: 1, name: data.username, coins: 100, streak: 5, mentalFitness: 85 });
      } else {
        setAuthError(data.message || 'Login failed. Please try again.');
      }
    } catch (err) {
      setAuthError('An error occurred. Please check your network connection.');
      console.error('Login error:', err);
    }
  };

  const handleSignup = async (e) => {
    e.preventDefault();
    setAuthError("");
    setAuthSuccess("");
    try {
      const response = await fetch(`${API_BASE_URL}/api/signup`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username: authUsername, password: authPassword }),
      });
      const data = await response.json();
      if (response.ok) {
        setAuthSuccess('Account created successfully! You can now log in.');
        setAuthMode("login");
        setAuthUsername("");
        setAuthPassword("");
      } else {
        setAuthError(data.message || 'Signup failed. Please try a different username.');
      }
    } catch (err) {
      setAuthError('An error occurred. Please try again later.');
      console.error('Signup error:', err);
    }
  };

  const handleLogout = async () => {
    try {
      await fetch(`${API_BASE_URL}/api/logout`);
    } finally {
      setIsAuthenticated(false);
      setUser(null);
      setMessages([]);
      setJournalEntries([]);
      setAuthMode("login");
    }
  };

  const startNewChat = () => {
    setMessages([{
      id: Date.now(),
      text: "Hello! I'm MoodMate, your mental health companion. How are you feeling today?",
      sender: "bot",
      timestamp: new Date().toISOString()
    }]);
    setShowNewChatModal(false);
  };

  const loadInitialData = () => {
    setMessages([{
      id: Date.now(),
      text: "Hello! I'm MoodMate, your mental health companion. How are you feeling today?",
      sender: "bot",
      timestamp: new Date().toISOString()
    }]);
    
    setTherapists(sampleTherapists);
    setResources(sampleResources);
    
    const userTribeIds = userTribes.map(t => t.id);
    const updatedTribes = sampleMoodTribes.map(tribe => ({
      ...tribe,
      isMember: userTribeIds.includes(tribe.id)
    }));
    setMoodTribes(updatedTribes);
    
    setShopItems(sampleShopItems);
    
    const samplePosts = [
      {id: 1, text: "Today I finally managed to meditate for 10 minutes straight! Feeling proud of myself.", mood: "happy", likes: 12, timestamp: new Date(Date.now() - 2*60*60*1000).toISOString(), anonymous: true, userName: "Anonymous"},
      {id: 2, text: "Struggling with anxiety today. Anyone else feeling this way?", mood: "anxious", likes: 8, timestamp: new Date(Date.now() - 5*60*60*1000).toISOString(), anonymous: true, userName: "Anonymous"},
      {id: 3, text: "Grateful for this community. Reading your stories gives me hope.", mood: "grateful", likes: 25, timestamp: new Date(Date.now() - 8*60*60*1000).toISOString(), anonymous: false, userName: "HappyUser123"}
    ];
    setMoodWall(samplePosts);
    
    const sampleEntries = [
      {id: 1, text: "Today was a good day. I felt productive and happy.", summary: "Positive day with productivity", mood: "happy", date: new Date().toISOString().split('T')[0], shared: false},
      {id: 2, text: "Feeling a bit anxious about the upcoming presentation. Need to practice more.", summary: "Anxious about presentation", mood: "anxious", date: "2023-10-24", shared: true},
      {id: 3, text: "Had a great conversation with my friend. It felt good to open up.", summary: "Positive social interaction", mood: "happy", date: "2023-10-23", shared: false}
    ];
    setJournalEntries(sampleEntries);
    
    const initialTribeChats = {};
    sampleMoodTribes.forEach(tribe => {
      initialTribeChats[tribe.id] = [
        {id: 1, message: "Hello everyone! How are you all feeling today?", user_name: "Anonymous", timestamp: new Date(Date.now() - 2*60*60*1000).toISOString()},
        {id: 2, message: "I've been practicing mindfulness and it's really helping with my anxiety 😊", user_name: "Anonymous", timestamp: new Date(Date.now() - 1*60*60*1000).toISOString()}
      ];
    });
    setTribeChatMessages(initialTribeChats);
    setAchievements(sampleAchievements);
  };
  
  const simulateTyping = async () => {
    const typingPhrases = ["Thinking", "Thinking.", "Thinking..", "Thinking..."];
    for (let i = 0; i < 6; i++) {
      setTypingIndicator(typingPhrases[i % 4]);
      await new Promise(resolve => setTimeout(resolve, 300));
    }
    setTypingIndicator("");
  };

  const sendMessage = async () => {
    if (!input.trim() || !isAuthenticated) return;

    const userMsg = { 
      id: Date.now(),
      text: input, 
      sender: "user",
      timestamp: new Date().toISOString()
    };
    setMessages(prev => [...prev, userMsg]);
    setInput("");
    setIsTyping(true);
    
    const emergencyKeywords = ['suicide', 'kill myself', 'end it all', 'want to die', 'self harm', 'hurt myself'];
    const hasEmergency = emergencyKeywords.some(keyword => 
      input.toLowerCase().includes(keyword)
    );
    
    if (hasEmergency) {
      setShowEmergencyModal(true);
      setIsTyping(false);
      return;
    }
    
    simulateTyping();

    try {
      const response = await fetch(`${API_BASE_URL}/api/chat`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ message: input }),
      });

      const data = await response.json();

      if (response.status === 401) {
          handleLogout();
          alert("Your session has expired. Please log in again.");
          return;
      }

      if (!response.ok) {
        if (data.error === "gemini_unavailable") {
            alert("AI service unavailable, please try again later.");
            setIsTyping(false);
            return;
        }
        throw new Error(data.error || "API error");
      }
      
      const botMsg = { 
        id: Date.now(),
        text: data.reply, 
        sender: "bot",
        mood: data.mood,
        moodEmoji: data.moodEmoji,
        phrase: data.phrase,
        audioUrl: data.audioUrl,
        timestamp: new Date().toISOString()
      };
      setMessages(prev => [...prev, botMsg]);

      if (data.audioUrl && audioEnabled) {
        const audio = new Audio(`${API_BASE_URL}${data.audioUrl}`);
        audio.play();
      }

      if (data.coinsEarned > 0) {
        const newCoins = (user?.coins || 0) + data.coinsEarned;
        const updatedUser = {...user, coins: newCoins, streak: data.streak};
        setUser(updatedUser);
      }

    } catch (error) {
      console.log("Chat error:", error);
      alert("An unexpected error occurred. Please try again.");
    }

    setIsTyping(false);
    setTypingIndicator("");
  };

  const detectMoodFromInput = (text) => {
    const textLower = text.toLowerCase();
    if (textLower.includes('happy') || textLower.includes('good') || textLower.includes('great') || textLower.includes('excited')) return 'happy';
    if (textLower.includes('sad') || textLower.includes('depressed') || textLower.includes('unhappy') || textLower.includes('cry')) return 'sad';
    if (textLower.includes('anxious') || textLower.includes('nervous') || textLower.includes('worried') || textLower.includes('stress')) return 'anxious';
    if (textLower.includes('angry') || textLower.includes('mad') || textLower.includes('frustrated') || textLower.includes('annoyed')) return 'angry';
    return 'neutral';
  };

  const getMoodEmoji = (mood) => {
    const emojis = {
      happy: "😊",
      sad: "😢",
      anxious: "😰",
      angry: "😠",
      neutral: "😊"
    };
    return emojis[mood];
  };

  const getMoodPhrase = (mood) => {
    const phrases = {
      happy: "खुश रहो हमेशा — आपकी मुस्कान की वजह कुछ है।",
      sad: "यह भी बीत जाएगा — थोड़ी सी उम्मीद रखो।",
      anxious: "शांत रहना सीखो, सब धीरे-धीरे ठीक होगा।",
      angry: "गहरे साँस लो — हर ग़ुस्सा कुछ सिखा कर जाता है।",
      neutral: "You're doing great. Keep going!"
    };
    return phrases[mood];
  };

  const addJournalEntry = () => {
    const entryText = prompt("What's on your mind today?");
    if (!entryText) return;

    const newEntry = {
      id: Date.now(),
      text: entryText,
      summary: "Personal reflection",
      mood: "neutral",
      date: new Date().toISOString().split('T')[0],
      shared: false
    };
    
    setJournalEntries(prev => [newEntry, ...prev]);
    
    const newCoins = (user?.coins || 0) + 3;
    const updatedUser = {...user, coins: newCoins};
    setUser(updatedUser);
    
    alert("Journal entry saved! +3 coins earned!");
  };

  const shareJournalEntry = (entryId) => {
    const updatedEntries = journalEntries.map(e => 
      e.id === entryId ? {...e, shared: true} : e
    );
    setJournalEntries(updatedEntries);
    alert("Journal entry shared to your tribes!");
  };

  const addMoodWallPost = () => {
    const postText = prompt("Share your feelings with the community (anonymous):");
    if (!postText) return;

    const newPost = {
      id: Date.now(),
      text: postText,
      mood: "neutral",
      likes: 0,
      timestamp: new Date().toISOString(),
      anonymous: true,
      userName: "Anonymous"
    };
    
    setMoodWall(prev => [newPost, ...prev]);
    alert("Posted to mood wall!");
  };

  const joinTribe = (tribeId) => {
    const tribe = moodTribes.find(t => t.id === tribeId);
    if (tribe) {
      const updatedTribes = moodTribes.map(t => 
        t.id === tribeId ? {...t, isMember: true, member_count: t.member_count + 1} : t
      );
      setMoodTribes(updatedTribes);
      setUserTribes(prev => [...prev, tribe]);
      
      alert(`🎉 You've joined the ${tribe.name}!`);
    }
  };

  const sendTribeMessage = (tribeId) => {
    if (!tribeInput.trim()) return;
    
    const newMessage = {
      id: Date.now(),
      message: tribeInput,
      user_name: "You",
      timestamp: new Date().toISOString()
    };
    
    setTribeChatMessages(prev => ({
      ...prev,
      [tribeId]: [...(prev[tribeId] || []), newMessage]
    }));
    
    setTribeInput("");
  };

  const playBreathingExercise = () => {
    setMessages(prev => [...prev, {
      id: Date.now(),
      text: "Let's do a quick breathing exercise. Breathe in for 4 seconds, hold for 4 seconds, exhale for 6 seconds. Repeat 5 times. I'll guide you through it...",
      sender: "bot",
      isExercise: true,
      timestamp: new Date().toISOString()
    }]);
  };

  const buyItem = (item) => {
    if (user.coins < item.price) {
      alert("Not enough coins!");
      return;
    }
    
    if (window.confirm(`Buy ${item.name} for ${item.price} coins?`)) {
      const newCoins = user.coins - item.price;
      const updatedUser = {...user, coins: newCoins};
      setUser(updatedUser);
      
      if (item.category === "theme") {
        setCurrentTheme(item.name.toLowerCase().replace(' theme', ''));
        alert(`Theme applied! You're now using the ${item.name}.`);
      } else if (item.name === "Second Chance Token") {
        alert("Second Chance Token purchased! This will save your streak if you miss a day.");
      } else {
        alert(`Purchased ${item.name}!`);
      }
    }
  };

  const applyTheme = (themeName) => {
    setCurrentTheme(themeName);
  };

  const toggleAudio = () => {
    const newAudioEnabled = !audioEnabled;
    setAudioEnabled(newAudioEnabled);
  };

  const toggleMusic = () => {
    const newMusicEnabled = !musicEnabled;
    setMusicEnabled(newMusicEnabled);
  };

  const switchAiProvider = (provider) => {
    setAiProvider(provider);
  };

  const EmergencyModal = () => {
    if (!showEmergencyModal) return null;
    
    return (
      <div className="emergency-modal">
        <div className="emergency-content">
          <h2>⚠️ We're concerned about you</h2>
          <p>Please choose an option below. Your safety matters 💙</p>
          
          <div className="emergency-buttons">
            <button 
              className="emergency-call" 
              onClick={() => {
                window.open('tel:1-800-273-8255');
                setShowEmergencyModal(false);
              }}
            >
              Call for Help 🔴
            </button>
            
            <button 
              className="emergency-resources" 
              onClick={() => {
                alert("Crisis Resources:\n\n1. National Suicide Prevention Lifeline: 1-800-273-8255\n2. Crisis Text Line: Text HOME to 741741\n3. iCall India: +91-9152987821");
                setShowEmergencyModal(false);
              }}
            >
              Show Resources 📋
            </button>
            
            <button 
              className="emergency-ok" 
              onClick={() => setShowEmergencyModal(false)}
            >
              I'm Fine Now ✅
            </button>
          </div>
        </div>
      </div>
    );
  };

  const NewChatModal = () => {
    if (!showNewChatModal) return null;
    
    return (
      <div className="modal-overlay">
        <div className="modal-content">
          <h2>Start New Chat</h2>
          <p>Are you sure you want to start a new chat? Your current conversation will be cleared.</p>
          <div className="modal-buttons">
            <button onClick={startNewChat} className="btn-primary">Yes, Start New Chat</button>
            <button onClick={() => setShowNewChatModal(false)} className="btn-secondary">Cancel</button>
          </div>
        </div>
      </div>
    );
  };

  const TribeCommunity = () => {
    if (!activeTribe) return null;
    
    const tribeChat = tribeChatMessages[activeTribe.id] || [];
    
    return (
      <div className="tribe-community">
        <div className="tribe-header">
          <button onClick={() => setActiveTribe(null)} className="back-btn">
            ← Back to Communities
          </button>
          <h2>{activeTribe.name} Community</h2>
          <p>{activeTribe.description}</p>
          <div className="tribe-stats">
            <span>👥 {activeTribe.member_count} members</span>
            <span>💬 {tribeChat.length} messages</span>
          </div>
        </div>
        
        <div className="tribe-content">
          <div className="tribe-chat">
            <h3>Community Chat</h3>
            <div className="tribe-messages">
              {tribeChat.map((msg) => (
                <div key={`tribe-msg-${msg.id}-${Date.now()}`} className="tribe-message">
                  <div className="message-avatar">{msg.user_name.charAt(0)}</div>
                  <div className="message-content">
                    <span className="message-sender">{msg.user_name}</span>
                    <p>{msg.message}</p>
                    <span className="message-time">{new Date(msg.timestamp).toLocaleTimeString()}</span>
                  </div>
                </div>
              ))}
            </div>
            
            <div className="tribe-input">
              <input 
                type="text" 
                placeholder="Share something with the community..." 
                value={tribeInput}
                onChange={(e) => setTribeInput(e.target.value)}
                onKeyDown={(e) => e.key === "Enter" && sendTribeMessage(activeTribe.id)}
              />
              <button onClick={() => sendTribeMessage(activeTribe.id)}>Send</button>
            </div>
          </div>
          
          <div className="tribe-resources">
            <h3>Community Resources</h3>
            <div className="resource-list">
              <div className="resource-item">
                <h4>Mindfulness Exercises</h4>
                <p>Guided meditation for beginners</p>
                <button>View</button>
              </div>
              <div className="resource-item">
                <h4>Support Threads</h4>
                <p>Connect with others who understand</p>
                <button>Explore</button>
              </div>
              <div className="resource-item">
                <h4>Weekly Challenges</h4>
                <p>Join community wellness challenges</p>
                <button>Participate</button>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  };

  const Sidebar = () => {
    if (!sidebarOpen) return null;
    
    return (
      <div className="sidebar-overlay" onClick={() => setSidebarOpen(false)}>
        <div className="sidebar" onClick={(e) => e.stopPropagation()}>
          <div className="sidebar-header">
            <h2>MoodMate</h2>
            <button onClick={() => setSidebarOpen(false)} className="close-btn">×</button>
          </div>
          
          <div className="sidebar-content">
            <div className="sidebar-section">
              <h3>Navigation</h3>
              <button 
                onClick={() => { setActiveTab("chat"); setSidebarOpen(false); }} 
                className={`sidebar-btn ${activeTab === "chat" ? "active" : ""}`}
              >
                💬 Chat
              </button>
              <button 
                onClick={() => { setActiveTab("journal"); setSidebarOpen(false); }} 
                className={`sidebar-btn ${activeTab === "journal" ? "active" : ""}`}
              >
                📓 Journal
              </button>
              <button 
                onClick={() => { setActiveTab("therapy"); setSidebarOpen(false); }} 
                className={`sidebar-btn ${activeTab === "therapy" ? "active" : ""}`}
              >
                👨‍⚕️ Therapy
              </button>
              <button 
                onClick={() => { setActiveTab("community"); setSidebarOpen(false); }} 
                className={`sidebar-btn ${activeTab === "community" ? "active" : ""}`}
              >
                🌍 Community
              </button>
              <button 
                onClick={() => { setActiveTab("shop"); setSidebarOpen(false); }} 
                className={`sidebar-btn ${activeTab === "shop" ? "active" : ""}`}
              >
                🛒 Shop
              </button>
              <button 
                onClick={() => { setActiveTab("profile"); setSidebarOpen(false); }} 
                className={`sidebar-btn ${activeTab === "profile" ? "active" : ""}`}
              >
                👤 Profile
              </button>
            </div>
            
            <div className="sidebar-section">
              <h3>Quick Actions</h3>
              <button onClick={() => { setShowNewChatModal(true); setSidebarOpen(false); }} className="sidebar-btn">
                New Chat
              </button>
              <button onClick={() => { playBreathingExercise(); setSidebarOpen(false); }} className="sidebar-btn">
                Breathing Exercise
              </button>
            </div>
            
            <div className="sidebar-section">
              <h3>Settings</h3>
              <div className="setting-item">
                <span>Voice Responses</span>
                <label className="switch">
                  <input type="checkbox" checked={audioEnabled} onChange={toggleAudio} />
                  <span className="slider"></span>
                </label>
              </div>
              <div className="setting-item">
                <span>Background Music</span>
                <label className="switch">
                  <input type="checkbox" checked={musicEnabled} onChange={toggleMusic} />
                  <span className="slider"></span>
                </label>
              </div>
              <div className="setting-item">
                <span>AI Provider</span>
                <select value={aiProvider} onChange={(e) => switchAiProvider(e.target.value)}>
                  <option value="gemini">Gemini AI</option>
                  <option value="fallback">Fallback Mode</option>
                </select>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  };
  
  const shopCategories = ["All", "theme", "cosmetic", "content", "utility", "feature"];

  const filteredShopItems = activeShopCategory === "All"
    ? sampleShopItems
    : sampleShopItems.filter(item => item.category.toLowerCase() === activeShopCategory.toLowerCase());

  const AuthView = () => (
    <div className="auth-container">
      <div className="auth-card">
        {authMode === "login" ? (
          <>
            <h2>Welcome Back!</h2>
            <p>Log in to continue your mental wellness journey.</p>
            <form onSubmit={handleLogin}>
              {authError && <p className="error-message">{authError}</p>}
              {authSuccess && <p className="success-message">{authSuccess}</p>}
              <div className="form-group">
                <label htmlFor="authUsername">Username</label>
                <input
                  type="text"
                  id="authUsername"
                  value={authUsername}
                  onChange={(e) => setAuthUsername(e.target.value)}
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="authPassword">Password</label>
                <input
                  type="password"
                  id="authPassword"
                  value={authPassword}
                  onChange={(e) => setAuthPassword(e.target.value)}
                  required
                />
              </div>
              <button type="submit" className="auth-btn">Log In</button>
            </form>
            <p className="auth-switch">
              Don't have an account? <span onClick={() => setAuthMode("signup")}>Sign Up</span>
            </p>
          </>
        ) : (
          <>
            <h2>Join MoodMate</h2>
            <p>Create your account and start your journey to a healthier mind.</p>
            <form onSubmit={handleSignup}>
              {authError && <p className="error-message">{authError}</p>}
              {authSuccess && <p className="success-message">{authSuccess}</p>}
              <div className="form-group">
                <label htmlFor="authUsername">Username</label>
                <input
                  type="text"
                  id="authUsername"
                  value={authUsername}
                  onChange={(e) => setAuthUsername(e.target.value)}
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="authPassword">Password</label>
                <input
                  type="password"
                  id="authPassword"
                  value={authPassword}
                  onChange={(e) => setAuthPassword(e.target.value)}
                  required
                />
              </div>
              <button type="submit" className="auth-btn">Sign Up</button>
            </form>
            <p className="auth-switch">
              Already have an account? <span onClick={() => setAuthMode("login")}>Log In</span>
            </p>
          </>
        )}
      </div>
    </div>
  );

  return (
    <div className={`app-container ${currentTheme}-theme`}>
      {!isAuthenticated ? (
        <AuthView />
      ) : (
        <>
          <EmergencyModal />
          <NewChatModal />
          <Sidebar />
          
          <header className="app-header">
            <div className="header-left">
              <button onClick={() => setSidebarOpen(true)} className="menu-btn">☰</button>
              <h1>🌙 MoodMate</h1>
              <div className="user-stats">
                <span className="streak-display">🔥 {user?.streak || 0} day streak</span>
                <span>🪙 {user?.coins || 0} coins</span>
              </div>
            </div>
            <div className="header-right">
              <div className="user-info">
                <span className="user-name">{user?.name}</span>
                <span className="user-status">Online</span>
              </div>
              <button onClick={handleLogout} className="logout-btn">Log Out</button>
            </div>
          </header>

          <nav className="app-nav">
            <button 
              className={activeTab === "chat" ? "active" : ""} 
              onClick={() => setActiveTab("chat")}
            >
              💬 Chat
            </button>
            <button 
              className={activeTab === "journal" ? "active" : ""} 
              onClick={() => setActiveTab("journal")}
            >
              📓 Journal
            </button>
            <button 
              className={activeTab === "therapy" ? "active" : ""} 
              onClick={() => setActiveTab("therapy")}
            >
              👨‍⚕️ Therapy
            </button>
            <button 
              className={activeTab === "community" ? "active" : ""} 
              onClick={() => setActiveTab("community")}
            >
              🌍 Community
            </button>
            <button 
              className={activeTab === "shop" ? "active" : ""} 
              onClick={() => setActiveTab("shop")}
            >
              🛒 Shop
            </button>
            <button 
              className={activeTab === "profile" ? "active" : ""} 
              onClick={() => setActiveTab("profile")}
            >
              👤 Profile
            </button>
          </nav>

          <main className="app-main">
            {activeTab === "chat" && (
              <div className="chat-tab">
                <div className="chat-header">
                  <h2>Chat with MoodMate</h2>
                  <button onClick={() => setShowNewChatModal(true)} className="new-chat-btn">
                    + New Chat
                  </button>
                </div>
                <div className="chat-box">
                  {messages.map((msg, i) => (
                    <div key={`msg-${i}-${msg.timestamp || Date.now()}`} className={`chat-message ${msg.sender} ${msg.isReward ? 'reward' : ''} ${msg.isExercise ? 'exercise' : ''}`}>
                      {msg.sender === "bot" && (
                        <div className="avatar bot-avatar">🤖</div>
                      )}
                      {msg.sender === "user" && (
                        <div className="avatar user-avatar">👤</div>
                      )}
                      <div className="bubble">
                        {msg.text}
                        {msg.moodEmoji && (
                          <div className="mood-indicator">
                            {msg.moodEmoji} {msg.phrase}
                          </div>
                        )}
                      </div>
                    </div>
                  ))}

                  {isTyping && (
                    <div className="chat-message bot">
                      <div className="avatar bot-avatar">🤖</div>
                      <div className="bubble typing">
                        {typingIndicator || "..."}
                      </div>
                    </div>
                  )}

                  <div ref={chatEndRef}></div>
                </div>

                <div className="input-area">
                  <input
                    type="text"
                    placeholder="How are you feeling today?"
                    value={input}
                    onChange={(e) => setInput(e.target.value)}
                    onKeyDown={(e) => e.key === "Enter" && sendMessage()}
                  />
                  <button onClick={sendMessage} className="send-btn">Send</button>
                  <button onClick={playBreathingExercise} className="exercise-btn" title="Breathing Exercise">🧘‍♂️</button>
                </div>
              </div>
            )}

            {activeTab === "journal" && (
              <div className="journal-tab">
                <div className="journal-header">
                  <h2>Your Journal</h2>
                  <button onClick={addJournalEntry} className="add-entry-btn">
                    + New Entry
                  </button>
                </div>
                
                <div className="journal-entries">
                  {journalEntries.length === 0 ? (
                    <div className="empty-state">
                      <p>No journal entries yet. Write your first entry!</p>
                      <button onClick={addJournalEntry}>Start Journaling</button>
                    </div>
                  ) : (
                    journalEntries.map((entry, index) => (
                      <div key={`entry-${entry.id}-${index}`} className="journal-entry">
                        <div className="entry-header">
                          <div className="entry-date">{entry.date}</div>
                          {!entry.shared && userTribes.length > 0 && (
                            <button 
                              onClick={() => shareJournalEntry(entry.id)}
                              className="share-btn"
                              title="Share to your tribes"
                            >
                              Share
                            </button>
                          )}
                        </div>
                        <div className="entry-text">{entry.text}</div>
                        {entry.summary && (
                          <div className="entry-summary">
                            <strong>Summary:</strong> {entry.summary}
                          </div>
                        )}
                        {entry.mood && (
                          <div className="entry-mood">
                            <strong>Mood:</strong> {entry.mood}
                          </div>
                        )}
                      </div>
                    ))
                  )}
                </div>
              </div>
            )}

            {activeTab === "therapy" && (
              <div className="therapy-tab">
                <div className="resources-section">
                  <h3>🆘 Crisis Resources</h3>
                  <div className="resources-grid">
                    {resources.filter(r => r.type === 'crisis').map((resource, index) => (
                      <div key={`resource-${index}`} className="resource-card">
                        <h4>{resource.name}</h4>
                        <p>{resource.number}</p>
                        <small>{resource.available}</small>
                        {resource.number && (
                          <button 
                            className="call-btn"
                            onClick={() => window.open(`tel:${resource.number.replace(/\D/g, '')}`)}
                          >
                            📞 Call Now
                          </button>
                        )}
                      </div>
                    ))}
                  </div>
                </div>

                <div className="therapists-section">
                  <h3>👨‍⚕️ Professional Therapists</h3>
                  <div className="therapists-grid">
                    {therapists.map((therapist) => (
                      <div key={`therapist-${therapist.id}`} className="therapist-card">
                        <div className="therapist-avatar">{therapist.avatar}</div>
                        <h4>{therapist.name}</h4>
                        <p>{therapist.specialization}</p>
                        <p>⭐ {therapist.rating} • {therapist.experience}</p>
                        <p>🪙 {therapist.price} coins/session</p>
                        <button 
                          className="book-btn"
                          disabled={user?.coins < therapist.price}
                        >
                          {user?.coins >= therapist.price ? "Book Session" : "Not Enough Coins"}
                        </button>
                      </div>
                    ))}
                  </div>
                </div>
              </div>
            )}

            {activeTab === "community" && activeTribe ? (
              <TribeCommunity />
            ) : activeTab === "community" ? (
              <div className="community-tab">
                <div className="community-layout">
                  <div className="mood-wall-section">
                    <h3>🌍 Community Mood Wall</h3>
                    <button onClick={addMoodWallPost} className="add-post-btn">+ Share Your Feelings</button>
                    <div className="mood-wall">
                      {moodWall.length === 0 ? (
                        <div className="empty-state">
                          <p>Be the first to share your thoughts with the community!</p>
                        </div>
                      ) : (
                        moodWall.slice(0, 3).map((post, index) => (
                          <div key={`post-${post.id}-${index}`} className="mood-post">
                            <p>{post.text}</p>
                            <div className="post-footer">
                              <div className="post-reactions">
                                <button>💖</button>
                                <button>🤗</button>
                                <button>👍</button>
                              </div>
                              <span className="post-time">{new Date(post.timestamp).toLocaleDateString()}</span>
                            </div>
                          </div>
                        ))
                      )}
                    </div>
                  </div>

                  <div className="tribes-section">
                    <h3>🎯 Mood Tribes</h3>
                    <div className="tribes-grid">
                      {moodTribes.map((tribe) => (
                        <div key={`tribe-${tribe.id}`} className="tribe-card">
                          <h4>{tribe.name}</h4>
                          <p>{tribe.description}</p>
                          <p className="member-count">👥 {tribe.member_count} members</p>
                          <button 
                            onClick={() => tribe.isMember ? setActiveTribe(tribe) : joinTribe(tribe.id)}
                            className={tribe.isMember ? "joined-btn" : "join-btn"}
                          >
                            {tribe.isMember ? "Visit Community" : "Join Tribe"}
                          </button>
                        </div>
                      ))}
                    </div>
                  </div>
                </div>
              </div>
            ) : null}

            {activeTab === "shop" && (
              <div className="shop-tab">
                <h2>🛒 MoodMate Shop</h2>
                <p>Use your Mood Coins to unlock special features and customization options!</p>
                
                <div className="shop-categories">
                  {shopCategories.map(category => (
                    <button 
                      key={category} 
                      className={`category-btn ${activeShopCategory === category ? 'active' : ''}`}
                      onClick={() => setActiveShopCategory(category)}
                    >
                      {category}
                    </button>
                  ))}
                </div>
                
                <div className="shop-grid">
                  {filteredShopItems.length > 0 ? (
                    filteredShopItems.map((item) => (
                      <div key={`shop-item-${item.id}`} className="shop-item-card">
                        <div className="item-image">
                          {item.category === "theme" && <div className={`theme-preview ${item.name.toLowerCase().replace(' theme', '')}-theme`}></div>}
                          {item.category === "cosmetic" && <span className="item-icon">🎨</span>}
                          {item.category === "content" && <span className="item-icon">📚</span>}
                          {item.category === "utility" && <span className="item-icon">⚡</span>}
                          {item.category === "feature" && <span className="item-icon">⚙️</span>}
                        </div>
                        <div className="item-info">
                          <h4>{item.name}</h4>
                          <p>{item.description}</p>
                          <div className="item-price">🪙 {item.price} coins</div>
                          <button 
                            onClick={() => buyItem(item)}
                            disabled={user?.coins < item.price}
                            className={user?.coins >= item.price ? "buy-btn" : "disabled-btn"}
                          >
                            {user?.coins >= item.price ? "Purchase" : "Not Enough Coins"}
                          </button>
                        </div>
                      </div>
                    ))
                  ) : (
                    <div className="empty-state">
                      <p>No items in this category yet.</p>
                    </div>
                  )}
                </div>
              </div>
            )}

            {activeTab === "profile" && (
              <div className="profile-tab">
                <div className="profile-header">
                  <div className="profile-avatar">
                    <div className="avatar-image">
                      {user?.name?.charAt(0) || 'U'}
                    </div>
                    <div className="profile-badge">
                      <span>🔥 {user?.streak || 0}</span>
                    </div>
                  </div>
                  <div className="profile-info">
                    <h2>{user?.name}</h2>
                    <p className="member-since">Active Member</p>
                    
                    <div className="profile-stats">
                      <div className="stat">
                        <span className="stat-value">{user?.streak || 0}</span>
                        <span className="stat-label">Day Streak</span>
                      </div>
                      <div className="stat">
                        <span className="stat-value">{user?.coins || 0}</span>
                        <span className="stat-label">Mood Coins</span>
                      </div>
                      <div className="stat">
                        <span className="stat-value">{journalEntries.length}</span>
                        <span className="stat-label">Journal Entries</span>
                      </div>
                    </div>
                  </div>
                </div>

                <div className="profile-section">
                  <h3>🔥 Streak Progress</h3>
                  <div className="streak-progress">
                    <div className="streak-days">
                      {[7, 30, 90, 180, 365].map(day => (
                        <div key={`milestone-${day}`} className={`streak-milestone ${user?.streak >= day ? 'achieved' : ''}`}>
                          <span className="milestone-day">{day}</span>
                          <span className="milestone-label">{day} Days</span>
                        </div>
                      ))}
                    </div>
                    <div className="streak-bar">
                      <div 
                        className="streak-progress-bar" 
                        style={{width: `${Math.min(100, (user?.streak / 365) * 100)}%`}}
                      ></div>
                    </div>
                    <div className="current-streak">
                      <span>Current Streak: {user?.streak || 0} days</span>
                      <span>Next milestone: {user?.streak < 7 ? 7 : user?.streak < 30 ? 30 : user?.streak < 90 ? 90 : user?.streak < 180 ? 180 : 365} days</span>
                    </div>
                  </div>
                </div>

                <div className="profile-section">
                  <h3>🏅 Achievements & Badges</h3>
                  <div className="achievements-grid">
                    {achievements.map(achievement => (
                      <div key={`achievement-${achievement.id}`} className={`achievement ${achievement.earned ? 'earned' : 'locked'}`}>
                        <span className="achievement-icon">{achievement.icon}</span>
                        <div className="achievement-info">
                          <h4>{achievement.name}</h4>
                          <p>{achievement.description}</p>
                        </div>
                        <span className="achievement-status">
                          {achievement.earned ? '✓' : '🔒'}
                        </span>
                      </div>
                    ))}
                  </div>
                </div>

                <div className="profile-section">
                  <h3>👥 My Tribes</h3>
                  {userTribes.length === 0 ? (
                    <p>You haven't joined any tribes yet. Visit the Community tab to join one!</p>
                  ) : (
                    <div className="user-tribes">
                      {userTribes.map(tribe => (
                        <div key={`user-tribe-${tribe.id}`} className="user-tribe">
                          <span>{tribe.name}</span>
                          <button onClick={() => setActiveTribe(tribe)}>
                            Visit
                          </button>
                        </div>
                      ))}
                    </div>
                  )}
                </div>

                <div className="profile-section">
                  <h3>⚙️ Settings</h3>
                  <div className="settings-grid">
                    <div className="setting-item">
                      <span>Voice Responses</span>
                      <label className="switch">
                        <input type="checkbox" checked={audioEnabled} onChange={toggleAudio} />
                        <span className="slider"></span>
                      </label>
                    </div>
                    <div className="setting-item">
                      <span>Background Music</span>
                      <label className="switch">
                        <input type="checkbox" checked={musicEnabled} onChange={toggleMusic} />
                        <span className="slider"></span>
                      </label>
                    </div>
                    <div className="setting-item">
                      <span>AI Provider</span>
                      <select value={aiProvider} onChange={(e) => switchAiProvider(e.target.value)}>
                        <option value="gemini">Gemini AI</option>
                        <option value="fallback">Fallback Mode</option>
                      </select>
                    </div>
                  </div>
                </div>
              </div>
            )}
          </main>
        </>
      )}
    </div>
  );
}

export default App;
