import React, { useState, useEffect, useRef } from 'react';
import './Chat.css';

function Chat({ user }) {
    const [messages, setMessages] = useState([]);
    const [input, setInput] = useState("");
    const [isTyping, setIsTyping] = useState(false);
    const chatEndRef = useRef(null);

    useEffect(() => {
        if (user && user.username) {
            setMessages([{
                id: Date.now(),
                text: `Hello ${user.username}! I'm MoodMate. How can I help you today?`,
                sender: 'bot'
            }]);
        }
    }, [user]);

    useEffect(() => {
        chatEndRef.current?.scrollIntoView({ behavior: "smooth" });
    }, [messages]);

    const sendMessage = async () => {
        if (!input.trim()) return;

        const userMessage = { id: Date.now(), text: input, sender: 'user' };
        setMessages(prev => [...prev, userMessage]);
        setInput("");
        setIsTyping(true);

        try {
            const response = await fetch(`/api/chat`, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ message: input })
            });

            if (!response.ok) {
                const errData = await response.json().catch(() => ({}));
                throw new Error(errData.error || `HTTP error! status: ${response.status}`);
            }
            
            const data = await response.json();
            const botMessage = { id: Date.now() + 1, text: data.reply, sender: 'bot' };
            setMessages(prev => [...prev, botMessage]);

        } catch (error) {
            console.error("Chat error:", error);
            const errorMsg = { 
                id: Date.now() + 1, 
                text: "Sorry, I couldn't connect. Please ensure the backend is running and try again.", 
                sender: 'bot' 
            };
            setMessages(prev => [...prev, errorMsg]);
        } finally {
            setIsTyping(false);
        }
    };

    return (
        <div className="chat-container">
            <div className="chat-messages">
                {messages.map((msg) => (
                    <div key={msg.id} className={`message ${msg.sender}`}>
                        <div className="message-text">{msg.text}</div>
                    </div>
                ))}
                {isTyping && (
                    <div className="message bot typing">
                        <div className="typing-indicator">
                            <span>•</span>
                            <span>•</span>
                            <span>•</span>
                        </div>
                    </div>
                )}
                <div ref={chatEndRef} className="scroll-anchor" />
            </div>
            
            <div className="chat-input-container">
                <div className="input-wrapper">
                    <input
                        type="text"
                        value={input}
                        onChange={(e) => setInput(e.target.value)}
                        onKeyDown={(e) => e.key === 'Enter' && sendMessage()}
                        placeholder="Type your message..."
                        className="message-input"
                    />
                    <button 
                        onClick={sendMessage} 
                        disabled={isTyping || !input.trim()}
                        className="send-button"
                    >
                        {isTyping ? '...' : 'Send'}
                    </button>
                </div>
            </div>
        </div>
    );
}

export default Chat;
